import React, { createContext, useContext, useState, useCallback, ReactNode } from 'react';

interface AIContextType {
  sendMessage: (message: string) => Promise<string>;
  streamResponse: (message: string, onChunk: (chunk: string) => void) => Promise<void>;
  models: string[];
  currentModel: string;
  switchModel: (model: string) => void;
  apiKeys: Record<string, string>;
  setApiKey: (provider: string, key: string) => void;
}

const AIContext = createContext<AIContextType | undefined>(undefined);

interface AIProviderProps {
  children: ReactNode;
}

export function AIProvider({ children }: AIProviderProps) {
  const [currentModel, setCurrentModel] = useState('gpt-4');
  const [apiKeys, setApiKeys] = useState<Record<string, string>>({});

  const models = [
    'gpt-4',
    'gpt-3.5-turbo',
    'claude-3-opus',
    'claude-3-sonnet',
    'llama-3',
    'mistral-large'
  ];

  const sendMessage = useCallback(async (message: string): Promise<string> => {
    // Simulate AI response
    await new Promise(resolve => setTimeout(resolve, 1000));

    // In production, this would call the actual AI API
    return `Response from ${currentModel}: I received your message "${message}"`;
  }, [currentModel]);

  const streamResponse = useCallback(async (
    message: string,
    onChunk: (chunk: string) => void
  ): Promise<void> => {
    // Simulate streaming response
    const words = `This is a streaming response from ${currentModel}. `.split(' ');

    for (const word of words) {
      await new Promise(resolve => setTimeout(resolve, 100));
      onChunk(word + ' ');
    }

    // In production, this would stream from the actual AI API
  }, [currentModel]);

  const switchModel = useCallback((model: string) => {
    if (models.includes(model)) {
      setCurrentModel(model);
    }
  }, [models]);

  const setApiKey = useCallback((provider: string, key: string) => {
    setApiKeys(prev => ({ ...prev, [provider]: key }));
  }, []);

  const value: AIContextType = {
    sendMessage,
    streamResponse,
    models,
    currentModel,
    switchModel,
    apiKeys,
    setApiKey
  };

  return <AIContext.Provider value={value}>{children}</AIContext.Provider>;
}

export function useAI() {
  const context = useContext(AIContext);
  if (!context) {
    throw new Error('useAI must be used within an AIProvider');
  }
  return context;
}

