import React, { useEffect, useState } from 'react';
import { useLynxPlatform } from '@lynx-family/react-lynx';

export function LoadingScreen() {
  const platform = useLynxPlatform();
  const [dots, setDots] = useState('');
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setDots(prev => prev.length >= 3 ? '' : prev + '.');
    }, 500);

    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 90) return prev;
        return prev + Math.random() * 15;
      });
    }, 200);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900">
      <div className="text-center">
        {/* Logo */}
        <div className="mb-8">
          <div className="w-24 h-24 mx-auto bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center">
            <span className="text-4xl font-bold text-white">AI</span>
          </div>
        </div>

        {/* App Name */}
        <h1 className="text-3xl font-bold text-white mb-2">Autonogrammer AI</h1>
        <p className="text-gray-400 mb-8">Powered by Lynx Framework</p>

        {/* Loading Indicator */}
        <div className="mb-4">
          <div className="w-64 h-2 bg-gray-800 rounded-full overflow-hidden">
            <div
              className="h-full bg-gradient-to-r from-blue-500 to-purple-600 transition-all duration-300"
              style={{ width: `${progress}%` }}
            />
          </div>
        </div>

        {/* Loading Text */}
        <p className="text-sm text-gray-500">
          Initializing{dots}
        </p>

        {/* Platform Info */}
        <p className="text-xs text-gray-600 mt-4">
          Platform: {platform.name} | Version: {platform.version}
        </p>
      </div>

      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-1/4 left-1/4 w-64 h-64 bg-blue-500/10 rounded-full blur-3xl animate-pulse" />
        <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl animate-pulse delay-1000" />
      </div>
    </div>
  );
}

export default LoadingScreen;

