// Tauri 2.0 Main Application Entry Point
#![cfg_attr(not(debug_assertions), windows_subsystem = "windows")]

use tauri::{
    plugin::{Builder as PluginBuilder, TauriPlugin},
    Manager, Runtime, State, Window,
};
use serde::{Deserialize, Serialize};
use std::sync::Arc;
use parking_lot::RwLock;
use tracing::{info, error};

// Application state
#[derive(Debug, Clone)]
struct AppState {
    ai_sessions: Arc<RwLock<Vec<AISession>>>,
    current_model: Arc<RwLock<String>>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
struct AISession {
    id: String,
    messages: Vec<Message>,
    model: String,
    created_at: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
struct Message {
    role: String,
    content: String,
    timestamp: String,
}

// AI Commands
#[tauri::command]
async fn send_ai_message(
    message: String,
    model: String,
    state: State<'_, AppState>,
) -> Result<String, String> {
    info!("Sending AI message with model: {}", model);

    // Update current model
    *state.current_model.write() = model.clone();

    // Simulate AI response (in production, call actual AI API)
    let response = format!("Response from {}: Processing '{}'", model, message);

    Ok(response)
}

#[tauri::command]
async fn stream_ai_response(
    message: String,
    model: String,
    window: Window,
) -> Result<(), String> {
    info!("Streaming AI response with model: {}", model);

    // Simulate streaming response
    tokio::spawn(async move {
        let chunks = vec!["This ", "is ", "a ", "streaming ", "response ", "from ", &model];

        for chunk in chunks {
            window.emit("ai-stream-chunk", chunk).unwrap();
            tokio::time::sleep(tokio::time::Duration::from_millis(100)).await;
        }

        window.emit("ai-stream-complete", ()).unwrap();
    });

    Ok(())
}

#[tauri::command]
fn get_available_models() -> Vec<String> {
    vec![
        "gpt-4".to_string(),
        "gpt-3.5-turbo".to_string(),
        "claude-3-opus".to_string(),
        "claude-3-sonnet".to_string(),
        "llama-3".to_string(),
        "mistral-large".to_string(),
    ]
}

// File System Commands
#[tauri::command]
async fn read_project_structure(path: String) -> Result<serde_json::Value, String> {
    use std::fs;
    use std::path::Path;

    fn scan_directory(dir: &Path, max_depth: usize, current_depth: usize) -> serde_json::Value {
        if current_depth >= max_depth {
            return serde_json::json!({ "type": "directory", "name": dir.file_name().unwrap().to_string_lossy(), "truncated": true });
        }

        let mut items = Vec::new();

        if let Ok(entries) = fs::read_dir(dir) {
            for entry in entries.flatten() {
                let path = entry.path();
                let name = entry.file_name().to_string_lossy().to_string();

                // Skip common ignore patterns
                if name.starts_with('.') || name == "node_modules" || name == "target" {
                    continue;
                }

                if path.is_dir() {
                    items.push(scan_directory(&path, max_depth, current_depth + 1));
                } else {
                    items.push(serde_json::json!({
                        "type": "file",
                        "name": name,
                        "size": entry.metadata().ok().map(|m| m.len()).unwrap_or(0)
                    }));
                }
            }
        }

        serde_json::json!({
            "type": "directory",
            "name": dir.file_name().unwrap_or_default().to_string_lossy(),
            "children": items
        })
    }

    let path = Path::new(&path);
    if !path.exists() {
        return Err("Path does not exist".to_string());
    }

    Ok(scan_directory(path, 3, 0))
}

// Development Tools Commands
#[tauri::command]
async fn run_terminal_command(command: String) -> Result<String, String> {
    use std::process::Command;

    // Security: Validate command before execution
    let allowed_commands = vec!["ls", "pwd", "git", "npm", "yarn", "pnpm", "deno", "cargo"];
    let cmd_parts: Vec<&str> = command.split_whitespace().collect();

    if cmd_parts.is_empty() {
        return Err("Empty command".to_string());
    }

    let base_cmd = cmd_parts[0];
    if !allowed_commands.contains(&base_cmd) {
        return Err(format!("Command '{}' not allowed", base_cmd));
    }

    let output = Command::new("sh")
        .arg("-c")
        .arg(&command)
        .output()
        .map_err(|e| e.to_string())?;

    let stdout = String::from_utf8_lossy(&output.stdout);
    let stderr = String::from_utf8_lossy(&output.stderr);

    if output.status.success() {
        Ok(stdout.to_string())
    } else {
        Err(stderr.to_string())
    }
}

// System Information
#[tauri::command]
fn get_system_info() -> serde_json::Value {
    serde_json::json!({
        "platform": std::env::consts::OS,
        "arch": std::env::consts::ARCH,
        "cpu_count": num_cpus::get(),
        "tauri_version": "2.0.0",
    })
}

// Initialize logging
fn init_logging() {
    tracing_subscriber::fmt()
        .with_max_level(tracing::Level::INFO)
        .with_target(false)
        .with_thread_ids(true)
        .with_file(true)
        .with_line_number(true)
        .init();
}

fn main() {
    init_logging();
    info!("Starting Autonogrammer AI Desktop Application");

    let app_state = AppState {
        ai_sessions: Arc::new(RwLock::new(Vec::new())),
        current_model: Arc::new(RwLock::new("gpt-4".to_string())),
    };

    tauri::Builder::default()
        .manage(app_state)
        .plugin(tauri_plugin_shell::init())
        .plugin(tauri_plugin_fs::init())
        .plugin(tauri_plugin_dialog::init())
        .plugin(tauri_plugin_notification::init())
        .plugin(tauri_plugin_process::init())
        .plugin(tauri_plugin_os::init())
        .plugin(tauri_plugin_clipboard_manager::init())
        .plugin(tauri_plugin_websocket::init())
        .plugin(tauri_plugin_http::init())
        .plugin(tauri_plugin_global_shortcut::init())
        .plugin(tauri_plugin_window_state::init())
        .plugin(tauri_plugin_updater::init())
        .plugin(tauri_plugin_store::init())
        .invoke_handler(tauri::generate_handler![
            send_ai_message,
            stream_ai_response,
            get_available_models,
            read_project_structure,
            run_terminal_command,
            get_system_info,
        ])
        .setup(|app| {
            info!("Tauri application setup complete");

            // Set up auto-updater
            let handle = app.handle();
            tauri::async_runtime::spawn(async move {
                // Check for updates periodically
                loop {
                    tokio::time::sleep(tokio::time::Duration::from_secs(3600)).await;
                    info!("Checking for updates...");
                    // Update check logic here
                }
            });

            Ok(())
        })
        .run(tauri::generate_context!())
        .expect("error while running tauri application");
}

// Add mobile support when building for mobile platforms
#[cfg(mobile)]
fn mobile_main() {
    use tauri::mobile::{App, AppHandle};

    tauri::Builder::default()
        .mobile_entry_point(|app: AppHandle| {
            info!("Mobile application started");
        })
        .run(tauri::generate_context!())
        .expect("error while running mobile application");
}

