import { HorizonConfig } from '@meta/horizon-sdk';

const config: HorizonConfig = {
  // World Configuration
  world: {
    id: process.env.HORIZON_WORLD_ID || 'autonogrammer-ai-world',
    name: 'Autonogrammer AI Virtual Office',
    description: 'An immersive AI-powered development environment in VR',
    category: 'productivity',
    tags: ['ai', 'development', 'coding', 'collaboration'],
    maxPlayers: 20,
    privateWorld: false
  },

  // Authentication
  auth: {
    apiKey: process.env.HORIZON_API_KEY,
    clientId: process.env.HORIZON_CLIENT_ID,
    clientSecret: process.env.HORIZON_CLIENT_SECRET
  },

  // Features
  features: {
    aiAssistant: {
      enabled: true,
      model: 'meta-llama-3',
      voiceEnabled: true,
      avatarStyle: 'professional'
    },

    avatarSystem: {
      customization: true,
      expressions: true,
      lipSync: true,
      bodyTracking: 'full'
    },

    spatialAudio: {
      enabled: true,
      quality: 'high',
      maxDistance: 50,
      rolloffModel: 'exponential'
    },

    handTracking: {
      enabled: true,
      precision: 'high',
      gestures: [
        'pinch',
        'grab',
        'point',
        'thumbsUp',
        'wave',
        'peace'
      ]
    },

    eyeTracking: {
      enabled: true,
      foveatedRendering: true,
      gazeInteraction: true
    },

    passthrough: {
      enabled: true,
      mode: 'selective', // 'full' | 'selective' | 'portal'
      opacity: 0.3
    },

    collaboration: {
      sharedWhiteboard: true,
      screenSharing: true,
      codeSharing: true,
      voiceChat: true,
      textChat: true
    }
  },

  // Platform Support
  platforms: {
    quest3: {
      enabled: true,
      minVersion: '62.0',
      performanceMode: 'balanced'
    },
    questPro: {
      enabled: true,
      minVersion: '62.0',
      performanceMode: 'quality',
      faceTracking: true
    },
    quest2: {
      enabled: true,
      minVersion: '62.0',
      performanceMode: 'performance'
    },
    desktop: {
      enabled: true,
      browser: ['chrome', 'edge', 'firefox'],
      minBrowserVersion: {
        chrome: '120',
        edge: '120',
        firefox: '120'
      }
    },
    mobile: {
      enabled: true,
      arMode: true,
      platforms: ['ios', 'android']
    }
  },

  // Environment Settings
  environment: {
    skybox: 'modern-office',
    lighting: {
      type: 'dynamic',
      time: 'auto', // Syncs with real time
      ambientIntensity: 0.6,
      directionalIntensity: 0.8
    },
    physics: {
      gravity: 9.8,
      collisions: true,
      interactions: true
    },
    zones: [
      {
        name: 'Main Office',
        type: 'workspace',
        capacity: 10
      },
      {
        name: 'Meeting Room',
        type: 'collaboration',
        capacity: 6,
        privateAudio: true
      },
      {
        name: 'AI Lab',
        type: 'interactive',
        capacity: 4,
        features: ['ai-assistant', 'holographic-display']
      },
      {
        name: 'Relaxation Zone',
        type: 'social',
        capacity: 8,
        ambience: 'calm'
      }
    ]
  },

  // Performance Optimization
  performance: {
    lodLevels: 3,
    culling: {
      frustum: true,
      occlusion: true,
      distance: 100
    },
    rendering: {
      targetFPS: 90,
      dynamicResolution: true,
      antialiasing: 'MSAA4x'
    },
    networking: {
      updateRate: 30,
      interpolation: true,
      compression: true
    }
  },

  // Analytics & Monitoring
  analytics: {
    enabled: true,
    events: [
      'world-join',
      'world-leave',
      'ai-interaction',
      'collaboration-start',
      'object-interaction'
    ],
    metrics: {
      performance: true,
      engagement: true,
      errors: true
    }
  },

  // Content Moderation
  moderation: {
    enabled: true,
    voiceModeration: true,
    textModeration: true,
    gestureFiltering: true,
    reportingEnabled: true
  },

  // Monetization (optional)
  monetization: {
    enabled: false,
    currency: 'horizon-tokens',
    items: []
  }
};

export default config;

