     1/**
     2 * Re.Pack Fast Refresh Integration
     3 *
     4 * Provides intelligent Fast Refresh for Re.Pack federated modules:
     5 * - Hot reload for federated mobile components
     6 * - Cross-platform state synchronization
     7 * - Module federation hot updates
     8 */
     9
    10import type { FastRefreshIntegration, RefreshUpdate } from '../KatalystFastRefreshProvider';
    11
    12export interface RePackFastRefreshConfig {
    13  federatedModules?: boolean;
    14  mobileSync?: boolean;
    15  chunkRefresh?: boolean;
    16  debugMode?: boolean;
    17}
    18
    19export class RePackFastRefresh implements FastRefreshIntegration {
    20  name = 'repack';
    21  private config: RePackFastRefreshConfig;
    22  private federatedModules: Map<string, any> = new Map();
    23  private moduleStates: Map<string, any> = new Map();
    24  private bridgeConnection: any = null;
    25
    26  constructor(config: RePackFastRefreshConfig) {
    27    this.config = config;
    28  }
    29
    30  async initialize(): Promise<void> {
    31    // Detect Re.Pack environment
    32    if (typeof global !== 'undefined') {
    33      const repackRuntime = (global as any).__REPACK__;
    34
    35      if (repackRuntime) {
    36        console.log(' Re.Pack Fast Refresh initialized');
    37
    38        // Initialize module federation tracking
    39        if (this.config.federatedModules) {
    40          this.setupFederatedModuleTracking();
    41        }
    42
    43        // Set up mobile sync
    44        if (this.config.mobileSync) {
    45          this.setupMobileSync();
    46        }
    47      } else {
    48        if (this.config.debugMode) {
    49          console.warn('  Re.Pack runtime not detected');
    50        }
    51      }
    52    }
    53  }
    54
    55  async refresh(moduleId: string, exports: any): Promise<void> {
    56    const isFederatedModule = this.isFederatedModule(exports);
    57    const isMobileComponent = this.isMobileComponent(exports);
    58    const isNativeModule = this.isNativeModule(exports);
    59
    60    if (isFederatedModule && this.config.federatedModules) {
    61      await this.refreshFederatedModule(moduleId, exports);
    62    }
    63
    64    if (isMobileComponent && this.config.mobileSync) {
    65      await this.refreshMobileComponent(moduleId, exports);
    66    }
    67
    68    if (isNativeModule) {
    69      await this.refreshNativeModule(moduleId, exports);
    70    }
    71
    72    if (this.config.chunkRefresh) {
    73      await this.refreshChunks(moduleId, exports);
    74    }
    75  }
    76
    77  async cleanup(): Promise<void> {
    78    this.federatedModules.clear();
    79    this.moduleStates.clear();
    80
    81    if (this.bridgeConnection) {
    82      this.bridgeConnection.disconnect();
    83    }
    84  }
    85
    86  // Federated Module Management
    87  private isFederatedModule(exports: any): boolean {
    88    return Object.values(exports).some(
    89      (value: any) =>
    90        value &&
    91        typeof value === 'object' &&
    92        (value.__federation__ || value.__remote__ || value.__webpack_require__)
    93    );
    94  }
    95
    96  private async refreshFederatedModule(moduleId: string, exports: any): Promise<void> {
    97    // Preserve federated module state
    98    const currentState = this.moduleStates.get(moduleId);
    99
   100    // Update federated module cache
   101    if (typeof global !== 'undefined' && (global as any).__webpack_require__) {
   102      const webpackRequire = (global as any).__webpack_require__;
   103
   104      // Clear module cache
   105      if (webpackRequire.cache && webpackRequire.cache[moduleId]) {
   106        delete webpackRequire.cache[moduleId];
   107      }
   108
   109      // Clear chunk cache if it exists
   110      if (webpackRequire.hmrC) {
   111        Object.keys(webpackRequire.hmrC).forEach((chunkId) => {
   112          if (webpackRequire.hmrC[chunkId]) {
   113            delete webpackRequire.hmrC[chunkId][moduleId];
   114          }
   115        });
   116      }
   117    }
   118
   119    // Re-register federated module
   120    this.federatedModules.set(moduleId, exports);
   121
   122    // Restore state if available
   123    if (currentState) {
   124      this.moduleStates.set(moduleId, currentState);
   125    }
   126
   127    // Notify remote hosts about the update
   128    await this.notifyRemoteHosts(moduleId, exports);
   129
   130    if (this.config.debugMode) {
   131      console.log(` Refreshed federated module: ${moduleId}`);
   132    }
   133  }
   134
   135  private async notifyRemoteHosts(moduleId: string, exports: any): Promise<void> {
   136    if (typeof global === 'undefined') return;
   137
   138    const federationRuntime = (global as any).__webpack_federation__;
   139
   140    if (federationRuntime && federationRuntime.instances) {
   141      federationRuntime.instances.forEach((instance: any) => {
   142        if (instance.moduleCache && instance.moduleCache.has(moduleId)) {
   143          // Update module in remote cache
   144          instance.moduleCache.set(moduleId, exports);
   145
   146          // Trigger update event
   147          if (instance.eventBus) {
   148            instance.eventBus.emit('module-updated', {
   149              moduleId,
   150              exports,
   151              timestamp: Date.now(),
   152            });
   153          }
   154        }
   155      });
   156    }
   157  }
   158
   159  // Mobile Component Management
   160  private isMobileComponent(exports: any): boolean {
   161    return Object.values(exports).some(
   162      (value: any) =>
   163        typeof value === 'function' &&
   164        (value.toString().includes('react-native') ||
   165          value.toString().includes('TouchableOpacity') ||
   166          value.toString().includes('SafeAreaView') ||
   167          value.toString().includes('useNativeDriver'))
   168    );
   169  }
   170
   171  private async refreshMobileComponent(moduleId: string, exports: any): Promise<void> {
   172    // Preserve component state for React Native components
   173    const componentStates = this.extractComponentStates(exports);
   174
   175    // Store states
   176    this.moduleStates.set(`${moduleId}:components`, componentStates);
   177
   178    // Trigger React Native Fast Refresh
   179    if (typeof global !== 'undefined' && (global as any).__REACT_DEVTOOLS_GLOBAL_HOOK__) {
   180      const hook = (global as any).__REACT_DEVTOOLS_GLOBAL_HOOK__;
   181
   182      if (hook.onCommitFiberRoot) {
   183        // Signal component update to React DevTools
   184        Object.values(exports).forEach((component) => {
   185          if (typeof component === 'function') {
   186            hook.onCommitFiberRoot(1, component);
   187          }
   188        });
   189      }
   190    }
   191
   192    // Sync with web if enabled
   193    if (this.config.mobileSync) {
   194      await this.syncWithWeb(moduleId, exports);
   195    }
   196
   197    if (this.config.debugMode) {
   198      console.log(` Refreshed mobile component: ${moduleId}`);
   199    }
   200  }
   201
   202  private extractComponentStates(exports: any): any[] {
   203    const states: any[] = [];
   204
   205    Object.values(exports).forEach((component: any) => {
   206      if (typeof component === 'function' && component.__fiber) {
   207        // Extract React Fiber state
   208        const fiber = component.__fiber;
   209        if (fiber.memoizedState) {
   210          states.push({
   211            component: component.name,
   212            state: fiber.memoizedState,
   213          });
   214        }
   215      }
   216    });
   217
   218    return states;
   219  }
   220
   221  private async syncWithWeb(moduleId: string, exports: any): Promise<void> {
   222    if (!this.bridgeConnection) return;
   223
   224    // Send update to web platform via bridge
   225    const updatePayload = {
   226      type: 'mobile-component-update',
   227      moduleId,
   228      exports: this.serializeExports(exports),
   229      timestamp: Date.now(),
   230    };
   231
   232    try {
   233      await this.bridgeConnection.send(updatePayload);
   234    } catch (error) {
   235      if (this.config.debugMode) {
   236        console.warn('Failed to sync with web:', error);
   237      }
   238    }
   239  }
   240
   241  private serializeExports(exports: any): any {
   242    // Serialize exports for cross-platform transmission
   243    const serialized: any = {};
   244
   245    Object.entries(exports).forEach(([key, value]) => {
   246      if (typeof value === 'function') {
   247        serialized[key] = {
   248          type: 'function',
   249          name: value.name,
   250          source: value.toString(),
   251        };
   252      } else if (typeof value === 'object' && value !== null) {
   253        serialized[key] = {
   254          type: 'object',
   255          value: JSON.stringify(value),
   256        };
   257      } else {
   258        serialized[key] = {
   259          type: typeof value,
   260          value,
   261        };
   262      }
   263    });
   264
   265    return serialized;
   266  }
   267
   268  // Native Module Management
   269  private isNativeModule(exports: any): boolean {
   270    return Object.values(exports).some(
   271      (value: any) =>
   272        value &&
   273        typeof value === 'object' &&
   274        (value.NativeModules || value.requireNativeComponent || value.NativeEventEmitter)
   275    );
   276  }
   277
   278  private async refreshNativeModule(moduleId: string, exports: any): Promise<void> {
   279    // Native modules require special handling as they bridge to native code
   280    if (typeof global !== 'undefined') {
   281      const NativeModules = (global as any).NativeModules;
   282
   283      if (NativeModules) {
   284        // Update native module registry
   285        Object.entries(exports).forEach(([key, value]) => {
   286          if (value && typeof value === 'object' && value.NativeModules) {
   287            Object.assign(NativeModules, value.NativeModules);
   288          }
   289        });
   290      }
   291    }
   292
   293    if (this.config.debugMode) {
   294      console.log(` Refreshed native module: ${moduleId}`);
   295    }
   296  }
   297
   298  // Chunk Management
   299  private async refreshChunks(moduleId: string, exports: any): Promise<void> {
   300    if (typeof global === 'undefined') return;
   301
   302    const webpackRequire = (global as any).__webpack_require__;
   303
   304    if (webpackRequire && webpackRequire.cache) {
   305      // Find related chunks
   306      const relatedChunks = this.findRelatedChunks(moduleId);
   307
   308      // Refresh chunk cache
   309      relatedChunks.forEach((chunkId) => {
   310        if (webpackRequire.cache[chunkId]) {
   311          delete webpackRequire.cache[chunkId];
   312        }
   313      });
   314
   315      // Trigger chunk reload
   316      if (webpackRequire.loadChunk) {
   317        relatedChunks.forEach((chunkId) => {
   318          webpackRequire.loadChunk(chunkId);
   319        });
   320      }
   321    }
   322
   323    if (this.config.debugMode) {
   324      console.log(` Refreshed chunks for: ${moduleId}`);
   325    }
   326  }
   327
   328  private findRelatedChunks(moduleId: string): string[] {
   329    // Find chunks that contain the module
   330    const chunks: string[] = [];
   331
   332    if (typeof global !== 'undefined') {
   333      const webpackRequire = (global as any).__webpack_require__;
   334
   335      if (webpackRequire && webpackRequire.cache) {
   336        Object.keys(webpackRequire.cache).forEach((cacheKey) => {
   337          const module = webpackRequire.cache[cacheKey];
   338          if (module && module.exports && module.id === moduleId) {
   339            chunks.push(cacheKey);
   340          }
   341        });
   342      }
   343    }
   344
   345    return chunks;
   346  }
   347
   348  // Setup Methods
   349  private setupFederatedModuleTracking(): void {
   350    if (typeof global === 'undefined') return;
   351
   352    // Hook into webpack federation events
   353    const federationRuntime = (global as any).__webpack_federation__;
   354
   355    if (federationRuntime) {
   356      // Track module loads
   357      const originalGet = federationRuntime.get;
   358
   359      federationRuntime.get = (moduleId: string) => {
   360        const result = originalGet.call(federationRuntime, moduleId);
   361
   362        if (result) {
   363          this.federatedModules.set(moduleId, result);
   364
   365          if (this.config.debugMode) {
   366            console.log(` Tracked federated module: ${moduleId}`);
   367          }
   368        }
   369
   370        return result;
   371      };
   372    }
   373  }
   374
   375  private setupMobileSync(): void {
   376    // Set up bridge connection for mobile-web sync
   377    this.bridgeConnection = this.createBridgeConnection();
   378
   379    if (this.bridgeConnection) {
   380      this.bridgeConnection.onMessage((message: any) => {
   381        if (message.type === 'web-component-update') {
   382          this.handleWebUpdate(message);
   383        }
   384      });
   385    }
   386  }
   387
   388  private createBridgeConnection(): any {
   389    // This would be platform-specific implementation
   390    // For React Native, this might use WebSocket or native bridge
   391    if (typeof global !== 'undefined' && (global as any).WebSocket) {
   392      const ws = new (global as any).WebSocket('ws://localhost:8080/repack-sync');
   393
   394      return {
   395        send: (data: any) => ws.send(JSON.stringify(data)),
   396        onMessage: (callback: (data: any) => void) => {
   397          ws.onmessage = (event) => {
   398            try {
   399              const data = JSON.parse(event.data);
   400              callback(data);
   401            } catch (error) {
   402              console.warn('Failed to parse bridge message:', error);
   403            }
   404          };
   405        },
   406        disconnect: () => ws.close(),
   407      };
   408    }
   409
   410    return null;
   411  }
   412
   413  private handleWebUpdate(message: any): void {
   414    const { moduleId, exports } = message;
   415
   416    // Apply web updates to mobile if compatible
   417    if (this.federatedModules.has(moduleId)) {
   418      this.refreshFederatedModule(moduleId, exports);
   419    }
   420  }
   421
   422  async broadcastUpdate(update: RefreshUpdate): Promise<void> {
   423    if (update.type === 'module-refresh' && this.bridgeConnection) {
   424      // Broadcast to other platforms
   425      await this.bridgeConnection.send({
   426        type: 'repack-update',
   427        ...update,
   428      });
   429    }
   430  }
   431}
