     1export interface ZustandConfig {
     2  devtools: boolean;
     3  persist: boolean;
     4  immer: boolean;
     5  subscribeWithSelector: boolean;
     6  temporal: boolean;
     7  middleware: ZustandMiddleware[];
     8  storage: ZustandStorageConfig;
     9}
    10
    11export interface ZustandMiddleware {
    12  name: string;
    13  enabled: boolean;
    14  options: Record<string, any>;
    15}
    16
    17export interface ZustandStorageConfig {
    18  name: string;
    19  storage: 'localStorage' | 'sessionStorage' | 'indexedDB' | 'custom';
    20  partialize?: (state: any) => any;
    21  merge?: (persistedState: any, currentState: any) => any;
    22  skipHydration?: boolean;
    23}
    24
    25export interface ZustandStore<T = any> {
    26  getState: () => T;
    27  setState: (partial: T | Partial<T> | ((state: T) => T | Partial<T>), replace?: boolean) => void;
    28  subscribe: (listener: (state: T, prevState: T) => void) => () => void;
    29  destroy: () => void;
    30}
    31
    32export interface ZustandDevtoolsConfig {
    33  enabled: boolean;
    34  name: string;
    35  serialize: boolean;
    36  anonymousActionType: string;
    37  store: string;
    38}
    39
    40export interface ZustandPersistConfig {
    41  name: string;
    42  storage: Storage;
    43  partialize?: (state: any) => any;
    44  onRehydrateStorage?: (state: any) => ((state?: any, error?: Error) => void) | void;
    45  version?: number;
    46  migrate?: (persistedState: any, version: number) => any;
    47  merge?: (persistedState: any, currentState: any) => any;
    48  skipHydration?: boolean;
    49}
    50
    51export class ZustandIntegration {
    52  private config: ZustandConfig;
    53
    54  constructor(config: ZustandConfig) {
    55    this.config = config;
    56  }
    57
    58  async setupStore() {
    59    return {
    60      name: 'zustand-store',
    61      setup: () => ({
    62        store: this.config,
    63        features: {
    64          lightweightStateManagement: true,
    65          noProviders: true,
    66          noBoilerplate: true,
    67          typescriptSupport: true,
    68          devtoolsIntegration: this.config.devtools,
    69          persistentState: this.config.persist,
    70          immerIntegration: this.config.immer,
    71          selectorSubscriptions: this.config.subscribeWithSelector,
    72          timeTravel: this.config.temporal,
    73        },
    74        api: {
    75          create: 'create',
    76          createWithEqualityFn: 'createWithEqualityFn',
    77          subscribeWithSelector: 'subscribeWithSelector',
    78          devtools: 'devtools',
    79          persist: 'persist',
    80          immer: 'immer',
    81          temporal: 'temporal',
    82        },
    83      }),
    84      plugins: [
    85        'zustand-devtools-plugin',
    86        'zustand-persist-plugin',
    87        'zustand-immer-plugin',
    88        'zustand-temporal-plugin',
    89      ],
    90      dependencies: ['zustand', 'immer', 'zustand/middleware'],
    91    };
    92  }
    93
    94  async setupDevtools() {
    95    return {
    96      name: 'zustand-devtools',
    97      setup: () => ({
    98        devtools: {
    99          enabled: this.config.devtools,
   100          name: 'Katalyst Store',
   101          serialize: true,
   102          anonymousActionType: 'anonymous',
   103          store: 'katalyst',
   104        },
   105        features: {
   106          timeTravel: true,
   107          actionReplay: true,
   108          stateInspection: true,
   109          actionLogging: true,
   110          stateComparison: true,
   111          exportImport: true,
   112          remoteDebugging: true,
   113        },
   114        integration: {
   115          reduxDevtools: true,
   116          reactDevtools: true,
   117          standalone: true,
   118        },
   119      }),
   120    };
   121  }
   122
   123  async setupPersistence() {
   124    return {
   125      name: 'zustand-persistence',
   126      setup: () => ({
   127        persist: {
   128          enabled: this.config.persist,
   129          storage: this.config.storage,
   130          options: {
   131            name: this.config.storage.name,
   132            storage: this.getStorageAdapter(this.config.storage.storage),
   133            partialize: this.config.storage.partialize,
   134            merge: this.config.storage.merge,
   135            skipHydration: this.config.storage.skipHydration,
   136          },
   137        },
   138        features: {
   139          localStorage: true,
   140          sessionStorage: true,
   141          indexedDB: true,
   142          customStorage: true,
   143          partialPersistence: true,
   144          stateMigration: true,
   145          hydrationControl: true,
   146          errorHandling: true,
   147        },
   148        storageAdapters: {
   149          localStorage: 'localStorage',
   150          sessionStorage: 'sessionStorage',
   151          indexedDB: 'idb-keyval',
   152          asyncStorage: '@react-native-async-storage/async-storage',
   153        },
   154      }),
   155    };
   156  }
   157
   158  async setupImmerIntegration() {
   159    return {
   160      name: 'zustand-immer',
   161      setup: () => ({
   162        immer: {
   163          enabled: this.config.immer,
   164          features: {
   165            immutableUpdates: true,
   166            draftState: true,
   167            structuralSharing: true,
   168            performanceOptimization: true,
   169            nestedUpdates: true,
   170            arrayOperations: true,
   171            objectOperations: true,
   172          },
   173        },
   174        api: {
   175          immer: 'immer',
   176          produce: 'produce',
   177          draft: 'Draft',
   178          original: 'original',
   179          current: 'current',
   180        },
   181        patterns: {
   182          simpleUpdate: '(state) => { state.count += 1; }',
   183          nestedUpdate: '(state) => { state.user.profile.name = "New Name"; }',
   184          arrayPush: '(state) => { state.items.push(newItem); }',
   185          arrayFilter: '(state) => { state.items = state.items.filter(item => item.id !== id); }',
   186        },
   187      }),
   188    };
   189  }
   190
   191  async setupSelectors() {
   192    return {
   193      name: 'zustand-selectors',
   194      setup: () => ({
   195        selectors: {
   196          enabled: this.config.subscribeWithSelector,
   197          features: {
   198            selectorSubscriptions: true,
   199            equalityChecking: true,
   200            shallowEqual: true,
   201            deepEqual: true,
   202            customEquality: true,
   203            memoization: true,
   204            performanceOptimization: true,
   205          },
   206        },
   207        api: {
   208          subscribeWithSelector: 'subscribeWithSelector',
   209          shallow: 'shallow',
   210          createWithEqualityFn: 'createWithEqualityFn',
   211        },
   212        patterns: {
   213          basicSelector: 'const count = useStore(state => state.count)',
   214          multipleSelectors:
   215            'const { count, increment } = useStore(state => ({ count: state.count, increment: state.increment }))',
   216          shallowSelector: 'const items = useStore(state => state.items, shallow)',
   217          customEquality: 'const user = useStore(state => state.user, (a, b) => a.id === b.id)',
   218        },
   219      }),
   220    };
   221  }
   222
   223  async setupTemporal() {
   224    return {
   225      name: 'zustand-temporal',
   226      setup: () => ({
   227        temporal: {
   228          enabled: this.config.temporal,
   229          features: {
   230            timeTravel: true,
   231            undoRedo: true,
   232            stateHistory: true,
   233            futureStates: true,
   234            historyLimit: true,
   235            clearHistory: true,
   236            pauseTracking: true,
   237          },
   238        },
   239        api: {
   240          temporal: 'temporal',
   241          undo: 'undo',
   242          redo: 'redo',
   243          clear: 'clear',
   244          pause: 'pause',
   245          resume: 'resume',
   246          getHistory: 'getHistory',
   247        },
   248        configuration: {
   249          limit: 100,
   250          handleSet: true,
   251          equality: 'shallow',
   252        },
   253      }),
   254    };
   255  }
   256
   257  async setupReactIntegration() {
   258    return {
   259      name: 'zustand-react',
   260      setup: () => ({
   261        react: {
   262          hooks: {
   263            useStore: 'useStore',
   264            useStoreWithEqualityFn: 'useStoreWithEqualityFn',
   265            createContext: 'createContext',
   266            useContext: 'useContext',
   267          },
   268          features: {
   269            automaticRerendering: true,
   270            selectorOptimization: true,
   271            contextIntegration: true,
   272            suspenseSupport: true,
   273            concurrentFeatures: true,
   274            strictMode: true,
   275            devMode: true,
   276          },
   277        },
   278        patterns: {
   279          basicUsage: `
   280            const useStore = create((set) => ({
   281              count: 0,
   282              increment: () => set((state) => ({ count: state.count + 1 })),
   283            }))
   284            
   285            function Counter() {
   286              const { count, increment } = useStore()
   287              return <button onClick={increment}>{count}</button>
   288            }
   289          `,
   290          withSelectors: `
   291            const count = useStore((state) => state.count)
   292            const increment = useStore((state) => state.increment)
   293          `,
   294          withContext: `
   295            const StoreContext = createContext()
   296            const StoreProvider = ({ children }) => (
   297              <StoreContext.Provider value={createStore()}>{children}</StoreContext.Provider>
   298            )
   299          `,
   300        },
   301      }),
   302    };
   303  }
   304
   305  private getStorageAdapter(storageType: string) {
   306    switch (storageType) {
   307      case 'localStorage':
   308        return typeof window !== 'undefined' ? globalThis.localStorage : undefined;
   309      case 'sessionStorage':
   310        return typeof window !== 'undefined' ? globalThis.sessionStorage : undefined;
   311      case 'indexedDB':
   312        return 'idb-keyval';
   313      default:
   314        return undefined;
   315    }
   316  }
   317
   318  async initialize() {
   319    const integrations = await Promise.all([
   320      this.setupStore(),
   321      this.setupDevtools(),
   322      this.setupPersistence(),
   323      this.setupImmerIntegration(),
   324      this.setupSelectors(),
   325      this.setupTemporal(),
   326      this.setupReactIntegration(),
   327    ]);
   328
   329    return integrations.filter(Boolean);
   330  }
   331
   332  getDefaultConfig(): ZustandConfig {
   333    return {
   334      devtools: true,
   335      persist: true,
   336      immer: true,
   337      subscribeWithSelector: true,
   338      temporal: false,
   339      middleware: [
   340        { name: 'devtools', enabled: true, options: {} },
   341        { name: 'persist', enabled: true, options: {} },
   342        { name: 'immer', enabled: true, options: {} },
   343      ],
   344      storage: {
   345        name: 'katalyst-store',
   346        storage: 'localStorage',
