import { AutonogrammerWorld } from '@autonogrammer/horizon-world';

// Metaverse App Entry Point
const world = new AutonogrammerWorld();

// Initialize and start the VR world
world.onCreate().then(() => {
  console.log('Autonogrammer AI Metaverse World initialized successfully');
}).catch((error) => {
  console.error('Failed to initialize Metaverse World:', error);
