import 'package:shared_preferences/shared_preferences.dart';
import '../../../../core/constants/app_constants.dart';
import '../models/counter_model.dart';

abstract class CounterLocalDataSource {
  Future<CounterModel?> getCounter();
  Future<void> cacheCounter(CounterModel counter);
}

class CounterLocalDataSourceImpl implements CounterLocalDataSource {
  final SharedPreferences sharedPreferences;

  const CounterLocalDataSourceImpl(this.sharedPreferences);

  @override
  Future<CounterModel?> getCounter() async {
    final jsonString = sharedPreferences.getString(AppConstants.counterKey);
    if (jsonString != null) {
      final json = jsonDecode(jsonString);
      return CounterModel.fromJson(json);
    }
    return null;
  }

  @override
  Future<void> cacheCounter(CounterModel counter) async {
    final jsonString = jsonEncode(counter.toJson());
    await sharedPreferences.setString(AppConstants.counterKey, jsonString);
  }
}

