import 'package:dartz/dartz.dart';
import '../../../../core/errors/failures.dart';
import '../../domain/entities/counter_entity.dart';
import '../../domain/repositories/counter_repository.dart';
import '../datasources/counter_local_datasource.dart';
import '../models/counter_model.dart';

class CounterRepositoryImpl implements CounterRepository {
  final CounterLocalDataSource localDataSource;

  const CounterRepositoryImpl(this.localDataSource);

  @override
  Future<Either<Failure, CounterEntity>> getCounter() async {
    try {
      final counterModel = await localDataSource.getCounter();
      if (counterModel != null) {
        return Right(counterModel);
      } else {
        // Return default counter if none exists
        final defaultCounter = CounterModel(
          value: 0,
          lastUpdated: DateTime.now(),
        );
        await localDataSource.cacheCounter(defaultCounter);
        return Right(defaultCounter);
      }
    } catch (e) {
      return const Left(CacheFailure());
    }
  }

  @override
  Future<Either<Failure, CounterEntity>> incrementCounter() async {
    try {
      final currentCounterEither = await getCounter();
      return currentCounterEither.fold(
        (failure) => Left(failure),
        (counter) async {
          final newCounter = CounterModel(
            value: counter.value + 1,
            lastUpdated: DateTime.now(),
          );
          await localDataSource.cacheCounter(newCounter);
          return Right(newCounter);
        },
      );
    } catch (e) {
      return const Left(CacheFailure());
    }
  }

  @override
  Future<Either<Failure, CounterEntity>> decrementCounter() async {
    try {
      final currentCounterEither = await getCounter();
      return currentCounterEither.fold(
        (failure) => Left(failure),
        (counter) async {
          final newCounter = CounterModel(
            value: counter.value - 1,
            lastUpdated: DateTime.now(),
          );
          await localDataSource.cacheCounter(newCounter);
          return Right(newCounter);
        },
      );
    } catch (e) {
      return const Left(CacheFailure());
    }
  }

  @override
  Future<Either<Failure, void>> resetCounter() async {
    try {
      final resetCounter = CounterModel(
        value: 0,
        lastUpdated: DateTime.now(),
      );
      await localDataSource.cacheCounter(resetCounter);
      return const Right(null);
    } catch (e) {
      return const Left(CacheFailure());
    }
  }
}

