import 'package:flutter_bloc/flutter_bloc.dart';
import '../../domain/usecases/get_counter.dart';
import '../../domain/usecases/increment_counter.dart';
import 'counter_event.dart';
import 'counter_state.dart';

class CounterBloc extends Bloc<CounterEvent, CounterState> {
  final GetCounter getCounter;
  final IncrementCounter incrementCounter;

  CounterBloc({
    required this.getCounter,
    required this.incrementCounter,
  }) : super(const CounterInitial()) {
    on<CounterLoadRequested>(_onCounterLoadRequested);
    on<CounterIncrementPressed>(_onCounterIncrementPressed);
  }

  Future<void> _onCounterLoadRequested(
    CounterLoadRequested event,
    Emitter<CounterState> emit,
  ) async {
    emit(const CounterLoading());

    final result = await getCounter();
    result.fold(
      (failure) => emit(CounterError(failure.message)),
      (counter) => emit(CounterLoaded(counter)),
    );
  }

  Future<void> _onCounterIncrementPressed(
    CounterIncrementPressed event,
    Emitter<CounterState> emit,
  ) async {
    if (state is CounterLoaded) {
      emit(const CounterLoading());
    }

    final result = await incrementCounter();
    result.fold(
      (failure) => emit(CounterError(failure.message)),
      (counter) => emit(CounterLoaded(counter)),
    );
  }
}

