import React from 'react'
import { Box, Text } from 'ink'
import { Command } from '../types/command'
import { globSync } from 'glob'
import { readFileSync, writeFileSync } from 'fs'
import { resolve, relative, dirname } from 'path'
import { execSync } from 'child_process'

interface PackageMapping {
  oldImport: RegExp
  newImport: string
  packageName: string
}

const PACKAGE_MAPPINGS: PackageMapping[] = [
  {
    oldImport: /from ['"]\.\.\/utils\/([^'"]+)['"]/g,
    newImport: 'from "@autonogrammer/utils/$1"',
    packageName: '@autonogrammer/utils'
  },
  {
    oldImport: /from ['"]\.\.\/components\/ui\/([^'"]+)['"]/g,
    newImport: 'from "@autonogrammer/design-system/ui/$1"',
    packageName: '@autonogrammer/design-system'
  },
  {
    oldImport: /from ['"]\.\.\/tools\/([^'"]+)['"]/g,
    newImport: 'from "@autonogrammer/tools/$1"',
    packageName: '@autonogrammer/tools'
  },
  {
    oldImport: /from ['"]\.\.\/services\/([^'"]+)['"]/g,
    newImport: 'from "@autonogrammer/core/services/$1"',
    packageName: '@autonogrammer/core'
  },
  {
    oldImport: /from ['"]\.\.\/hooks\/([^'"]+)['"]/g,
    newImport: 'from "@autonogrammer/hooks/$1"',
    packageName: '@autonogrammer/hooks'
  },
  {
    oldImport: /from ['"]@katalyst\/([^'"]+)['"]/g,
    newImport: 'from "@autonogrammer/$1"',
    packageName: '@autonogrammer/core'
  }
]

interface TurboSyncResult {
  file: string
  originalImports: number
  updatedImports: number
  packages: Set<string>
}

class TurboSyncCommand implements Command {
  name = 'turbo'
  description = 'Synchronize src files with turborepo packages'
  aliases = ['turbo.sync', 'ts']

  async execute(args: string[]): Promise<React.ReactElement> {
    const subcommand = args[0] || 'sync'

    switch (subcommand) {
      case 'sync':
        return this.syncPackages(args.slice(1))
      case 'analyze':
        return this.analyzeImports()
      case 'migrate':
        return this.migrateComponent(args[1])
      case 'health':
        return this.checkHealth()
      default:
        return this.showHelp()
    }
  }

  private async syncPackages(patterns: string[]): Promise<React.ReactElement> {
    const targetPattern = patterns[0] || 'src/**/*.{ts,tsx}'
    const files = globSync(targetPattern, { 
      ignore: ['**/node_modules/**', '**/dist/**', '**/*.test.*']
    })

    const results: TurboSyncResult[] = []
    let totalOriginal = 0
    let totalUpdated = 0

    for (const file of files) {
      const result = await this.processFile(file)
      if (result.updatedImports > 0) {
        results.push(result)
        totalOriginal += result.originalImports
        totalUpdated += result.updatedImports
      }
    }

    return (
      <Box flexDirection="column">
        <Text bold color="cyan">ðŸš€ Turbo Package Synchronization Complete</Text>
        <Text> </Text>
        <Text>ðŸ“Š Summary:</Text>
        <Text>  â€¢ Files processed: {files.length}</Text>
        <Text>  â€¢ Files updated: {results.length}</Text>
        <Text>  â€¢ Imports migrated: {totalUpdated}</Text>
        <Text> </Text>
        {results.length > 0 && (
          <>
            <Text bold>Updated Files:</Text>
            {results.map((r, i) => (
              <Box key={i} marginLeft={2}>
                <Text color="green">âœ“ </Text>
                <Text>{relative(process.cwd(), r.file)}</Text>
                <Text color="gray"> ({r.updatedImports} imports)</Text>
              </Box>
            ))}
          </>
        )}
        <Text> </Text>
        <Text color="yellow">ðŸ’¡ Run 'pnpm install' to ensure all packages are installed</Text>
      </Box>
    )
  }

  private async processFile(filePath: string): Promise<TurboSyncResult> {
    const content = readFileSync(filePath, 'utf-8')
    let updatedContent = content
    const packages = new Set<string>()
    let originalImports = 0
    let updatedImports = 0

    for (const mapping of PACKAGE_MAPPINGS) {
      const matches = content.match(mapping.oldImport)
      if (matches) {
        originalImports += matches.length
        updatedContent = updatedContent.replace(mapping.oldImport, mapping.newImport)
        packages.add(mapping.packageName)
        updatedImports += matches.length
      }
    }

    // Handle relative imports that should use packages
    const relativeImportRegex = /from ['"](\.\.[\/\\]+)(components|utils|hooks|tools|services)[\/\\]([^'"]+)['"]/g
    const relativeMatches = content.match(relativeImportRegex)

    if (relativeMatches) {
      originalImports += relativeMatches.length
      updatedContent = updatedContent.replace(
        relativeImportRegex,
        (match, dots, pkg, path) => {
          const packageName = this.getPackageName(pkg)
          packages.add(packageName)
          updatedImports++
          return `from "${packageName}/${path}"`
        }
      )
    }

    if (updatedImports > 0) {
      writeFileSync(filePath, updatedContent)
    }

    return {
      file: filePath,
      originalImports,
      updatedImports,
      packages
    }
  }

  private getPackageName(folder: string): string {
    const packageMap: Record<string, string> = {
      'components': '@autonogrammer/design-system',
      'utils': '@autonogrammer/utils',
      'hooks': '@autonogrammer/hooks',
      'tools': '@autonogrammer/tools',
      'services': '@autonogrammer/core'
    }
    return packageMap[folder] || `@autonogrammer/${folder}`
  }

  private async analyzeImports(): Promise<React.ReactElement> {
    const files = globSync('src/**/*.{ts,tsx}', {
      ignore: ['**/node_modules/**', '**/dist/**']
    })

    const importStats = new Map<string, number>()
    const relativeImports: string[] = []
    const packageImports: string[] = []

    for (const file of files) {
      const content = readFileSync(file, 'utf-8')
      const imports = content.match(/from ['"][^'"]+['"]/g) || []

      for (const imp of imports) {
        const importPath = imp.match(/from ['"]([^'"]+)['"]/)?.[1] || ''

        if (importPath.startsWith('.')) {
          relativeImports.push(`${file}: ${importPath}`)
        } else if (importPath.startsWith('@')) {
          packageImports.push(importPath)
          const pkg = importPath.split('/')[0]
          importStats.set(pkg, (importStats.get(pkg) || 0) + 1)
        }
      }
    }

    return (
      <Box flexDirection="column">
        <Text bold color="cyan">ðŸ“¦ Import Analysis Report</Text>
        <Text> </Text>
        <Text>ðŸ“Š Statistics:</Text>
        <Text>  â€¢ Total files: {files.length}</Text>
        <Text>  â€¢ Relative imports: {relativeImports.length}</Text>
        <Text>  â€¢ Package imports: {packageImports.length}</Text>
        <Text> </Text>
        <Text bold>Top Package Usage:</Text>
        {Array.from(importStats.entries())
          .sort((a, b) => b[1] - a[1])
          .slice(0, 10)
          .map(([pkg, count], i) => (
            <Text key={i}>  {i + 1}. {pkg}: {count} imports</Text>
          ))}
        <Text> </Text>
        <Text color="yellow">
          ðŸ’¡ {relativeImports.length} relative imports can be migrated to package imports
        </Text>
      </Box>
    )
  }

  private async migrateComponent(componentPath: string): Promise<React.ReactElement> {
    if (!componentPath) {
      return <Text color="red">Please provide a component path to migrate</Text>
    }

    const sourcePath = resolve(process.cwd(), componentPath)
    const componentName = sourcePath.split('/').pop()?.replace(/\.(ts|tsx)$/, '')

    // Determine target package based on path
    let targetPackage = '@autonogrammer/core'
    if (componentPath.includes('components')) {
      targetPackage = '@autonogrammer/design-system'
    } else if (componentPath.includes('tools')) {
      targetPackage = '@autonogrammer/tools'
    } else if (componentPath.includes('hooks')) {
      targetPackage = '@autonogrammer/hooks'
    }

    const targetDir = resolve(process.cwd(), 'packages', targetPackage.split('/')[1], 'src')

    // Simulate migration (in real implementation, would move files)
    return (
      <Box flexDirection="column">
        <Text bold color="cyan">ðŸšš Component Migration Plan</Text>
        <Text> </Text>
        <Text>ðŸ“¦ Source: {componentPath}</Text>
        <Text>ðŸ“¦ Target: {targetPackage}</Text>
        <Text>ðŸ“‚ Destination: {relative(process.cwd(), targetDir)}</Text>
        <Text> </Text>
        <Text bold>Migration Steps:</Text>
        <Text>  1. Move component to {targetPackage}</Text>
        <Text>  2. Update imports in component</Text>
        <Text>  3. Export from package index</Text>
        <Text>  4. Update all references</Text>
        <Text>  5. Run tests to verify</Text>
        <Text> </Text>
        <Text color="green">âœ“ Ready to migrate. Run with --execute to perform migration</Text>
      </Box>
    )
  }

  private async checkHealth(): Promise<React.ReactElement> {
    const issues: string[] = []
    const warnings: string[] = []
    const successes: string[] = []

    // Check turbo.json exists
    try {
      readFileSync('turbo.json')
      successes.push('turbo.json found')
    } catch {
      issues.push('turbo.json not found')
    }

    // Check package structure
    const packages = globSync('packages/*/package.json')
    if (packages.length > 0) {
      successes.push(`${packages.length} packages found`)
    } else {
      issues.push('No packages found in packages/ directory')
    }

    // Check for circular dependencies
    const circularDeps = this.checkCircularDependencies()
    if (circularDeps.length > 0) {
      warnings.push(`${circularDeps.length} potential circular dependencies`)
    }

    // Check build cache
    try {
      const cacheStatus = execSync('turbo run build --dry-run', { encoding: 'utf-8' })
      if (cacheStatus.includes('cache hit')) {
        successes.push('Turbo cache is working')
      }
    } catch {
      warnings.push('Turbo cache may not be configured')
    }

    return (
      <Box flexDirection="column">
        <Text bold color="cyan">ðŸ¥ Turborepo Health Check</Text>
        <Text> </Text>

        {successes.length > 0 && (
          <>
            <Text bold color="green">âœ“ Healthy:</Text>
            {successes.map((s, i) => (
              <Text key={i} color="green">  â€¢ {s}</Text>
            ))}
            <Text> </Text>
          </>
        )}

        {warnings.length > 0 && (
          <>
            <Text bold color="yellow">âš  Warnings:</Text>
            {warnings.map((w, i) => (
              <Text key={i} color="yellow">  â€¢ {w}</Text>
            ))}
            <Text> </Text>
          </>
        )}

        {issues.length > 0 && (
          <>
            <Text bold color="red">âœ— Issues:</Text>
            {issues.map((issue, i) => (
              <Text key={i} color="red">  â€¢ {issue}</Text>
            ))}
            <Text> </Text>
          </>
        )}

        <Text bold>Overall Status: {
          issues.length === 0 ? 'ðŸŸ¢ Healthy' :
          warnings.length > 0 ? 'ðŸŸ¡ Needs Attention' :
          'ðŸ”´ Critical Issues'
        }</Text>
      </Box>
    )
  }

  private checkCircularDependencies(): string[] {
    // Simplified circular dependency check
    const deps: string[] = []
    const packages = globSync('packages/*/package.json')

    for (const pkg of packages) {
      const content = JSON.parse(readFileSync(pkg, 'utf-8'))
      const dependencies = {
        ...content.dependencies,
        ...content.devDependencies
      }

      for (const dep in dependencies) {
        if (dep.startsWith('@autonogrammer/')) {
          // Check if this creates a cycle
          const depPkgPath = `packages/${dep.split('/')[1]}/package.json`
          try {
            const depContent = JSON.parse(readFileSync(depPkgPath, 'utf-8'))
            if (depContent.dependencies?.[content.name]) {
              deps.push(`${content.name} <-> ${dep}`)
            }
          } catch {
            // Package doesn't exist yet
          }
        }
      }
    }

    return deps
  }

  private showHelp(): React.ReactElement {
    return (
      <Box flexDirection="column">
        <Text bold color="cyan">ðŸš€ Turbo Command - Turborepo Package Synchronization</Text>
        <Text> </Text>
        <Text bold>Available Subcommands:</Text>
        <Text>  â€¢ sync [pattern]  - Synchronize imports to use turborepo packages</Text>
        <Text>  â€¢ analyze        - Analyze current import structure</Text>
        <Text>  â€¢ migrate <path> - Migrate a component to a package</Text>
        <Text>  â€¢ health         - Check turborepo health status</Text>
        <Text> </Text>
        <Text bold>Examples:</Text>
        <Text color="gray">  /turbo sync              # Sync all src files</Text>
        <Text color="gray">  /turbo sync src/tools/*  # Sync specific directory</Text>
        <Text color="gray">  /turbo analyze           # Analyze import patterns</Text>
        <Text color="gray">  /turbo migrate src/components/Button.tsx</Text>
        <Text color="gray">  /turbo health            # Check monorepo health</Text>
      </Box>
    )
  }
}

export default TurboSyncCommand
