"""
Knowledge Encoding/Decoding Utilities for Manus.im Pro
Provides efficient compression and expansion of knowledge entries using emoji encoding
"""

import json
import re
import gzip
import base64
from typing import Dict, List, Any, Optional, Tuple
from dataclasses import dataclass
from enum import Enum
import hashlib


class EncodingMode(Enum):
    """Supported encoding modes"""
    EMOJI = "emoji"
    COMPRESSED = "compressed"
    HYBRID = "hybrid"
    QUANTUM = "quantum"


@dataclass
class KnowledgeEntry:
    """Structured knowledge entry"""
    id: int
    title: str
    encoded: str
    expansion: str
    priority: str
    metadata: Optional[Dict[str, Any]] = None


class ManusKnowledgeEncoder:
    """Main encoder for Manus knowledge system"""

    def __init__(self, encoding_map_path: str = "manus-encoding-map.json"):
        self.encoding_map = self._load_encoding_map(encoding_map_path)
        self.emoji_to_protocol = self._build_emoji_lookup()
        self.compression_stats = {
            "total_original_size": 0,
            "total_compressed_size": 0,
            "compression_ratio": 0.0
        }

    def _load_encoding_map(self, path: str) -> Dict[str, Any]:
        """Load the emoji encoding map"""
        try:
            with open(path, 'r', encoding='utf-8') as f:
                return json.load(f)
        except FileNotFoundError:
            # Return default encoding map if file not found
            return self._get_default_encoding_map()

    def _get_default_encoding_map(self) -> Dict[str, Any]:
        """Get default encoding map"""
        return {
            "protocol_encodings": {
                "ğŸŒ³": {
                    "name": "quantum_forest",
                    "expands_to": "/quantum.forest.consciousness{crown_orchestration=true}",
                    "description": "Quantum forest consciousness protocol"
                },
                "âš¡": {
                    "name": "etd_measurement",
                    "expands_to": "/etd.measure{value_range='$500K-$2M'}",
                    "description": "ETD measurement system"
                },
                "ğŸ§ ": {
                    "name": "crown_consciousness",
                    "expands_to": "/crown.orchestrate{multi_branch=true}",
                    "description": "Crown consciousness orchestration"
                },
                "ğŸ”": {
                    "name": "blockchain_anchor",
                    "expands_to": "/blockchain.verify{immutable=true}",
                    "description": "Blockchain verification"
                },
                "ğŸŒ": {
                    "name": "mycorrhizal_network",
                    "expands_to": "/network.mycorrhizal{connections=active}",
                    "description": "Network integration"
                }
            },
            "composite_patterns": {},
            "encoding_rules": {
                "max_emoji_per_entry": 5,
                "fallback_compression": "gzip+base64",
                "expansion_trigger": "@expand:",
                "inline_reference": "Â§{emoji}"
            }
        }

    def _build_emoji_lookup(self) -> Dict[str, str]:
        """Build reverse lookup table for emojis"""
        lookup = {}
        for emoji, data in self.encoding_map.get("protocol_encodings", {}).items():
            lookup[emoji] = data["expands_to"]
            lookup[data["name"]] = data["expands_to"]
        return lookup

    def encode_knowledge_entry(self, entry: Dict[str, Any], mode: EncodingMode = EncodingMode.EMOJI) -> str:
        """Encode a knowledge entry using specified mode"""
        original_size = len(json.dumps(entry))

        if mode == EncodingMode.EMOJI:
            encoded = self._encode_with_emoji(entry)
        elif mode == EncodingMode.COMPRESSED:
