                    similar.append(other)
                    processed.add(j)

            if similar:
                # Merge entries
                merged_entry = self._merge_entries(entry, similar)
                merged.append(merged_entry)
            else:
                merged.append(entry)

            processed.add(i)

        return merged

    def _are_similar(self, entry1: Dict[str, Any], entry2: Dict[str, Any]) -> bool:
        """Check if two entries are similar enough to merge"""
        # Check emoji overlap
        emojis1 = set(self.encoder._extract_emoji_sequence(entry1.get("encoded", "")))
        emojis2 = set(self.encoder._extract_emoji_sequence(entry2.get("encoded", "")))

        overlap = len(emojis1.intersection(emojis2))
        total = len(emojis1.union(emojis2))

        if total > 0 and overlap / total > 0.5:
            return True

        # Check title similarity
        title1 = entry1.get("title", "").lower()
        title2 = entry2.get("title", "").lower()

        common_terms = ["api", "endpoint", "quantum", "security", "enterprise"]
        for term in common_terms:
            if term in title1 and term in title2:
                return True

        return False

    def _merge_entries(self, primary: Dict[str, Any], similar: List[Dict[str, Any]]) -> Dict[str, Any]:
        """Merge similar entries into one"""
        merged = primary.copy()

        # Combine titles
        titles = [primary.get("title", "")] + [e.get("title", "") for e in similar]
        merged["title"] = f"{primary.get('title', '')} + {len(similar)} related"

        # Combine encoded content
        all_emojis = set()
        for entry in [primary] + similar:
            emojis = self.encoder._extract_emoji_sequence(entry.get("encoded", ""))
            all_emojis.update(emojis)

        merged["encoded"] = f"MERGED:{''.join(all_emojis[:5])}"

        # Use highest priority
        priorities = [primary.get("priority", "low")] + [e.get("priority", "low") for e in similar]
        priority_order = {"critical": 0, "high": 1, "medium": 2, "low": 3}
        merged["priority"] = min(priorities, key=lambda p: priority_order.get(p, 3))

