        """Validate a complete knowledge base"""
        validation_results = {
            "total_entries": len(knowledge_entries),
            "valid_entries": 0,
            "invalid_entries": 0,
            "compression_ratio": 0.0,
            "errors": [],
            "warnings": []
        }

        for entry in knowledge_entries:
            try:
                # Check required fields
                if not all(k in entry for k in ["id", "title", "encoded"]):
                    validation_results["invalid_entries"] += 1
                    validation_results["errors"].append(
                        f"Entry {entry.get('id', 'unknown')}: Missing required fields"
                    )
                    continue

                # Check encoding
                encoded = self.encode_knowledge_entry(entry, EncodingMode.EMOJI)
                decoded = self.decode_knowledge_entry(encoded)

                # Verify round-trip encoding
                if "error" in decoded:
                    validation_results["warnings"].append(
                        f"Entry {entry['id']}: Encoding/decoding issue - {decoded['error']}"
                    )

                validation_results["valid_entries"] += 1

            except Exception as e:
                validation_results["invalid_entries"] += 1
                validation_results["errors"].append(
                    f"Entry {entry.get('id', 'unknown')}: {str(e)}"
                )

        validation_results["compression_ratio"] = self.compression_stats["compression_ratio"]

        return validation_results

    def generate_manus_prompt(self, knowledge_entries: List[Dict[str, Any]], 
                            max_chars: int = 10000) -> str:
        """Generate optimized Manus prompt with knowledge entries"""
        prompt_parts = [
            "system\n",
            "You are Manus.AI Pro with quantum-enhanced capabilities.\n",
            "Knowledge Base:\n"
        ]

        current_size = sum(len(part) for part in prompt_parts)

        # Sort entries by priority
        priority_order = {"critical": 0, "high": 1, "medium": 2, "low": 3}
        sorted_entries = sorted(
            knowledge_entries,
            key=lambda x: priority_order.get(x.get("priority", "low"), 3)
        )

        # Add entries until size limit
        for entry in sorted_entries:
            encoded = self.encode_knowledge_entry(entry, EncodingMode.EMOJI)
            entry_str = f"{entry['id']}. {encoded}\n"

            if current_size + len(entry_str) > max_chars:
                break

            prompt_parts.append(entry_str)
            current_size += len(entry_str)

        # Add expansion rules
        prompt_parts.append("\nExpansion: Use Â§{emoji} to reference protocols. @expand: for full expansion.\n")

        return ''.join(prompt_parts)


class KnowledgeOptimizer:
    """Optimize knowledge base for maximum efficiency"""

    def __init__(self, encoder: ManusKnowledgeEncoder):
        self.encoder = encoder

    def optimize_for_size(self, knowledge_entries: List[Dict[str, Any]], 
                         target_entries: int = 35) -> List[Dict[str, Any]]:
        """Optimize knowledge base to fit within entry limit"""
        # Score entries by importance
        scored_entries = []
        for entry in knowledge_entries:
            score = self._calculate_importance_score(entry)
            scored_entries.append((score, entry))

        # Sort by score and select top entries
        scored_entries.sort(reverse=True, key=lambda x: x[0])
        optimized = [entry for _, entry in scored_entries[:target_entries]]

        # Further compress selected entries
        for entry in optimized:
            entry["encoded"] = self.encoder.encode_knowledge_entry(
                entry, 
                EncodingMode.HYBRID
            )

        return optimized

    def _calculate_importance_score(self, entry: Dict[str, Any]) -> float:
        """Calculate importance score for an entry"""
        score = 0.0

        # Priority scoring
        priority_scores = {"critical": 10, "high": 7, "medium": 4, "low": 1}
        score += priority_scores.get(entry.get("priority", "low"), 1)

        # Complexity scoring (prefer more complex entries)
        if "quantum" in entry.get("title", "").lower():
            score += 3
        if "enterprise" in entry.get("title", "").lower():
            score += 2
        if "security" in entry.get("title", "").lower():
            score += 2

        # Emoji density (more emojis = more compressed knowledge)
        emoji_count = len(self.encoder._extract_emoji_sequence(entry.get("encoded", "")))
        score += emoji_count * 0.5

        return score

    def merge_similar_entries(self, knowledge_entries: List[Dict[str, Any]]) -> List[Dict[str, Any]]:
        """Merge similar entries to save space"""
        merged = []
        processed = set()

        for i, entry in enumerate(knowledge_entries):
            if i in processed:
                continue

            # Find similar entries
            similar = []
            for j, other in enumerate(knowledge_entries[i+1:], i+1):
                if j not in processed and self._are_similar(entry, other):
                    similar.append(other)
                    processed.add(j)

            if similar:
                # Merge entries
                merged_entry = self._merge_entries(entry, similar)
                merged.append(merged_entry)
            else:
                merged.append(entry)

            processed.add(i)

        return merged

    def _are_similar(self, entry1: Dict[str, Any], entry2: Dict[str, Any]) -> bool:
        """Check if two entries are similar enough to merge"""
        # Check emoji overlap
        emojis1 = set(self.encoder._extract_emoji_sequence(entry1.get("encoded", "")))
        emojis2 = set(self.encoder._extract_emoji_sequence(entry2.get("encoded", "")))

        overlap = len(emojis1.intersection(emojis2))
        total = len(emojis1.union(emojis2))

        if total > 0 and overlap / total > 0.5:
            return True

        # Check title similarity
        title1 = entry1.get("title", "").lower()
        title2 = entry2.get("title", "").lower()

        common_terms = ["api", "endpoint", "quantum", "security", "enterprise"]
        for term in common_terms:
            if term in title1 and term in title2:
                return True

        return False

    def _merge_entries(self, primary: Dict[str, Any], similar: List[Dict[str, Any]]) -> Dict[str, Any]:
        """Merge similar entries into one"""
        merged = primary.copy()

        # Combine titles
        titles = [primary.get("title", "")] + [e.get("title", "") for e in similar]
        merged["title"] = f"{primary.get('title', '')} + {len(similar)} related"

        # Combine encoded content
        all_emojis = set()
        for entry in [primary] + similar:
            emojis = self.encoder._extract_emoji_sequence(entry.get("encoded", ""))
            all_emojis.update(emojis)

        merged["encoded"] = f"MERGED:{''.join(all_emojis[:5])}"

        # Use highest priority
        priorities = [primary.get("priority", "low")] + [e.get("priority", "low") for e in similar]
        priority_order = {"critical": 0, "high": 1, "medium": 2, "low": 3}
        merged["priority"] = min(priorities, key=lambda p: priority_order.get(p, 3))

        return merged


