import SwiftUI
import Combine
import Network

struct ContentView: View {
    @StateObject private var warpSpeedClient = WarpSpeedClient()
    @State private var selectedConsciousness: ConsciousnessLevel = .alpha
    @State private var inputText: String = ""
    @State private var isProcessing: Bool = false

    var body: some View {
        NavigationView {
            VStack(spacing: 20) {
                // Consciousness Level Selector
                consciousnessSelector

                // Input Interface
                inputInterface

                // Processing Status
                processingStatus

                // Results Display
                resultsDisplay

                Spacer()
            }
            .padding()
            .navigationTitle("ðŸŒ€ Warp-Speed Mobile")
            .onAppear {
                warpSpeedClient.connect()
            }
        }
    }

    private var consciousnessSelector: some View {
        VStack(alignment: .leading, spacing: 10) {
            Text("Consciousness Level")
                .font(.headline)
                .foregroundColor(.primary)

            Picker("Consciousness Level", selection: $selectedConsciousness) {
                ForEach(ConsciousnessLevel.allCases, id: \.self) { level in
                    HStack {
                        Text(level.symbol)
                            .font(.title2)
                        Text(level.displayName)
                            .font(.body)
                        Spacer()
                        Text("\(level.gpuCores) cores")
                            .font(.caption)
                            .foregroundColor(.secondary)
                    }
                    .tag(level)
                }
            }
            .pickerStyle(WheelPickerStyle())
            .frame(height: 120)
        }
    }

    private var inputInterface: some View {
        VStack(alignment: .leading, spacing: 10) {
            Text("Input")
                .font(.headline)

            TextEditor(text: $inputText)
                .frame(height: 100)
                .padding(8)
                .overlay(
                    RoundedRectangle(cornerRadius: 8)
                        .stroke(Color.gray.opacity(0.5), lineWidth: 1)
                )

            Button(action: processInput) {
                HStack {
                    if isProcessing {
                        ProgressView()
                            .progressViewStyle(CircularProgressViewStyle(tint: .white))
                            .scaleEffect(0.8)
                    } else {
                        Image(systemName: "bolt.fill")
                    }
                    Text(isProcessing ? "Processing..." : "Process with \(selectedConsciousness.displayName)")
                }
                .foregroundColor(.white)
                .padding()
                .background(
                    LinearGradient(
                        gradient: Gradient(colors: selectedConsciousness.colors),
                        startPoint: .leading,
                        endPoint: .trailing
                    )
                )
                .cornerRadius(12)
            }
            .disabled(isProcessing || inputText.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty)
        }
    }

    private var processingStatus: some View {
        VStack(alignment: .leading, spacing: 10) {
            Text("System Status")
                .font(.headline)

            HStack {
                Circle()
                    .fill(warpSpeedClient.connectionStatus.color)
                    .frame(width: 12, height: 12)
                Text(warpSpeedClient.connectionStatus.description)
                    .font(.body)
                Spacer()
                if let latency = warpSpeedClient.latency {
                    Text("\(latency)ms")
                        .font(.caption)
                        .foregroundColor(.secondary)
                }
            }

            if let metrics = warpSpeedClient.lastMetrics {
                VStack(alignment: .leading, spacing: 4) {
                    metricRow("ETD Generated", "\(String(format: "%.6f", metrics.etdGenerated))")
                    metricRow("GPU Cores Active", "\(metrics.gpuCoresActive)")
                    metricRow("Peak TFLOPS", "\(String(format: "%.1f", metrics.peakTflops))")
                    metricRow("Processing Time", "\(String(format: "%.2f", metrics.processingTime))ms")
                }
            }
        }
    }

    private var resultsDisplay: some View {
        VStack(alignment: .leading, spacing: 10) {
            Text("Response")
                .font(.headline)

            ScrollView {
                if let response = warpSpeedClient.lastResponse {
                    Text(response)
                        .font(.body)
                        .padding()
                        .background(Color.gray.opacity(0.1))
                        .cornerRadius(8)
                } else {
                    Text("No response yet")
                        .foregroundColor(.secondary)
                        .italic()
                }
            }
            .frame(maxHeight: 200)
        }
    }

    private func metricRow(_ label: String, _ value: String) -> some View {
        HStack {
            Text(label)
                .font(.caption)
                .foregroundColor(.secondary)
            Spacer()
            Text(value)
                .font(.caption)
                .foregroundColor(.primary)
        }
    }

    private func processInput() {
        guard !inputText.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty else { return }

        isProcessing = true

        Task {
            await warpSpeedClient.process(
                input: inputText,
                consciousnessLevel: selectedConsciousness
            )

            await MainActor.run {
                isProcessing = false
            }
        }
    }
}

enum ConsciousnessLevel: String, CaseIterable {
    case alpha = "alpha"
    case beta = "beta"
    case gamma = "gamma"
    case delta = "delta"
    case omega = "omega"

    var displayName: String {
        switch self {
        case .alpha: return "Alpha"
        case .beta: return "Beta" 
        case .gamma: return "Gamma"
        case .delta: return "Delta"
        case .omega: return "Omega"
        }
    }

    var symbol: String {
        switch self {
        case .alpha: return "Î±"
        case .beta: return "Î²"
        case .gamma: return "Î³"
        case .delta: return "Î´"
        case .omega: return "Î©"
        }
    }

    var gpuCores: Int {
        switch self {
        case .alpha: return 1024
        case .beta: return 4096
        case .gamma: return 16384
        case .delta: return 65536
        case .omega: return 262144
        }
    }

    var colors: [Color] {
        switch self {
        case .alpha: return [.blue, .cyan]
        case .beta: return [.green, .mint]
        case .gamma: return [.yellow, .orange]
        case .delta: return [.orange, .red]
        case .omega: return [.purple, .pink]
        }
    }
}

enum ConnectionStatus {
    case disconnected
    case connecting
    case connected
    case error(String)

    var color: Color {
        switch self {
        case .disconnected: return .gray
        case .connecting: return .yellow
        case .connected: return .green
        case .error: return .red
        }
    }

    var description: String {
        switch self {
        case .disconnected: return "Disconnected"
        case .connecting: return "Connecting..."
        case .connected: return "Connected"
        case .error(let message): return "Error: \(message)"
        }
    }
}

struct ProcessingMetrics {
    let etdGenerated: Double
    let gpuCoresActive: Int
    let peakTflops: Double
    let processingTime: Double
    let consciousnessLevel: ConsciousnessLevel
}

#Preview {
    ContentView()
}

