    @Published var connectionStatus: ConnectionStatus = .disconnected
    @Published var lastResponse: String?
    @Published var lastMetrics: ProcessingMetrics?
    @Published var latency: Int?

    private var webSocketTask: URLSessionWebSocketTask?
    private var urlSession: URLSession
    private let serverHost: String
    private let serverPort: Int

    private var pingTimer: Timer?
    private var reconnectTimer: Timer?
    private var lastPingTime: Date?

    init(serverHost: String = "localhost", serverPort: Int = 4000) {
        self.serverHost = serverHost
        self.serverPort = serverPort

        let config = URLSessionConfiguration.default
        config.timeoutIntervalForRequest = 30
        config.timeoutIntervalForResource = 60
        self.urlSession = URLSession(configuration: config)
    }

    func connect() {
        guard connectionStatus != .connecting && connectionStatus != .connected else { return }

        connectionStatus = .connecting

        guard let url = URL(string: "ws://\(serverHost):\(serverPort)/socket/websocket") else {
            connectionStatus = .error("Invalid URL")
            return
        }

        var request = URLRequest(url: url)
        request.setValue("websocket", forHTTPHeaderField: "Upgrade")
        request.setValue("Upgrade", forHTTPHeaderField: "Connection")
        request.setValue("13", forHTTPHeaderField: "Sec-WebSocket-Version")
        request.setValue(generateWebSocketKey(), forHTTPHeaderField: "Sec-WebSocket-Key")

        webSocketTask = urlSession.webSocketTask(with: request)
        webSocketTask?.resume()

        connectionStatus = .connected
        startListening()
        startPingTimer()

        // Join the warp_speed channel
        joinWarpSpeedChannel()
    }

    func disconnect() {
        stopTimers()
        webSocketTask?.cancel(with: .goingAway, reason: nil)
        webSocketTask = nil
        connectionStatus = .disconnected
        lastResponse = nil
        lastMetrics = nil
        latency = nil
    }

    func process(input: String, consciousnessLevel: ConsciousnessLevel) async {
        guard connectionStatus == .connected else {
            // Attempt to reconnect if not connected
            connect()
            try? await Task.sleep(nanoseconds: 1_000_000_000) // Wait 1 second
            guard connectionStatus == .connected else { return }
        }

        let message = WarpSpeedMessage(
            topic: "warp_speed:process",
            event: "process_input",
            payload: WarpSpeedPayload(
                input: input,
                consciousness_level: consciousnessLevel.rawValue
            ),
            ref: generateRef()
        )

        do {
            let jsonData = try JSONEncoder().encode(message)
            let jsonString = String(data: jsonData, encoding: .utf8) ?? ""
            await sendMessage(.string(jsonString))
        } catch {
            print("Failed to encode message: \(error)")
        }
    }

    private func startListening() {
        guard let webSocketTask = webSocketTask else { return }

        webSocketTask.receive { [weak self] result in
            Task { @MainActor in
                guard let self = self else { return }

                switch result {
                case .success(let message):
                    await self.handleMessage(message)
                    self.startListening() // Continue listening

                case .failure(let error):
                    print("WebSocket error: \(error)")
                    self.connectionStatus = .error(error.localizedDescription)
                    self.scheduleReconnect()
                }
            }
        }
    }

    private func handleMessage(_ message: URLSessionWebSocketTask.Message) async {
        switch message {
        case .string(let text):
            await parseWarpSpeedResponse(text)
        case .data(let data):
            if let text = String(data: data, encoding: .utf8) {
                await parseWarpSpeedResponse(text)
            }
        @unknown default:
            print("Unknown message type received")
        }
    }

    private func parseWarpSpeedResponse(_ text: String) async {
        do {
            if let data = text.data(using: .utf8) {
                if let response = try? JSONDecoder().decode(WarpSpeedResponse.self, from: data) {
                    await handleWarpSpeedResponse(response)
                } else if let phoenixMessage = try? JSONDecoder().decode(PhoenixMessage.self, from: data) {
                    await handlePhoenixMessage(phoenixMessage)
                }
            }
        } catch {
            print("Failed to parse message: \(error)")
        }
    }

    private func handleWarpSpeedResponse(_ response: WarpSpeedResponse) async {
        lastResponse = response.response

        // Convert consciousness level string back to enum
        if let level = ConsciousnessLevel(rawValue: response.consciousness_level) {
            lastMetrics = ProcessingMetrics(
                etdGenerated: response.etd_generated,
                gpuCoresActive: response.gpu_metrics.cores_active,
                peakTflops: response.gpu_metrics.teraflops,
                processingTime: response.processing_time,
                consciousnessLevel: level
            )
        }
    }

    private func handlePhoenixMessage(_ message: PhoenixMessage) async {
        switch message.event {
        case "phx_reply":
            if message.payload.status == "ok" {
                print("Successfully joined channel")
            }
        case "pong":
            if let startTime = lastPingTime {
                latency = Int(Date().timeIntervalSince(startTime) * 1000)
            }
        default:
            print("Received Phoenix message: \(message.event)")
        }
    }

    private func joinWarpSpeedChannel() {
        let joinMessage = PhoenixMessage(
            topic: "warp_speed:process",
            event: "phx_join",
            payload: PhoenixPayload(status: nil),
            ref: generateRef()
        )

        Task {
            do {
                let jsonData = try JSONEncoder().encode(joinMessage)
                let jsonString = String(data: jsonData, encoding: .utf8) ?? ""
                await sendMessage(.string(jsonString))
            } catch {
                print("Failed to join channel: \(error)")
            }
        }
    }

    private func sendMessage(_ message: URLSessionWebSocketTask.Message) async {
        do {
            try await webSocketTask?.send(message)
        } catch {
            print("Failed to send message: \(error)")
            connectionStatus = .error("Send failed")
        }
    }

    private func startPingTimer() {
        pingTimer = Timer.scheduledTimer(withTimeInterval: 30.0, repeats: true) { [weak self] _ in
            Task { @MainActor in
                await self?.sendPing()
            }
        }
    }

    private func sendPing() async {
        lastPingTime = Date()
        let pingMessage = PhoenixMessage(
            topic: "phoenix",
            event: "heartbeat",
            payload: PhoenixPayload(status: nil),
            ref: generateRef()
        )

        do {
            let jsonData = try JSONEncoder().encode(pingMessage)
            let jsonString = String(data: jsonData, encoding: .utf8) ?? ""
            await sendMessage(.string(jsonString))
        } catch {
            print("Failed to send ping: \(error)")
        }
    }

    private func scheduleReconnect() {
        reconnectTimer?.invalidate()
        reconnectTimer = Timer.scheduledTimer(withTimeInterval: 5.0, repeats: false) { [weak self] _ in
            Task { @MainActor in
                self?.connect()
            }
        }
    }

    private func stopTimers() {
        pingTimer?.invalidate()
        pingTimer = nil
        reconnectTimer?.invalidate()
        reconnectTimer = nil
    }

    private func generateRef() -> String {
        return UUID().uuidString
    }

    private func generateWebSocketKey() -> String {
        let key = Data((0..<16).map { _ in UInt8.random(in: 0...255) })
        return key.base64EncodedString()
    }

    deinit {
        disconnect()
    }
}

// MARK: - Message Models

struct WarpSpeedMessage: Codable {
    let topic: String
    let event: String
    let payload: WarpSpeedPayload
    let ref: String
}

struct WarpSpeedPayload: Codable {
    let input: String
    let consciousness_level: String
}

struct WarpSpeedResponse: Codable {
    let response: String
    let consciousness_level: String
    let etd_generated: Double
    let processing_time: Double
    let gpu_metrics: GPUMetrics
    let quantum_trace: [QuantumEvent]?
}

struct GPUMetrics: Codable {
    let cores_active: Int
    let teraflops: Double
}

struct QuantumEvent: Codable {
    let event_type: String
    let timestamp: Double?
}

struct PhoenixMessage: Codable {
    let topic: String
    let event: String
    let payload: PhoenixPayload
    let ref: String
}

struct PhoenixPayload: Codable {
    let status: String?
}

