    57‚Üí        let editor_bridge = editor::Bridge::new(&config.editor)?;
    58‚Üí        
    59‚Üí        // Initialize terminal multiplexer with lazy loading
    60‚Üí        lazy_loader.load_component("terminal_mux").await?;
    61‚Üí        let terminal_mux = terminal::Multiplexer::new(&config.terminal)?;
    62‚Üí        
    63‚Üí        // Initialize LSP manager with lazy loading
