        )

        # Apply styling to success/fail indicators
        html_content = html_content.replace("‚úÖ", '<span class="success">‚úÖ</span>')
        html_content = html_content.replace("‚ùå", '<span class="error">‚ùå</span>')
        html_content = html_content.replace("‚ö†Ô∏è", '<span class="warning">‚ö†Ô∏è</span>')

        return html_template.format(content=html_content)

    def save_reports(self):
        """Save reports to files"""

        # Collect all results
        self.collect_test_results()
        self.analyze_performance()

        # Generate markdown report
        markdown_report = self.generate_markdown_report()

        # Save markdown
        with open("vimmacs_test_report.md", "w") as f:
            f.write(markdown_report)
        print("üìù Markdown report saved to: vimmacs_test_report.md")

        # Generate and save HTML
        try:
            html_report = self.generate_html_report(markdown_report)
            with open("vimmacs_test_report.html", "w") as f:
                f.write(html_report)
            print("üåê HTML report saved to: vimmacs_test_report.html")
        except ImportError:
            print("‚ö†Ô∏è  Install 'markdown' package for HTML reports: pip install markdown")

        # Save JSON summary
        summary = {
            "timestamp": datetime.now().isoformat(),
            "test_results": self.test_results,
            "performance_metrics": self.performance_metrics,
            "security_findings": self.security_findings
        }

        with open("vimmacs_test_summary.json", "w") as f:
            json.dump(summary, f, indent=2, default=str)
        print("üìä JSON summary saved to: vimmacs_test_summary.json")


def main():
    """Generate comprehensive test reports"""
    generator = TestReportGenerator()
