//! Vimmacs Core - Secure AI-integrated terminal development environment
//! 
//! This library provides the core functionality for Vimmacs, including:
//! - Secure AI integration with Claude Code
//! - Input sanitization and validation
//! - Editor interoperability layer
//! - Configuration management
//! - Performance monitoring

use anyhow::Result;
use tracing::{info, warn, error};

pub mod ai;
pub mod config;
pub mod editor;
pub mod security;
pub mod terminal;
pub mod utils;

/// Core Vimmacs configuration and runtime
pub struct VimmacsCore {
    config: config::Config,
    ai_client: ai::Client,
    editor_bridge: editor::Bridge,
    terminal_mux: terminal::Multiplexer,
}

impl VimmacsCore {
    /// Initialize a new Vimmacs instance with secure defaults
    pub async fn new() -> Result<Self> {
        info!("Initializing Vimmacs Core");

        // Load configuration with security validation
        let config = config::Config::load().await?;

        // Initialize AI client with security wrapper
        let ai_client = ai::Client::new(&config.ai)?;

        // Set up editor bridge
        let editor_bridge = editor::Bridge::new(&config.editor)?;

        // Initialize terminal multiplexer
        let terminal_mux = terminal::Multiplexer::new(&config.terminal)?;

        info!("Vimmacs Core initialized successfully");
        Ok(Self {
            config,
            ai_client,
            editor_bridge,
            terminal_mux,
        })
    }

    /// Start the main Vimmacs runtime
    pub async fn run(&mut self) -> Result<()> {
        info!("Starting Vimmacs runtime");

        // Initialize components
        self.ai_client.connect().await?;
        self.editor_bridge.start().await?;
        self.terminal_mux.initialize().await?;

        // Main event loop
        loop {
            tokio::select! {
                // Handle AI requests
                result = self.ai_client.handle_request() => {
                    if let Err(e) = result {
                        error!("AI request failed: {}", e);
                    }
                }

                // Handle editor events
                result = self.editor_bridge.handle_event() => {
                    if let Err(e) = result {
                        error!("Editor event failed: {}", e);
                    }
                }

                // Handle terminal events
                result = self.terminal_mux.handle_event() => {
                    if let Err(e) = result {
                        error!("Terminal event failed: {}", e);
                    }
                }

                // Graceful shutdown signal
                _ = tokio::signal::ctrl_c() => {
                    info!("Received shutdown signal");
                    break;
                }
            }
        }

        self.shutdown().await?;
        Ok(())
    }

    /// Gracefully shutdown all components
    async fn shutdown(&mut self) -> Result<()> {
        info!("Shutting down Vimmacs");

        self.ai_client.disconnect().await?;
        self.editor_bridge.stop().await?;
        self.terminal_mux.cleanup().await?;

        info!("Vimmacs shutdown complete");
        Ok(())
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[tokio::test]
    async fn test_vimmacs_initialization() {
        // Test basic initialization
        // This would require proper test configuration setup
    }

    #[test]
    fn test_security_validation() {
        // Test input validation and security features
        use crate::security::sanitize_input;

        let malicious_input = "<script>alert('xss')</script>";
        let sanitized = sanitize_input(malicious_input);
        assert!(!sanitized.contains("<script>"));
    }
}

