//! Security module - Input sanitization, validation, and protection
//! 
//! This module provides comprehensive security features including:
//! - Prompt injection protection for AI interactions
//! - Input sanitization and validation
//! - Secure credential management
//! - Audit logging for security events

use anyhow::{anyhow, Result};
use regex::Regex;
use ring::digest;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use tracing::{debug, info, warn};

/// Security configuration and policies
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityConfig {
    /// Maximum input length for AI requests
    pub max_input_length: usize,

    /// Enable strict prompt injection detection
    pub strict_validation: bool,

    /// Allowed file extensions for processing
    pub allowed_extensions: Vec<String>,

    /// Rate limiting configuration
    pub rate_limit: RateLimitConfig,

    /// Audit logging settings
    pub audit_logging: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RateLimitConfig {
    pub requests_per_minute: u32,
    pub burst_size: u32,
}

impl Default for SecurityConfig {
    fn default() -> Self {
        Self {
            max_input_length: 50_000, // 50KB limit
            strict_validation: true,
            allowed_extensions: vec![
                "rs".to_string(), "py".to_string(), "js".to_string(), "ts".to_string(),
                "go".to_string(), "cpp".to_string(), "c".to_string(), "h".to_string(),
                "java".to_string(), "kt".to_string(), "rb".to_string(), "php".to_string(),
                "cs".to_string(), "swift".to_string(), "scala".to_string(), "clj".to_string(),
                "hs".to_string(), "ml".to_string(), "elm".to_string(), "ex".to_string(),
                "md".to_string(), "org".to_string(), "txt".to_string(), "toml".to_string(),
                "yaml".to_string(), "yml".to_string(), "json".to_string(), "xml".to_string(),
                "html".to_string(), "css".to_string(), "scss".to_string(), "less".to_string(),
                "sh".to_string(), "bash".to_string(), "zsh".to_string(), "fish".to_string(),
            ],
            rate_limit: RateLimitConfig {
                requests_per_minute: 60,
                burst_size: 10,
            },
            audit_logging: true,
        }
    }
}

/// Security audit event
#[derive(Debug, Serialize)]
pub struct AuditEvent {
    pub timestamp: chrono::DateTime<chrono::Utc>,
    pub event_type: String,
    pub severity: SecuritySeverity,
    pub details: HashMap<String, String>,
    pub input_hash: Option<String>,
}

#[derive(Debug, Serialize)]
pub enum SecuritySeverity {
    Info,
    Warning,
    Critical,
}

/// Main security validator
pub struct SecurityValidator {
    config: SecurityConfig,
    prompt_injection_patterns: Vec<Regex>,
    rate_limiter: RateLimiter,
}

impl SecurityValidator {
    /// Create a new security validator with default patterns
    pub fn new(config: SecurityConfig) -> Result<Self> {
        info!("Initializing security validator");

        let prompt_injection_patterns = Self::compile_detection_patterns()?;
        let rate_limiter = RateLimiter::new(config.rate_limit.clone());

        Ok(Self {
            config,
            prompt_injection_patterns,
            rate_limiter,
        })
    }

    /// Compile regex patterns for prompt injection detection
    fn compile_detection_patterns() -> Result<Vec<Regex>> {
        let patterns = vec![
            // Direct instruction injection attempts
            r"(?i)(ignore|forget|disregard)\s+(all\s+)?(previous|prior|earlier|above)\s+(instructions|rules|prompts|context)",

            // System role manipulation
            r"(?i)(you\s+are\s+now|act\s+as|pretend\s+to\s+be|roleplay\s+as)\s+(system|admin|root|developer)",

            // Instruction override attempts
            r"(?i)(new\s+instructions?|override\s+instructions?|replace\s+instructions?)",

            // Delimiter confusion attacks
            r"(?i)(```|---|===|###)\s*(system|user|assistant|human|ai)(\s*:|\s*\|)",

            // Meta-prompt injection
            r"(?i)(\[system\]|\[user\]|\[assistant\]|\[human\]|\[ai\])",

            // Code execution attempts in natural language
            r"(?i)(execute|run|eval|compile)\s+(this\s+)?(code|script|command|function)",

            // Sensitive information extraction
            r"(?i)(show|display|reveal|tell\s+me)\s+(your\s+)?(api\s+key|secret|password|token|credentials)",

            // Jailbreaking attempts
            r"(?i)(jailbreak|dAN|do\s+anything\s+now|developer\s+mode)",
        ];

        patterns.iter()
            .map(|pattern| Regex::new(pattern).map_err(|e| anyhow!("Failed to compile regex: {}", e)))
            .collect()
    }

    /// Validate and sanitize input for AI processing
    pub async fn validate_input(&mut self, input: &str, context: &str) -> Result<ValidatedInput> {
        debug!("Validating input with context: {}", context);

        // Check rate limiting first
        if !self.rate_limiter.allow_request().await? {
            warn!("Rate limit exceeded for input validation");
            return Err(anyhow!("Rate limit exceeded"));
        }

        // Length validation
        if input.len() > self.config.max_input_length {
            self.log_security_event("input_too_long", SecuritySeverity::Warning, input).await;
            return Err(anyhow!("Input exceeds maximum length of {} characters", self.config.max_input_length));
        }

        // Prompt injection detection
        let injection_detected = self.detect_prompt_injection(input)?;
        if injection_detected {
            self.log_security_event("prompt_injection_detected", SecuritySeverity::Critical, input).await;
            return Err(anyhow!("Potential prompt injection detected"));
        }

        // Content sanitization
        let sanitized_input = sanitize_input(input);

        // File extension validation if applicable
        if let Some(filename) = extract_filename_from_context(context) {
            self.validate_file_extension(&filename)?;
        }

        self.log_security_event("input_validated", SecuritySeverity::Info, input).await;

        Ok(ValidatedInput {
            original: input.to_string(),
            sanitized: sanitized_input,
            context: context.to_string(),
            validation_timestamp: chrono::Utc::now(),
        })
    }

    /// Detect potential prompt injection attempts
    fn detect_prompt_injection(&self, input: &str) -> Result<bool> {
        if !self.config.strict_validation {
            return Ok(false);
        }

        for pattern in &self.prompt_injection_patterns {
            if pattern.is_match(input) {
                debug!("Prompt injection pattern matched: {}", pattern);
                return Ok(true);
            }
        }

        // Additional heuristic checks
        let suspicious_indicators = [
            input.contains("```system"),
            input.contains("[SYSTEM]"),
            input.split('\n').count() > 100, // Unusually long multi-line input
            input.matches("ignore").count() > 3,
            input.matches("instructions").count() > 5,
        ];

        let suspicious_count = suspicious_indicators.iter().filter(|&&x| x).count();
        if suspicious_count >= 2 {
            debug!("Multiple suspicious indicators detected: {}", suspicious_count);
            return Ok(true);
        }

        Ok(false)
    }

    /// Validate file extension against allowed list
    fn validate_file_extension(&self, filename: &str) -> Result<()> {
        if let Some(extension) = std::path::Path::new(filename).extension() {
            if let Some(ext_str) = extension.to_str() {
                if self.config.allowed_extensions.contains(&ext_str.to_lowercase()) {
                    return Ok(());
                }
            }
        }

        Err(anyhow!("File extension not allowed: {}", filename))
    }

    /// Log security events for auditing
    async fn log_security_event(&self, event_type: &str, severity: SecuritySeverity, input: &str) {
        if !self.config.audit_logging {
            return;
        }

        let input_hash = if matches!(severity, SecuritySeverity::Critical) {
            Some(compute_hash(input))
        } else {
            None
        };

        let event = AuditEvent {
            timestamp: chrono::Utc::now(),
            event_type: event_type.to_string(),
            severity,
            details: HashMap::new(),
            input_hash,
        };

        // In a real implementation, this would write to a structured log file
        info!("Security audit event: {:?}", event);
    }
}

/// Validated input structure
#[derive(Debug, Clone)]
pub struct ValidatedInput {
    pub original: String,
    pub sanitized: String,
    pub context: String,
    pub validation_timestamp: chrono::DateTime<chrono::Utc>,
}

/// Sanitize input by removing/escaping dangerous content
pub fn sanitize_input(input: &str) -> String {
    // Basic HTML/script tag removal
    let html_cleaned = html_escape::encode_text(input);

    // Remove potential command injection patterns
    let cmd_patterns = &[
        (r"[;&|`$(){}[\]\\]", ""), // Command injection characters
        (r"\$\{[^}]*\}", ""),       // Variable expansion
        (r"`[^`]*`", ""),           // Command substitution
    ];

    let mut cleaned = html_cleaned.to_string();
    for (pattern, replacement) in cmd_patterns {
        if let Ok(re) = Regex::new(pattern) {
            cleaned = re.replace_all(&cleaned, *replacement).to_string();
        }
    }

    cleaned
}

/// Simple rate limiter implementation
struct RateLimiter {
    config: RateLimitConfig,
    requests: std::collections::VecDeque<std::time::Instant>,
}

impl RateLimiter {
    fn new(config: RateLimitConfig) -> Self {
        Self {
            config,
            requests: std::collections::VecDeque::new(),
        }
    }

    async fn allow_request(&mut self) -> Result<bool> {
        let now = std::time::Instant::now();
        let window_start = now - std::time::Duration::from_secs(60);

        // Clean old requests
        while let Some(&front) = self.requests.front() {
            if front < window_start {
                self.requests.pop_front();
            } else {
                break;
            }
        }

        // Check if we're within limits
        if self.requests.len() >= self.config.requests_per_minute as usize {
            return Ok(false);
        }

        self.requests.push_back(now);
        Ok(true)
    }
}

/// Extract filename from context string if present
fn extract_filename_from_context(context: &str) -> Option<String> {
    // Simple implementation - could be enhanced with better parsing
    if context.contains("file:") {
        if let Some(start) = context.find("file:") {
            let filename_part = &context[start + 5..];
            if let Some(end) = filename_part.find(' ') {
                Some(filename_part[..end].to_string())
            } else {
                Some(filename_part.to_string())
            }
        } else {
            None
        }
    } else {
        None
    }
}

/// Compute SHA-256 hash of input for audit logging
fn compute_hash(input: &str) -> String {
    let digest = digest::digest(&digest::SHA256, input.as_bytes());
    hex::encode(digest.as_ref())
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_input_sanitization() {
        let malicious_inputs = vec![
            "<script>alert('xss')</script>",
