//! AI Integration Module - Secure Claude Code API client
//! 
//! This module provides secure integration with Claude Code API including:
//! - Authenticated request handling with security validation
//! - Response parsing and error handling
//! - Rate limiting and retry logic
//! - Caching for performance optimization
//! - Secure credential management

use anyhow::{anyhow, Result};
use reqwest::{header::HeaderMap, Client as HttpClient};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::sync::Arc;
use std::time::{Duration, Instant};
use tokio::sync::Mutex;
use tracing::{debug, error, info, warn};
use uuid::Uuid;

use crate::security::{SecurityValidator, ValidatedInput};

/// AI client configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AiConfig {
    /// Claude API endpoint
    pub api_endpoint: String,

    /// API key for authentication
    pub api_key: String,

    /// Model to use for requests
    pub model: String,

    /// Maximum tokens per request
    pub max_tokens: u32,

    /// Request timeout in seconds
    pub timeout_seconds: u64,

    /// Enable response caching
    pub cache_enabled: bool,

    /// Cache TTL in seconds
    pub cache_ttl: u64,

    /// Retry configuration
    pub retry_config: RetryConfig,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RetryConfig {
    pub max_attempts: u32,
    pub base_delay_ms: u64,
    pub max_delay_ms: u64,
    pub exponential_backoff: bool,
}

impl Default for AiConfig {
    fn default() -> Self {
        Self {
            api_endpoint: "https://api.anthropic.com/v1/messages".to_string(),
            api_key: String::new(), // Should be loaded from secure config
            model: "claude-3-sonnet-20240229".to_string(),
            max_tokens: 4096,
            timeout_seconds: 60,
            cache_enabled: true,
            cache_ttl: 300, // 5 minutes
            retry_config: RetryConfig {
                max_attempts: 3,
                base_delay_ms: 1000,
                max_delay_ms: 10000,
                exponential_backoff: true,
            },
        }
    }
}

/// Claude API request structure
#[derive(Debug, Serialize)]
struct ClaudeRequest {
    model: String,
    max_tokens: u32,
    messages: Vec<Message>,
    system: Option<String>,
    temperature: f32,
    metadata: RequestMetadata,
}

#[derive(Debug, Serialize, Deserialize)]
struct Message {
    role: String,
    content: String,
}

#[derive(Debug, Serialize)]
struct RequestMetadata {
    user_id: String,
    session_id: String,
    timestamp: String,
}

/// Claude API response structure
#[derive(Debug, Deserialize)]
struct ClaudeResponse {
    id: String,
    model: String,
    role: String,
    content: Vec<ContentBlock>,
    usage: TokenUsage,
    stop_reason: String,
}

#[derive(Debug, Deserialize)]
struct ContentBlock {
    #[serde(rename = "type")]
    content_type: String,
    text: String,
}

#[derive(Debug, Deserialize)]
struct TokenUsage {
    input_tokens: u32,
    output_tokens: u32,
}

/// Processed AI response
#[derive(Debug, Clone)]
pub struct AiResponse {
    pub id: String,
    pub content: String,
    pub model: String,
    pub input_tokens: u32,
    pub output_tokens: u32,
    pub processing_time: Duration,
    pub cached: bool,
}

/// Response cache entry
#[derive(Debug, Clone)]
struct CacheEntry {
    response: AiResponse,
    created_at: Instant,
    access_count: u32,
}

/// AI request context
#[derive(Debug)]
pub struct RequestContext {
    pub session_id: String,
    pub user_id: String,
    pub file_path: Option<String>,
    pub editor_context: Option<String>,
    pub system_prompt: Option<String>,
}

impl Default for RequestContext {
    fn default() -> Self {
        Self {
            session_id: Uuid::new_v4().to_string(),
            user_id: "default".to_string(),
            file_path: None,
            editor_context: None,
            system_prompt: None,
        }
    }
}

/// Main AI client with security integration
pub struct Client {
    config: AiConfig,
    http_client: HttpClient,
    security_validator: Arc<Mutex<SecurityValidator>>,
    response_cache: Arc<Mutex<HashMap<String, CacheEntry>>>,
    session_id: String,
}

impl Client {
    /// Create a new AI client with security validation
    pub fn new(config: &AiConfig) -> Result<Self> {
        info!("Initializing AI client with security validation");

        // Validate API key is present
        if config.api_key.is_empty() {
            return Err(anyhow!("API key not configured"));
        }

        // Create HTTP client with timeout and headers
        let mut headers = HeaderMap::new();
        headers.insert("anthropic-version", "2023-06-01".parse()?);
        headers.insert("content-type", "application/json".parse()?);
        headers.insert("x-api-key", config.api_key.parse()?);

        let http_client = HttpClient::builder()
            .timeout(Duration::from_secs(config.timeout_seconds))
            .default_headers(headers)
            .user_agent("vimmacs/1.0")
            .build()?;

        // Initialize security validator
        let security_config = crate::security::SecurityConfig::default();
        let security_validator = Arc::new(Mutex::new(
            SecurityValidator::new(security_config)?
        ));

        let response_cache = Arc::new(Mutex::new(HashMap::new()));
        let session_id = Uuid::new_v4().to_string();

        Ok(Self {
            config: config.clone(),
            http_client,
            security_validator,
            response_cache,
            session_id,
        })
    }

    /// Connect to the AI service (placeholder for connection setup)
    pub async fn connect(&self) -> Result<()> {
        info!("Connecting to AI service at {}", self.config.api_endpoint);

        // Test connection with a minimal request
        let test_context = RequestContext::default();
        let test_result = self.generate_response("test connection", test_context).await;

        match test_result {
            Ok(_) => {
                info!("AI service connection successful");
                Ok(())
            }
            Err(e) => {
                error!("Failed to connect to AI service: {}", e);
                Err(anyhow!("AI service connection failed: {}", e))
            }
        }
    }

    /// Disconnect from the AI service
    pub async fn disconnect(&self) -> Result<()> {
        info!("Disconnecting from AI service");
        self.cleanup_cache().await;
        Ok(())
    }

    /// Generate AI response with security validation
    pub async fn generate_response(&self, input: &str, context: RequestContext) -> Result<AiResponse> {
        let start_time = Instant::now();

        // Security validation
        let validated_input = {
            let mut validator = self.security_validator.lock().await;
            let context_str = format!(
                "session:{} file:{:?} editor:{:?}", 
                context.session_id,
                context.file_path,
                context.editor_context
            );
            validator.validate_input(input, &context_str).await?
        };

        // Check cache first
        if self.config.cache_enabled {
            if let Some(cached) = self.get_cached_response(&validated_input.sanitized).await {
                debug!("Returning cached response for input");
                return Ok(cached);
            }
        }

        // Prepare request
        let request = self.prepare_request(&validated_input, &context)?;

        // Send request with retry logic
        let response = self.send_request_with_retry(request).await?;

        // Process and cache response
        let ai_response = self.process_response(response, start_time, false).await?;

        if self.config.cache_enabled {
            self.cache_response(&validated_input.sanitized, ai_response.clone()).await;
        }

        Ok(ai_response)
    }

    /// Handle incoming AI requests (main event loop integration)
    pub async fn handle_request(&self) -> Result<()> {
        // Placeholder for request handling logic
        // In a real implementation, this would listen for incoming requests
        // from the terminal interface or editor bridge
        tokio::time::sleep(Duration::from_millis(100)).await;
        Ok(())
    }

    /// Prepare Claude API request
    fn prepare_request(&self, validated_input: &ValidatedInput, context: &RequestContext) -> Result<ClaudeRequest> {
        let messages = vec![Message {
            role: "user".to_string(),
            content: validated_input.sanitized.clone(),
        }];

        let system_prompt = context.system_prompt.clone().unwrap_or_else(|| {
            "You are Claude Code, a helpful AI assistant for software development. \
             Provide clear, secure, and well-structured code and explanations.".to_string()
        });

        let metadata = RequestMetadata {
            user_id: context.user_id.clone(),
            session_id: context.session_id.clone(),
            timestamp: chrono::Utc::now().to_rfc3339(),
        };

        Ok(ClaudeRequest {
            model: self.config.model.clone(),
            max_tokens: self.config.max_tokens,
            messages,
            system: Some(system_prompt),
            temperature: 0.3, // Conservative temperature for code generation
            metadata,
        })
    }

    /// Send request with exponential backoff retry
    async fn send_request_with_retry(&self, request: ClaudeRequest) -> Result<ClaudeResponse> {
        let mut attempt = 0;
        let mut delay = Duration::from_millis(self.config.retry_config.base_delay_ms);

        loop {
            attempt += 1;

            match self.send_request(&request).await {
                Ok(response) => return Ok(response),
                Err(e) if attempt >= self.config.retry_config.max_attempts => {
                    error!("Request failed after {} attempts: {}", attempt, e);
                    return Err(e);
                }
                Err(e) => {
                    warn!("Request attempt {} failed: {}, retrying in {:?}", attempt, e, delay);
                    tokio::time::sleep(delay).await;

                    if self.config.retry_config.exponential_backoff {
                        delay = std::cmp::min(
                            delay * 2,
                            Duration::from_millis(self.config.retry_config.max_delay_ms),
                        );
                    }
                }
            }
        }
    }

    /// Send single HTTP request to Claude API
    async fn send_request(&self, request: &ClaudeRequest) -> Result<ClaudeResponse> {
        debug!("Sending request to Claude API: {}", self.config.api_endpoint);

        let response = self.http_client
            .post(&self.config.api_endpoint)
            .json(request)
            .send()
            .await?;

        if !response.status().is_success() {
            let status = response.status();
            let error_text = response.text().await.unwrap_or_default();
            return Err(anyhow!("API request failed with status {}: {}", status, error_text));
        }

        let claude_response: ClaudeResponse = response.json().await?;
        debug!("Received response from Claude API: {}", claude_response.id);

        Ok(claude_response)
    }

    /// Process Claude API response
    async fn process_response(&self, response: ClaudeResponse, start_time: Instant, cached: bool) -> Result<AiResponse> {
        let content = response.content
            .into_iter()
            .filter(|block| block.content_type == "text")
            .map(|block| block.text)
            .collect::<Vec<_>>()
            .join("\n");

        if content.is_empty() {
            return Err(anyhow!("Empty response content from Claude API"));
        }

        Ok(AiResponse {
            id: response.id,
            content,
            model: response.model,
            input_tokens: response.usage.input_tokens,
            output_tokens: response.usage.output_tokens,
            processing_time: start_time.elapsed(),
            cached,
        })
    }

    /// Check response cache
    async fn get_cached_response(&self, input: &str) -> Option<AiResponse> {
        let mut cache = self.response_cache.lock().await;
        let cache_key = self.generate_cache_key(input);

        if let Some(entry) = cache.get_mut(&cache_key) {
            // Check TTL
            if entry.created_at.elapsed().as_secs() > self.config.cache_ttl {
                cache.remove(&cache_key);
                return None;
            }

            // Update access count
            entry.access_count += 1;

            // Return cached response
            let mut response = entry.response.clone();
            response.cached = true;
            debug!("Cache hit for input (accessed {} times)", entry.access_count);
            Some(response)
        } else {
            None
        }
    }

    /// Cache response
    async fn cache_response(&self, input: &str, response: AiResponse) {
        let mut cache = self.response_cache.lock().await;
        let cache_key = self.generate_cache_key(input);

        let entry = CacheEntry {
            response,
            created_at: Instant::now(),
            access_count: 0,
        };

        cache.insert(cache_key, entry);
        debug!("Cached response (cache size: {})", cache.len());
    }

    /// Generate cache key from input
    fn generate_cache_key(&self, input: &str) -> String {
        use ring::digest;
        let digest = digest::digest(&digest::SHA256, input.as_bytes());
        hex::encode(digest.as_ref())
    }

    /// Clean up expired cache entries
    async fn cleanup_cache(&self) {
        let mut cache = self.response_cache.lock().await;
        let expired_keys: Vec<_> = cache
            .iter()
            .filter_map(|(key, entry)| {
                if entry.created_at.elapsed().as_secs() > self.config.cache_ttl {
                    Some(key.clone())
                } else {
                    None
                }
            })
            .collect();

        for key in expired_keys {
            cache.remove(&key);
        }

        if !cache.is_empty() {
            debug!("Cache cleanup complete, {} entries remaining", cache.len());
        }
    }
}

