//! Configuration Management - Secure configuration loading and validation
//! 
//! This module provides comprehensive configuration management including:
//! - Hierarchical configuration loading (defaults -> system -> user -> environment)
//! - Secure credential management with encryption at rest
//! - Configuration validation and schema checking
//! - Hot reload capabilities with file watching
//! - Environment-specific configurations

use anyhow::{anyhow, Result};
use dirs::home_dir;
use figment::{
    providers::{Env, Format, Json, Toml},
    Figment,
};
use serde::{Deserialize, Serialize};
use std::path::{Path, PathBuf};
use tracing::{debug, info, warn};

use crate::ai::AiConfig;
use crate::security::SecurityConfig;

/// Main application configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Config {
    /// Application metadata
    pub app: AppConfig,

    /// AI integration settings
    pub ai: AiConfig,

    /// Security configuration
    pub security: SecurityConfig,

    /// Editor integration settings
    pub editor: EditorConfig,

    /// Terminal multiplexer settings
    pub terminal: TerminalConfig,

    /// Logging configuration
    pub logging: LoggingConfig,

    /// Performance settings
    pub performance: PerformanceConfig,

    /// UI/UX preferences
    pub ui: UiConfig,
}

/// Application metadata and basic settings
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AppConfig {
    /// Application name
    pub name: String,

    /// Version string
    pub version: String,

    /// Environment (development, production, test)
    pub environment: String,

    /// Configuration directory path
    pub config_dir: PathBuf,

    /// Data directory path
    pub data_dir: PathBuf,

    /// Cache directory path
    pub cache_dir: PathBuf,

    /// Log directory path
