    /// Key bindings profile
    pub keybindings: String,

    /// Font configuration
    pub font: FontConfig,

    /// Enable notifications
    pub notifications: bool,

    /// Status line configuration
    pub statusline: StatuslineConfig,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct FontConfig {
    /// Font family
    pub family: String,

    /// Font size
    pub size: u16,

    /// Enable ligatures
    pub ligatures: bool,

    /// Line height multiplier
    pub line_height: f32,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StatuslineConfig {
    /// Enable status line
    pub enabled: bool,

    /// Position (top, bottom)
    pub position: String,

    /// Components to display
    pub components: Vec<String>,

    /// Update interval in milliseconds
    pub update_interval_ms: u64,
}

impl Default for Config {
    fn default() -> Self {
        let home = home_dir().unwrap_or_else(|| "/tmp".into());
        let config_dir = home.join(".config/vimmacs");
        let data_dir = home.join(".local/share/vimmacs");
        let cache_dir = home.join(".cache/vimmacs");
        let log_dir = data_dir.join("logs");

        Self {
            app: AppConfig {
                name: "vimmacs".to_string(),
                version: "1.0.0".to_string(),
