//! Editor Interoperability Bridge - Unified editor integration layer
//! 
//! This module provides seamless integration between different editors including:
//! - NeoVim integration with Lua scripting bridge
//! - Doom Emacs integration with Elisp communication
//! - LSP (Language Server Protocol) coordination
//! - File synchronization and real-time editing
//! - Editor-agnostic command interface

use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::{Path, PathBuf};
use std::process::Stdio;
use tokio::io::{AsyncBufReadExt, AsyncWriteExt, BufReader};
use tokio::net::{TcpListener, TcpStream};
use tokio::process::{Child, Command as AsyncCommand};
use tokio::sync::{mpsc, oneshot};
use tracing::{debug, error, info, warn};
use uuid::Uuid;

use crate::config::{EditorConfig, EmacsConfig, NeovimConfig};

/// Editor bridge for managing multiple editor instances
pub struct Bridge {
    config: EditorConfig,
    active_editors: HashMap<String, EditorInstance>,
    command_sender: mpsc::UnboundedSender<EditorCommand>,
    event_receiver: mpsc::UnboundedReceiver<EditorEvent>,
    rpc_server: Option<RpcServer>,
}

/// Supported editor types
#[derive(Debug, Clone, Copy, PartialEq, Serialize, Deserialize)]
pub enum EditorType {
    Neovim,
    Emacs,
    VSCode, // Future support
}

/// Editor instance information
#[derive(Debug)]
struct EditorInstance {
    id: String,
    editor_type: EditorType,
    process: Option<Child>,
    working_directory: PathBuf,
    open_files: Vec<PathBuf>,
    rpc_port: Option<u16>,
    status: EditorStatus,
}

/// Editor status states
#[derive(Debug, Clone, Copy, PartialEq)]
pub enum EditorStatus {
    Starting,
    Ready,
    Busy,
    Error,
    Stopped,
}

/// Editor commands for remote control
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum EditorCommand {
    OpenFile { file_path: PathBuf, line: Option<u32>, column: Option<u32> },
    SaveFile { file_path: PathBuf },
    CloseFile { file_path: PathBuf },
    ExecuteCommand { command: String, args: Vec<String> },
    GetBufferContent { file_path: PathBuf },
    SetBufferContent { file_path: PathBuf, content: String },
    GoToDefinition { file_path: PathBuf, line: u32, column: u32 },
    FindReferences { file_path: PathBuf, line: u32, column: u32 },
    ApplyCodeAction { file_path: PathBuf, action: String },
    FormatDocument { file_path: PathBuf },
    Shutdown,
}

/// Editor events for monitoring and coordination
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum EditorEvent {
    EditorStarted { id: String, editor_type: EditorType },
    EditorStopped { id: String },
    FileOpened { editor_id: String, file_path: PathBuf },
    FileClosed { editor_id: String, file_path: PathBuf },
    FileModified { editor_id: String, file_path: PathBuf },
    FileSaved { editor_id: String, file_path: PathBuf },
    CursorMoved { editor_id: String, file_path: PathBuf, line: u32, column: u32 },
    ErrorOccurred { editor_id: String, error: String },
    LspReady { editor_id: String, language: String },
}

/// RPC command and response structures
#[derive(Debug, Serialize, Deserialize)]
struct RpcRequest {
    id: String,
    method: String,
    params: serde_json::Value,
}

#[derive(Debug, Serialize, Deserialize)]
struct RpcResponse {
    id: String,
    result: Option<serde_json::Value>,
    error: Option<RpcError>,
}

#[derive(Debug, Serialize, Deserialize)]
struct RpcError {
    code: i32,
    message: String,
}

/// RPC server for editor communication
struct RpcServer {
    port: u16,
    listener: TcpListener,
}

/// File operation result
#[derive(Debug, Serialize, Deserialize)]
pub struct FileOperationResult {
    pub success: bool,
    pub message: String,
    pub content: Option<String>,
}

impl Bridge {
    /// Create a new editor bridge
    pub fn new(config: &EditorConfig) -> Result<Self> {
        info!("Initializing editor bridge");

        let (command_sender, command_receiver) = mpsc::unbounded_channel();
        let (event_sender, event_receiver) = mpsc::unbounded_channel();

        // Start RPC server for editor communication
        let rpc_server = None; // Will be initialized in start()

        Ok(Self {
            config: config.clone(),
            active_editors: HashMap::new(),
            command_sender,
            event_receiver,
            rpc_server,
        })
    }

    /// Start the editor bridge system
    pub async fn start(&mut self) -> Result<()> {
        info!("Starting editor bridge system");

        // Initialize RPC server
        self.start_rpc_server().await?;

        // Pre-start primary editor if configured
        if !self.config.primary_editor.is_empty() {
            let editor_type = match self.config.primary_editor.as_str() {
                "neovim" | "nvim" => EditorType::Neovim,
                "emacs" => EditorType::Emacs,
                _ => return Err(anyhow!("Unsupported primary editor: {}", self.config.primary_editor)),
            };

            self.start_editor(editor_type, None).await?;
        }

        Ok(())
    }

    /// Stop the editor bridge system
    pub async fn stop(&mut self) -> Result<()> {
        info!("Stopping editor bridge system");

        // Stop all active editors
        let editor_ids: Vec<_> = self.active_editors.keys().cloned().collect();
        for editor_id in editor_ids {
            self.stop_editor(&editor_id).await?;
        }

        // Close RPC server
        if let Some(_rpc_server) = self.rpc_server.take() {
            debug!("Closing RPC server");
        }

        Ok(())
    }

    /// Handle editor events
    pub async fn handle_event(&mut self) -> Result<()> {
        if let Ok(event) = self.event_receiver.try_recv() {
            match event {
                EditorEvent::FileOpened { editor_id, file_path } => {
                    debug!("File opened in editor {}: {:?}", editor_id, file_path);
                    if let Some(editor) = self.active_editors.get_mut(&editor_id) {
                        if !editor.open_files.contains(&file_path) {
                            editor.open_files.push(file_path);
                        }
                    }
                }
                EditorEvent::FileClosed { editor_id, file_path } => {
                    debug!("File closed in editor {}: {:?}", editor_id, file_path);
                    if let Some(editor) = self.active_editors.get_mut(&editor_id) {
                        editor.open_files.retain(|f| f != &file_path);
                    }
                }
                EditorEvent::ErrorOccurred { editor_id, error } => {
                    error!("Editor {} error: {}", editor_id, error);
                    if let Some(editor) = self.active_editors.get_mut(&editor_id) {
                        editor.status = EditorStatus::Error;
                    }
                }
                _ => {
                    debug!("Received editor event: {:?}", event);
                }
            }
        }

        Ok(())
    }

    /// Start a new editor instance
    pub async fn start_editor(&mut self, editor_type: EditorType, working_dir: Option<&Path>) -> Result<String> {
        let editor_id = Uuid::new_v4().to_string();
        let working_directory = working_dir
            .map(|p| p.to_path_buf())
            .unwrap_or_else(|| std::env::current_dir().unwrap_or_else(|| "/tmp".into()));

        info!("Starting {:?} editor instance: {}", editor_type, editor_id);

        let mut editor_instance = EditorInstance {
            id: editor_id.clone(),
            editor_type,
            process: None,
            working_directory: working_directory.clone(),
            open_files: Vec::new(),
            rpc_port: None,
            status: EditorStatus::Starting,
        };

        // Start the appropriate editor process
        let process = match editor_type {
            EditorType::Neovim => {
                self.start_neovim_process(&working_directory).await?
            }
            EditorType::Emacs => {
                self.start_emacs_process(&working_directory).await?
            }
            EditorType::VSCode => {
                return Err(anyhow!("VSCode integration not yet implemented"));
            }
        };

        editor_instance.process = Some(process);
        editor_instance.status = EditorStatus::Ready;

        self.active_editors.insert(editor_id.clone(), editor_instance);

        // Emit editor started event
        let _ = self.event_receiver.try_recv(); // Clear any pending events first
        // In a real implementation, we'd send this through the event system

        Ok(editor_id)
    }

    /// Stop an editor instance
    pub async fn stop_editor(&mut self, editor_id: &str) -> Result<()> {
        info!("Stopping editor instance: {}", editor_id);

        if let Some(mut editor) = self.active_editors.remove(editor_id) {
            if let Some(mut process) = editor.process.take() {
                // Try graceful shutdown first
                if let Err(_) = process.kill().await {
                    warn!("Failed to gracefully stop editor process");
                }

                // Wait for process to exit
                if let Ok(status) = process.wait().await {
                    debug!("Editor process exited with status: {:?}", status);
                }
            }

            editor.status = EditorStatus::Stopped;
        }

        Ok(())
    }

    /// Open a file in the appropriate editor
    pub async fn open_file(&mut self, file_path: &Path, line: Option<u32>, column: Option<u32>) -> Result<String> {
        let editor_type = self.determine_editor_for_file(file_path);

        // Find existing editor instance or start new one
        let editor_id = if let Some((id, _)) = self.active_editors
            .iter()
            .find(|(_, editor)| editor.editor_type == editor_type && editor.status == EditorStatus::Ready)
        {
            id.clone()
        } else {
            self.start_editor(editor_type, file_path.parent()).await?
        };

        // Send open file command to editor
        self.send_editor_command(&editor_id, EditorCommand::OpenFile {
            file_path: file_path.to_path_buf(),
            line,
            column,
        }).await?;

        Ok(editor_id)
    }

    /// Save a file in the specified editor
    pub async fn save_file(&mut self, editor_id: &str, file_path: &Path) -> Result<()> {
        self.send_editor_command(editor_id, EditorCommand::SaveFile {
            file_path: file_path.to_path_buf(),
        }).await
    }

    /// Get content of a buffer from an editor
    pub async fn get_buffer_content(&mut self, editor_id: &str, file_path: &Path) -> Result<String> {
        // In a real implementation, this would send an RPC request and wait for response
        // For now, we'll return a placeholder
        debug!("Getting buffer content from editor {}: {:?}", editor_id, file_path);
        Ok("// Buffer content placeholder".to_string())
    }

    /// Set content of a buffer in an editor
    pub async fn set_buffer_content(&mut self, editor_id: &str, file_path: &Path, content: &str) -> Result<()> {
        self.send_editor_command(editor_id, EditorCommand::SetBufferContent {
            file_path: file_path.to_path_buf(),
            content: content.to_string(),
        }).await
    }

    /// List all active editor instances
    pub fn list_editors(&self) -> Vec<(&str, EditorType, EditorStatus)> {
        self.active_editors
            .iter()
            .map(|(id, editor)| (id.as_str(), editor.editor_type, editor.status))
            .collect()
    }

    /// Get files open in a specific editor
    pub fn get_open_files(&self, editor_id: &str) -> Option<&Vec<PathBuf>> {
        self.active_editors.get(editor_id).map(|editor| &editor.open_files)
    }

    // Private helper methods

    async fn start_rpc_server(&mut self) -> Result<()> {
        let listener = TcpListener::bind("127.0.0.1:0").await?;
        let port = listener.local_addr()?.port();

        info!("Started RPC server on port {}", port);

        self.rpc_server = Some(RpcServer { port, listener });
        Ok(())
    }

    async fn start_neovim_process(&self, working_dir: &Path) -> Result<Child> {
        let mut args = vec!["--headless", "--listen", "127.0.0.1:0"];

        // Add init commands
        for init_command in &self.config.neovim.init_commands {
            args.extend(["--cmd", init_command]);
        }

        let child = AsyncCommand::new(&self.config.neovim.executable)
            .args(args)
            .current_dir(working_dir)
            .stdin(Stdio::piped())
            .stdout(Stdio::piped())
            .stderr(Stdio::piped())
            .spawn()?;

        debug!("Started Neovim process in {:?}", working_dir);
        Ok(child)
    }

    async fn start_emacs_process(&self, working_dir: &Path) -> Result<Child> {
        let mut args = vec!["--daemon"];

        // Add custom elisp configurations
        for elisp_config in &self.config.emacs.elisp_configs {
            args.extend(["--eval", elisp_config]);
        }

        let child = AsyncCommand::new(&self.config.emacs.executable)
            .args(args)
            .current_dir(working_dir)
            .stdin(Stdio::piped())
            .stdout(Stdio::piped())
            .stderr(Stdio::piped())
            .spawn()?;

        debug!("Started Emacs daemon process in {:?}", working_dir);
        Ok(child)
    }

    fn determine_editor_for_file(&self, file_path: &Path) -> EditorType {
        if self.config.auto_detect {
            if let Some(extension) = file_path.extension().and_then(|ext| ext.to_str()) {
                if let Some(editor_name) = self.config.file_associations.get(extension) {
                    return match editor_name.as_str() {
                        "neovim" | "nvim" => EditorType::Neovim,
                        "emacs" => EditorType::Emacs,
                        _ => self.default_editor_type(),
                    };
                }
            }
        }

        self.default_editor_type()
    }

    fn default_editor_type(&self) -> EditorType {
        match self.config.primary_editor.as_str() {
            "neovim" | "nvim" => EditorType::Neovim,
            "emacs" => EditorType::Emacs,
            _ => EditorType::Neovim, // Default fallback
        }
    }

    async fn send_editor_command(&self, editor_id: &str, command: EditorCommand) -> Result<()> {
        debug!("Sending command to editor {}: {:?}", editor_id, command);

        // In a real implementation, this would send the command via RPC
        // For now, we'll just log it and simulate success
        match command {
            EditorCommand::OpenFile { file_path, line, column } => {
                info!("Opening file {:?} at line {:?}, column {:?}", file_path, line, column);
            }
            EditorCommand::SaveFile { file_path } => {
                info!("Saving file {:?}", file_path);
            }
            EditorCommand::ExecuteCommand { command, args } => {
                info!("Executing command: {} {:?}", command, args);
            }
            _ => {
                debug!("Executed editor command: {:?}", command);
            }
        }

        Ok(())
    }
}

/// LSP (Language Server Protocol) integration
pub struct LspManager {
    active_servers: HashMap<String, LspServer>,
    config: EditorConfig,
}

#[derive(Debug)]
struct LspServer {
    language: String,
    process: Child,
    capabilities: Vec<String>,
}

impl LspManager {
    pub fn new(config: &EditorConfig) -> Self {
        Self {
            active_servers: HashMap::new(),
            config: config.clone(),
        }
    }

    pub async fn start_language_server(&mut self, language: &str) -> Result<()> {
        if self.active_servers.contains_key(language) {
            return Ok(()); // Already running
        }

        info!("Starting LSP server for {}", language);

        let (executable, args) = self.get_lsp_server_config(language)?;

        let process = AsyncCommand::new(executable)
            .args(args)
            .stdin(Stdio::piped())
            .stdout(Stdio::piped())
            .stderr(Stdio::piped())
            .spawn()?;

        let server = LspServer {
            language: language.to_string(),
            process,
            capabilities: Vec::new(),
        };

        self.active_servers.insert(language.to_string(), server);
        Ok(())
    }

    fn get_lsp_server_config(&self, language: &str) -> Result<(&str, Vec<&str>)> {
        match language {
            "rust" => Ok(("rust-analyzer", vec![])),
            "python" => Ok(("pylsp", vec![])),
            "javascript" | "typescript" => Ok(("typescript-language-server", vec!["--stdio"])),
            "go" => Ok(("gopls", vec![])),
            _ => Err(anyhow!("No LSP server configured for language: {}", language)),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[test]
    fn test_editor_type_serialization() {
        let editor_type = EditorType::Neovim;
        let serialized = serde_json::to_string(&editor_type).unwrap();
        let deserialized: EditorType = serde_json::from_str(&serialized).unwrap();
        assert_eq!(editor_type, deserialized);
    }

    #[test]
    fn test_editor_command_serialization() {
        let command = EditorCommand::OpenFile {
            file_path: "/tmp/test.rs".into(),
            line: Some(10),
            column: Some(5),
        };

        let serialized = serde_json::to_string(&command).unwrap();
        let deserialized: EditorCommand = serde_json::from_str(&serialized).unwrap();

        match (command, deserialized) {
            (EditorCommand::OpenFile { file_path: f1, line: l1, column: c1 },
             EditorCommand::OpenFile { file_path: f2, line: l2, column: c2 }) => {
                assert_eq!(f1, f2);
                assert_eq!(l1, l2);
                assert_eq!(c1, c2);
            }
            _ => panic!("Command serialization failed"),
        }
    }

    #[tokio::test]
    async fn test_bridge_creation() {
        let config = EditorConfig::default();
        let bridge = Bridge::new(&config);
        assert!(bridge.is_ok());
    }

    #[test]
    fn test_file_editor_determination() {
        let config = EditorConfig::default();
        let bridge = Bridge::new(&config).unwrap();

        let rs_file = Path::new("test.rs");
        let md_file = Path::new("README.md");

        assert_eq!(bridge.determine_editor_for_file(rs_file), EditorType::Neovim);
        assert_eq!(bridge.determine_editor_for_file(md_file), EditorType::Emacs);
    }
}

