//! Vimmacs - Main binary entry point
//! 
//! Terminal-based AI coding environment with security-first architecture
//! Integrates NeoVim, Doom Emacs, terminal multiplexing, and Claude Code AI

use anyhow::Result;
use clap::{Parser, Subcommand};
use std::path::PathBuf;
use tracing::{error, info};
use tracing_subscriber::{layer::SubscriberExt, util::SubscriberInitExt};

use vimmacs_core::{VimmacsCore, config::Config, utils::{SystemUtils, FileSystemUtils}};

/// Vimmacs - AI-powered terminal development environment
#[derive(Parser)]
#[command(author, version, about, long_about = None)]
struct Cli {
    /// Configuration file path
    #[arg(short, long)]
    config: Option<PathBuf>,

    /// Working directory
    #[arg(short, long)]
    directory: Option<PathBuf>,

    /// Verbose logging
    #[arg(short, long)]
    verbose: bool,

    /// Debug mode
    #[arg(short, long)]
    debug: bool,

    /// Subcommands
    #[command(subcommand)]
    command: Option<Commands>,
}

#[derive(Subcommand)]
enum Commands {
    /// Initialize vimmacs configuration
    Init {
        /// Force overwrite existing configuration
        #[arg(long)]
        force: bool,
    },
    /// Start vimmacs in interactive mode
    Start {
        /// Session name
        #[arg(short, long)]
        session: Option<String>,

        /// Editor to use (neovim, emacs, auto)
        #[arg(short, long)]
        editor: Option<String>,

        /// Files to open initially
        files: Vec<PathBuf>,
    },
    /// Manage terminal sessions
    Session {
        #[command(subcommand)]
        action: SessionCommand,
    },
    /// Project management
    Project {
        #[command(subcommand)]
        action: ProjectCommand,
    },
    /// System information and diagnostics
    Info,
    /// Run security checks
    Security {
        #[command(subcommand)]
        action: SecurityCommand,
    },
}

#[derive(Subcommand)]
enum SessionCommand {
    /// List active sessions
    List,
    /// Create new session
    Create {
        name: String,
        #[arg(short, long)]
        directory: Option<PathBuf>,
    },
    /// Attach to existing session
    Attach { name: String },
    /// Kill session
    Kill { name: String },
    /// Kill all sessions
    KillAll,
}

#[derive(Subcommand)]
enum ProjectCommand {
    /// Detect project information
    Info { path: Option<PathBuf> },
    /// Initialize project workspace
    Init { path: Option<PathBuf> },
    /// Analyze project files
    Analyze { path: Option<PathBuf> },
}

#[derive(Subcommand)]
enum SecurityCommand {
    /// Run security validation tests
    Validate,
    /// Check for security updates
    Updates,
    /// Audit configuration
    Audit,
}

#[tokio::main]
async fn main() -> Result<()> {
    let cli = Cli::parse();

    // Initialize logging
    init_logging(&cli)?;

    // Load configuration
    let config = load_config(cli.config).await?;

    // Change working directory if specified
    if let Some(ref dir) = cli.directory {
        std::env::set_current_dir(dir)?;
        info!("Changed working directory to: {:?}", dir);
    }

    // Execute command or start interactive mode
    match cli.command {
        Some(Commands::Init { force }) => {
            handle_init(force).await
        }
        Some(Commands::Start { session, editor, files }) => {
            handle_start(config, session, editor, files).await
        }
        Some(Commands::Session { action }) => {
            handle_session_command(config, action).await
        }
        Some(Commands::Project { action }) => {
            handle_project_command(action).await
        }
        Some(Commands::Info) => {
            handle_info().await
        }
        Some(Commands::Security { action }) => {
            handle_security_command(action).await
        }
        None => {
            // Default: start interactive mode
            handle_start(config, None, None, Vec::new()).await
        }
    }
}

/// Initialize logging based on CLI options
fn init_logging(cli: &Cli) -> Result<()> {
    let log_level = if cli.debug {
        "debug"
    } else if cli.verbose {
        "info"
    } else {
        "warn"
    };

