impl Config {
    /// Load configuration from multiple sources with priority order
    pub async fn load() -> Result<Self> {
        info!("Loading configuration from multiple sources");

        let home = home_dir().unwrap_or_else(|| "/tmp".into());
        let config_dir = home.join(".config/vimmacs");

        // Ensure configuration directory exists
        if !config_dir.exists() {
            std::fs::create_dir_all(&config_dir)?;
            info!("Created configuration directory: {:?}", config_dir);
        }

        // Configuration sources in priority order (last wins)
        let figment = Figment::new()
            // 1. Built-in defaults
            .merge(Self::default())
            // 2. System-wide configuration
            .merge(Toml::file("/etc/vimmacs/config.toml"))
            // 3. User configuration directory
            .merge(Toml::file(config_dir.join("config.toml")))
            // 4. User configuration file (legacy)
            .merge(Toml::file(home.join(".vimmacs.toml")))
            // 5. Local project configuration
            .merge(Toml::file("vimmacs.toml"))
            .merge(Json::file("vimmacs.json"))
            // 6. Environment variables (VIMMACS_*)
            .merge(Env::prefixed("VIMMACS_").split("_"));

        let config: Config = figment.extract()
            .map_err(|e| anyhow!("Failed to load configuration: {}", e))?;

        // Validate configuration
        config.validate().await?;

        info!("Configuration loaded successfully");
        debug!("Configuration: {:?}", config);

        Ok(config)
    }

    /// Validate configuration settings
    async fn validate(&self) -> Result<()> {
        debug!("Validating configuration");

        // Validate directories exist or can be created
        for dir in [&self.app.config_dir, &self.app.data_dir, &self.app.cache_dir] {
            if !dir.exists() {
                std::fs::create_dir_all(dir)
                    .map_err(|e| anyhow!("Failed to create directory {:?}: {}", dir, e))?;
            }
        }

        // Validate AI configuration
        if self.ai.api_key.is_empty() {
            warn!("AI API key not configured - AI features will be disabled");
        }

        // Validate editor executables
        if !self.editor.neovim.executable.as_os_str().is_empty() {
            if let Err(_) = which::which(&self.editor.neovim.executable) {
                warn!("Neovim executable not found: {:?}", self.editor.neovim.executable);
            }
        }

        if !self.editor.emacs.executable.as_os_str().is_empty() {
            if let Err(_) = which::which(&self.editor.emacs.executable) {
                warn!("Emacs executable not found: {:?}", self.editor.emacs.executable);
            }
        }

        // Validate terminal multiplexer
        match self.terminal.multiplexer.as_str() {
            "tmux" => {
                if let Err(_) = which::which(&self.terminal.tmux.executable) {
                    warn!("Tmux executable not found: {:?}", self.terminal.tmux.executable);
                }
            }
            "zellij" => {
                if let Err(_) = which::which(&self.terminal.zellij.executable) {
                    warn!("Zellij executable not found: {:?}", self.terminal.zellij.executable);
                }
            }
            _ => {
                return Err(anyhow!("Unsupported terminal multiplexer: {}", self.terminal.multiplexer));
            }
        }

        // Validate logging configuration
        if self.logging.file_enabled {
            if let Some(parent) = self.logging.file_path.parent() {
                std::fs::create_dir_all(parent)
                    .map_err(|e| anyhow!("Failed to create log directory {:?}: {}", parent, e))?;
            }
        }

        info!("Configuration validation completed successfully");
        Ok(())
    }

    /// Save current configuration to file
    pub async fn save(&self, path: Option<PathBuf>) -> Result<()> {
        let config_path = path.unwrap_or_else(|| self.app.config_dir.join("config.toml"));

        let config_content = toml::to_string_pretty(self)
            .map_err(|e| anyhow!("Failed to serialize configuration: {}", e))?;

        std::fs::write(&config_path, config_content)
            .map_err(|e| anyhow!("Failed to write configuration file {:?}: {}", config_path, e))?;

        info!("Configuration saved to {:?}", config_path);
        Ok(())
    }

    /// Get editor for file based on extension
    pub fn get_editor_for_file(&self, file_path: &Path) -> String {
        if !self.editor.auto_detect {
            return self.editor.primary_editor.clone();
        }

        if let Some(extension) = file_path.extension().and_then(|ext| ext.to_str()) {
            if let Some(editor) = self.editor.file_associations.get(extension) {
                return editor.clone();
            }
        }

        self.editor.primary_editor.clone()
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[test]
    fn test_config_defaults() {
        let config = Config::default();
        assert_eq!(config.app.name, "vimmacs");
        assert_eq!(config.app.version, "1.0.0");
        assert_eq!(config.editor.primary_editor, "neovim");
        assert!(config.editor.lsp_enabled);
    }

    #[test]
    fn test_editor_file_association() {
        let config = Config::default();

        assert_eq!(config.get_editor_for_file(Path::new("test.rs")), "neovim");
        assert_eq!(config.get_editor_for_file(Path::new("README.md")), "emacs");
        assert_eq!(config.get_editor_for_file(Path::new("unknown.xyz")), "neovim");
    }

    #[tokio::test]
    async fn test_config_validation() {
        let temp_dir = TempDir::new().unwrap();
        let mut config = Config::default();
        config.app.config_dir = temp_dir.path().join("config");
        config.app.data_dir = temp_dir.path().join("data");
        config.app.cache_dir = temp_dir.path().join("cache");

        // Should succeed with default configuration
        assert!(config.validate().await.is_ok());

        // Directories should be created
        assert!(config.app.config_dir.exists());
        assert!(config.app.data_dir.exists());
        assert!(config.app.cache_dir.exists());
    }
}

