//! Terminal Multiplexer Integration - Unified terminal session management
//! 
//! This module provides comprehensive terminal multiplexer integration including:
//! - Tmux and Zellij session management
//! - Dynamic pane creation and navigation
//! - Session persistence and restoration
//! - Inter-pane communication and coordination
//! - File system integration and project workspaces

use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::{Path, PathBuf};
use std::process::{Command, Stdio};
use tokio::process::Command as AsyncCommand;
use tracing::{debug, error, info, warn};
use uuid::Uuid;

use crate::config::{TerminalConfig, TmuxConfig, ZellijConfig};

/// Terminal multiplexer session manager
pub struct Multiplexer {
    config: TerminalConfig,
    active_sessions: HashMap<String, SessionInfo>,
    current_session: Option<String>,
    multiplexer_type: MultiplexerType,
}

/// Supported multiplexer types
#[derive(Debug, Clone, Copy, PartialEq)]
enum MultiplexerType {
    Tmux,
    Zellij,
    Screen,
}

/// Session information and metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SessionInfo {
    pub id: String,
    pub name: String,
    pub created_at: chrono::DateTime<chrono::Utc>,
    pub working_directory: PathBuf,
    pub panes: Vec<PaneInfo>,
    pub layout: String,
    pub environment: HashMap<String, String>,
    pub persistent: bool,
}

/// Pane information within a session
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PaneInfo {
    pub id: String,
    pub index: u32,
    pub title: String,
    pub command: Option<String>,
    pub active: bool,
    pub dimensions: PaneDimensions,
    pub working_directory: PathBuf,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PaneDimensions {
    pub width: u32,
    pub height: u32,
    pub x: u32,
    pub y: u32,
}

/// Terminal event types
#[derive(Debug, Clone)]
pub enum TerminalEvent {
    SessionCreated(String),
    SessionDestroyed(String),
    PaneCreated { session: String, pane: String },
    PaneDestroyed { session: String, pane: String },
    PaneActivated { session: String, pane: String },
    WindowResized { width: u32, height: u32 },
    CommandExecuted { pane: String, command: String, exit_code: i32 },
}

/// Pane creation options
#[derive(Debug, Clone)]
pub struct PaneOptions {
    pub title: Option<String>,
    pub command: Option<String>,
    pub working_directory: Option<PathBuf>,
    pub split_direction: SplitDirection,
    pub size_percentage: Option<u32>,
}

#[derive(Debug, Clone)]
pub enum SplitDirection {
    Horizontal,
    Vertical,
}

impl Default for PaneOptions {
    fn default() -> Self {
        Self {
            title: None,
            command: None,
            working_directory: None,
            split_direction: SplitDirection::Vertical,
            size_percentage: Some(50),
        }
    }
}

impl Multiplexer {
    /// Create a new terminal multiplexer instance
    pub fn new(config: &TerminalConfig) -> Result<Self> {
        info!("Initializing terminal multiplexer: {}", config.multiplexer);

        let multiplexer_type = match config.multiplexer.as_str() {
            "tmux" => MultiplexerType::Tmux,
            "zellij" => MultiplexerType::Zellij,
            "screen" => MultiplexerType::Screen,
            _ => return Err(anyhow!("Unsupported multiplexer: {}", config.multiplexer)),
        };

        // Verify multiplexer is available
        let executable = match multiplexer_type {
            MultiplexerType::Tmux => &config.tmux.executable,
            MultiplexerType::Zellij => &config.zellij.executable,
            MultiplexerType::Screen => &PathBuf::from("screen"),
        };

        if which::which(executable).is_err() {
            return Err(anyhow!("Multiplexer executable not found: {:?}", executable));
        }

        Ok(Self {
            config: config.clone(),
            active_sessions: HashMap::new(),
            current_session: None,
            multiplexer_type,
        })
    }

    /// Initialize the multiplexer system
    pub async fn initialize(&mut self) -> Result<()> {
        info!("Initializing terminal multiplexer system");

        // Load existing sessions
        self.discover_existing_sessions().await?;

        // Auto-attach to existing session or create default
        if self.config.auto_attach {
            if let Some(session_name) = self.find_attachable_session().await? {
                info!("Auto-attaching to existing session: {}", session_name);
                self.attach_session(&session_name).await?;
            } else {
                info!("Creating default session: {}", self.config.default_session);
                self.create_session(&self.config.default_session, None).await?;
            }
        }

        Ok(())
    }

    /// Cleanup and shutdown multiplexer
    pub async fn cleanup(&mut self) -> Result<()> {
        info!("Cleaning up terminal multiplexer");

        // Save session state if persistence is enabled
        if self.config.persist_sessions {
            self.save_session_state().await?;
        }

        // Optionally detach from sessions without killing them
        if let Some(current) = &self.current_session {
            info!("Detaching from current session: {}", current);
            self.detach_session().await?;
        }

        Ok(())
    }

    /// Handle terminal events
    pub async fn handle_event(&mut self) -> Result<()> {
        // In a real implementation, this would process multiplexer events
        // For now, it's a placeholder that maintains the async interface
        tokio::time::sleep(tokio::time::Duration::from_millis(100)).await;
        Ok(())
    }

    /// Create a new session
    pub async fn create_session(&mut self, name: &str, working_dir: Option<&Path>) -> Result<String> {
        info!("Creating session: {}", name);

        let session_id = Uuid::new_v4().to_string();
        let working_directory = working_dir
            .map(|p| p.to_path_buf())
            .unwrap_or_else(|| std::env::current_dir().unwrap_or_else(|| "/tmp".into()));

        match self.multiplexer_type {
            MultiplexerType::Tmux => {
                self.create_tmux_session(name, &working_directory).await?;
            }
            MultiplexerType::Zellij => {
                self.create_zellij_session(name, &working_directory).await?;
            }
            MultiplexerType::Screen => {
                return Err(anyhow!("Screen multiplexer not yet implemented"));
            }
        }

        let session_info = SessionInfo {
            id: session_id.clone(),
            name: name.to_string(),
            created_at: chrono::Utc::now(),
            working_directory,
            panes: Vec::new(),
            layout: "main-vertical".to_string(),
            environment: self.config.terminal_prefs.env_vars.clone(),
            persistent: self.config.persist_sessions,
        };

        self.active_sessions.insert(name.to_string(), session_info);
        self.current_session = Some(name.to_string());

        Ok(session_id)
    }

    /// Create a new pane in the current session
    pub async fn create_pane(&mut self, options: PaneOptions) -> Result<String> {
        let session_name = self.current_session.as_ref()
            .ok_or_else(|| anyhow!("No active session"))?;

        info!("Creating pane in session: {}", session_name);

        let pane_id = Uuid::new_v4().to_string();

        match self.multiplexer_type {
            MultiplexerType::Tmux => {
                self.create_tmux_pane(session_name, &options).await?;
            }
            MultiplexerType::Zellij => {
                self.create_zellij_pane(session_name, &options).await?;
            }
            MultiplexerType::Screen => {
                return Err(anyhow!("Screen multiplexer not yet implemented"));
            }
        }

        // Update session info with new pane
        if let Some(session) = self.active_sessions.get_mut(session_name) {
            let pane_info = PaneInfo {
                id: pane_id.clone(),
                index: session.panes.len() as u32,
                title: options.title.unwrap_or_else(|| "Shell".to_string()),
                command: options.command,
                active: true,
                dimensions: PaneDimensions { width: 80, height: 24, x: 0, y: 0 }, // Default
                working_directory: options.working_directory
                    .unwrap_or_else(|| session.working_directory.clone()),
            };

            // Mark other panes as inactive
            for pane in &mut session.panes {
                pane.active = false;
            }

            session.panes.push(pane_info);
        }

        Ok(pane_id)
    }

    /// Execute command in a specific pane
    pub async fn execute_in_pane(&self, pane_id: &str, command: &str) -> Result<()> {
        debug!("Executing command in pane {}: {}", pane_id, command);

        let session_name = self.current_session.as_ref()
            .ok_or_else(|| anyhow!("No active session"))?;

        match self.multiplexer_type {
            MultiplexerType::Tmux => {
                self.execute_tmux_command(session_name, pane_id, command).await?;
            }
            MultiplexerType::Zellij => {
                self.execute_zellij_command(session_name, pane_id, command).await?;
            }
            MultiplexerType::Screen => {
                return Err(anyhow!("Screen multiplexer not yet implemented"));
            }
        }

        Ok(())
    }

    /// Switch to a specific session
    pub async fn attach_session(&mut self, session_name: &str) -> Result<()> {
        info!("Attaching to session: {}", session_name);

        match self.multiplexer_type {
            MultiplexerType::Tmux => {
                self.attach_tmux_session(session_name).await?;
            }
            MultiplexerType::Zellij => {
                self.attach_zellij_session(session_name).await?;
            }
            MultiplexerType::Screen => {
                return Err(anyhow!("Screen multiplexer not yet implemented"));
            }
        }

        self.current_session = Some(session_name.to_string());
        Ok(())
    }

    /// Detach from current session
    pub async fn detach_session(&mut self) -> Result<()> {
        if let Some(session_name) = &self.current_session {
            info!("Detaching from session: {}", session_name);

            match self.multiplexer_type {
                MultiplexerType::Tmux => {
                    self.detach_tmux_session().await?;
                }
                MultiplexerType::Zellij => {
                    self.detach_zellij_session().await?;
                }
                MultiplexerType::Screen => {
                    return Err(anyhow!("Screen multiplexer not yet implemented"));
                }
            }

            self.current_session = None;
        }

        Ok(())
    }

    /// List all active sessions
    pub fn list_sessions(&self) -> Vec<&SessionInfo> {
        self.active_sessions.values().collect()
    }

    /// Get current session information
    pub fn current_session(&self) -> Option<&SessionInfo> {
        self.current_session.as_ref()
            .and_then(|name| self.active_sessions.get(name))
    }

    // Tmux-specific implementations
    async fn create_tmux_session(&self, name: &str, working_dir: &Path) -> Result<()> {
        let output = AsyncCommand::new(&self.config.tmux.executable)
            .args(["new-session", "-d", "-s", name, "-c"])
            .arg(working_dir)
            .output()
            .await?;

        if !output.status.success() {
            let error = String::from_utf8_lossy(&output.stderr);
            return Err(anyhow!("Failed to create tmux session: {}", error));
        }

        Ok(())
    }

    async fn create_tmux_pane(&self, session_name: &str, options: &PaneOptions) -> Result<()> {
        let split_flag = match options.split_direction {
