    }

    #[tokio::test]
    async fn test_filesystem_utils_analyze_directory() {
        let temp_dir = TempDir::new().unwrap();
        let test_file = temp_dir.path().join("test.rs");
        tokio::fs::write(&test_file, "fn main() {}").await.unwrap();

        let stats = FileSystemUtils::analyze_directory_files(temp_dir.path()).await.unwrap();
        assert_eq!(stats.total_files, 1);
        assert!(stats.file_types.contains_key("rs"));
    }

    #[tokio::test]
    async fn test_project_detection() {
        let temp_dir = TempDir::new().unwrap();
        let cargo_toml = temp_dir.path().join("Cargo.toml");
        tokio::fs::write(&cargo_toml, "[package]\nname = \"test\"").await.unwrap();

        let project_info = FileSystemUtils::detect_project_info(temp_dir.path()).await.unwrap();
        assert_eq!(project_info.project_type, ProjectType::Rust);
        assert_eq!(project_info.main_language, Some("rust".to_string()));
    }
}

