//! Utility Functions - Common helper functions and system utilities
//! 
//! This module provides reusable utility functions including:
//! - File system operations and project detection
//! - Process management and system integration
//! - String manipulation and text processing
//! - Performance monitoring and metrics collection
//! - Cross-platform compatibility helpers

use anyhow::{anyhow, Result};
use std::collections::HashMap;
use std::env;
use std::path::{Path, PathBuf};
use std::time::{Duration, Instant, SystemTime, UNIX_EPOCH};
use tokio::fs;
use tokio::process::Command;
use tracing::{debug, warn};
use walkdir::WalkDir;

/// Project detection and metadata
#[derive(Debug, Clone)]
pub struct ProjectInfo {
    pub root_path: PathBuf,
    pub project_type: ProjectType,
    pub name: String,
    pub main_language: Option<String>,
    pub build_system: Option<String>,
    pub git_repository: bool,
    pub config_files: Vec<PathBuf>,
}

/// Supported project types
#[derive(Debug, Clone, PartialEq, Eq, Hash)]
pub enum ProjectType {
    Rust,
    Python,
    JavaScript,
    TypeScript,
    Go,
    Java,
    CSharp,
    Cpp,
    Mixed,
    Unknown,
}

/// Performance metrics collection
#[derive(Debug, Clone)]
pub struct PerformanceMetrics {
    pub memory_usage_mb: f64,
    pub cpu_usage_percent: f64,
    pub file_operations_per_sec: u32,
    pub network_requests_per_min: u32,
    pub cache_hit_rate: f64,
    pub response_time_ms: HashMap<String, u64>,
}

/// System information
#[derive(Debug, Clone)]
pub struct SystemInfo {
    pub os_type: String,
    pub os_version: String,
    pub architecture: String,
    pub cpu_cores: u32,
    pub total_memory_gb: f64,
    pub available_memory_gb: f64,
    pub terminal_type: Option<String>,
    pub shell: Option<String>,
}

/// File operation statistics
#[derive(Debug, Clone)]
pub struct FileStats {
    pub total_files: u32,
    pub total_size_bytes: u64,
    pub file_types: HashMap<String, u32>,
    pub largest_files: Vec<(PathBuf, u64)>,
    pub recently_modified: Vec<(PathBuf, SystemTime)>,
}

/// Text processing utilities
pub struct TextUtils;

impl TextUtils {
    /// Extract filename from various context formats
    pub fn extract_filename(text: &str) -> Option<String> {
        // Try common patterns: file:path, "path", 'path', or standalone paths
        let patterns = [
            r#"file:([^\s]+)"#,
            r#""([^"]+\.[a-zA-Z0-9]+)""#,
            r#"'([^']+\.[a-zA-Z0-9]+)'"#,
            r#"([/\w\-\.]+\.[a-zA-Z0-9]+)"#,
        ];

        for pattern in &patterns {
            if let Ok(re) = regex::Regex::new(pattern) {
                if let Some(captures) = re.captures(text) {
                    if let Some(filename) = captures.get(1) {
                        return Some(filename.as_str().to_string());
                    }
                }
            }
        }

        None
    }

    /// Convert text to safe filename
    pub fn to_safe_filename(text: &str) -> String {
        text.chars()
            .map(|c| match c {
                'a'..='z' | 'A'..='Z' | '0'..='9' | '-' | '_' | '.' => c,
                ' ' => '_',
                _ => '-',
            })
            .collect::<String>()
            .trim_matches('-')
            .to_string()
    }

    /// Truncate text to specified length with ellipsis
    pub fn truncate_with_ellipsis(text: &str, max_length: usize) -> String {
        if text.len() <= max_length {
            text.to_string()
        } else {
            format!("{}...", &text[..max_length.saturating_sub(3)])
        }
    }

    /// Count lines in text
    pub fn count_lines(text: &str) -> usize {
        text.lines().count()
    }

    /// Extract code blocks from markdown-style text
    pub fn extract_code_blocks(text: &str) -> Vec<(Option<String>, String)> {
        let mut blocks = Vec::new();
        let mut in_block = false;
        let mut current_language = None;
        let mut current_block = String::new();

        for line in text.lines() {
            if line.starts_with("```") {
                if in_block {
                    // End of block
                    blocks.push((current_language.clone(), current_block.trim().to_string()));
                    current_block.clear();
                    current_language = None;
                    in_block = false;
                } else {
                    // Start of block
                    current_language = if line.len() > 3 {
                        Some(line[3..].trim().to_string())
                    } else {
                        None
                    };
                    in_block = true;
                }
            } else if in_block {
                current_block.push_str(line);
                current_block.push('\n');
            }
        }

        blocks
    }
}

/// File system utilities
pub struct FileSystemUtils;

impl FileSystemUtils {
    /// Detect project type and information from directory
    pub async fn detect_project_info(path: &Path) -> Result<ProjectInfo> {
        let mut project_info = ProjectInfo {
            root_path: path.to_path_buf(),
            project_type: ProjectType::Unknown,
            name: path.file_name()
                .and_then(|n| n.to_str())
                .unwrap_or("unknown")
                .to_string(),
            main_language: None,
            build_system: None,
            git_repository: false,
            config_files: Vec::new(),
        };

        // Check for git repository
        if path.join(".git").exists() {
            project_info.git_repository = true;
        }

        // Detect project type based on configuration files
        let config_indicators = [
            ("Cargo.toml", ProjectType::Rust, "rust", Some("cargo")),
            ("pyproject.toml", ProjectType::Python, "python", Some("pip")),
            ("requirements.txt", ProjectType::Python, "python", Some("pip")),
            ("package.json", ProjectType::JavaScript, "javascript", Some("npm")),
            ("tsconfig.json", ProjectType::TypeScript, "typescript", Some("npm")),
            ("go.mod", ProjectType::Go, "go", Some("go")),
            ("pom.xml", ProjectType::Java, "java", Some("maven")),
            ("build.gradle", ProjectType::Java, "java", Some("gradle")),
            ("*.csproj", ProjectType::CSharp, "csharp", Some("dotnet")),
            ("CMakeLists.txt", ProjectType::Cpp, "cpp", Some("cmake")),
            ("Makefile", ProjectType::Cpp, "cpp", Some("make")),
        ];

        for &(file_pattern, ref project_type, language, build_system) in &config_indicators {
            let config_path = path.join(file_pattern);
            if config_path.exists() || (file_pattern.contains('*') && Self::glob_exists(path, file_pattern).await) {
                project_info.project_type = project_type;
                project_info.main_language = Some(language.to_string());
                project_info.build_system = build_system.map(|s| s.to_string());
                project_info.config_files.push(config_path);
                break;
            }
        }

        // If no specific type detected, analyze file extensions
        if project_info.project_type == ProjectType::Unknown {
            let file_stats = Self::analyze_directory_files(path).await?;
            project_info.project_type = Self::infer_project_type_from_files(&file_stats);
            project_info.main_language = Self::infer_main_language(&file_stats);
        }

        Ok(project_info)
    }

    /// Get file statistics for directory
    pub async fn analyze_directory_files(path: &Path) -> Result<FileStats> {
        let mut stats = FileStats {
            total_files: 0,
            total_size_bytes: 0,
            file_types: HashMap::new(),
            largest_files: Vec::new(),
            recently_modified: Vec::new(),
        };

        for entry in WalkDir::new(path)
            .into_iter()
            .filter_map(|e| e.ok())
            .filter(|e| e.file_type().is_file())
        {
            let file_path = entry.path();

            // Get file metadata
            if let Ok(metadata) = entry.metadata() {
                stats.total_files += 1;
                stats.total_size_bytes += metadata.len();

                // Track file extension
                if let Some(extension) = file_path.extension().and_then(|e| e.to_str()) {
                    *stats.file_types.entry(extension.to_lowercase()).or_insert(0) += 1;
                }

                // Track largest files (keep top 10)
                stats.largest_files.push((file_path.to_path_buf(), metadata.len()));
                stats.largest_files.sort_by(|a, b| b.1.cmp(&a.1));
                stats.largest_files.truncate(10);

                // Track recently modified files (keep top 20)
                if let Ok(modified) = metadata.modified() {
                    stats.recently_modified.push((file_path.to_path_buf(), modified));
                    stats.recently_modified.sort_by(|a, b| b.1.cmp(&a.1));
                    stats.recently_modified.truncate(20);
                }
            }
        }

        Ok(stats)
    }

    /// Find files matching pattern recursively
    pub async fn find_files_with_pattern(path: &Path, pattern: &str) -> Result<Vec<PathBuf>> {
        let mut matching_files = Vec::new();

        for entry in WalkDir::new(path)
            .into_iter()
            .filter_map(|e| e.ok())
            .filter(|e| e.file_type().is_file())
        {
            let file_path = entry.path();
            if let Some(file_name) = file_path.file_name().and_then(|n| n.to_str()) {
                if glob_match::glob_match(pattern, file_name) {
                    matching_files.push(file_path.to_path_buf());
                }
            }
        }

        Ok(matching_files)
    }

    /// Create directory structure if it doesn't exist
    pub async fn ensure_directory_structure(paths: &[&Path]) -> Result<()> {
        for path in paths {
            if !path.exists() {
                fs::create_dir_all(path).await?;
                debug!("Created directory: {:?}", path);
            }
        }
        Ok(())
    }

    /// Copy file with backup if destination exists
    pub async fn copy_with_backup(source: &Path, destination: &Path) -> Result<()> {
        if destination.exists() {
            let backup_path = destination.with_extension(
                format!("{}.backup.{}", 
                    destination.extension().and_then(|e| e.to_str()).unwrap_or(""),
                    chrono::Utc::now().format("%Y%m%d_%H%M%S")
                )
            );
            fs::rename(destination, backup_path).await?;
        }

        fs::copy(source, destination).await?;
        Ok(())
    }

    // Helper methods
    async fn glob_exists(path: &Path, pattern: &str) -> bool {
        if let Ok(files) = Self::find_files_with_pattern(path, pattern).await {
            !files.is_empty()
        } else {
            false
        }
    }

    fn infer_project_type_from_files(stats: &FileStats) -> ProjectType {
        let mut type_scores = HashMap::new();

        for (ext, count) in &stats.file_types {
            let weight = *count as f64;
            match ext.as_str() {
                "rs" => *type_scores.entry(ProjectType::Rust).or_insert(0.0) += weight * 2.0,
                "py" => *type_scores.entry(ProjectType::Python).or_insert(0.0) += weight * 2.0,
                "js" => *type_scores.entry(ProjectType::JavaScript).or_insert(0.0) += weight * 1.5,
                "ts" => *type_scores.entry(ProjectType::TypeScript).or_insert(0.0) += weight * 2.0,
                "go" => *type_scores.entry(ProjectType::Go).or_insert(0.0) += weight * 2.0,
                "java" => *type_scores.entry(ProjectType::Java).or_insert(0.0) += weight * 2.0,
                "cs" => *type_scores.entry(ProjectType::CSharp).or_insert(0.0) += weight * 2.0,
                "cpp" | "cxx" | "cc" => *type_scores.entry(ProjectType::Cpp).or_insert(0.0) += weight * 1.8,
                "c" | "h" => *type_scores.entry(ProjectType::Cpp).or_insert(0.0) += weight * 1.5,
                _ => {}
            }
        }

        type_scores
            .into_iter()
            .max_by(|a, b| a.1.partial_cmp(&b.1).unwrap_or(std::cmp::Ordering::Equal))
            .map(|(project_type, _)| project_type)
            .unwrap_or(ProjectType::Unknown)
    }

    fn infer_main_language(stats: &FileStats) -> Option<String> {
        stats.file_types
            .iter()
            .max_by_key(|(_, count)| *count)
            .map(|(ext, _)| {
                match ext.as_str() {
                    "rs" => "rust",
                    "py" => "python",
                    "js" => "javascript",
                    "ts" => "typescript",
                    "go" => "go",
                    "java" => "java",
                    "cs" => "csharp",
                    "cpp" | "cxx" | "cc" | "c" => "cpp",
                    _ => ext,
                }
                .to_string()
            })
    }
}

/// System utilities
pub struct SystemUtils;

impl SystemUtils {
    /// Get system information
    pub async fn get_system_info() -> SystemInfo {
        SystemInfo {
            os_type: env::consts::OS.to_string(),
            os_version: Self::get_os_version().await.unwrap_or_else(|| "unknown".to_string()),
            architecture: env::consts::ARCH.to_string(),
            cpu_cores: num_cpus::get() as u32,
            total_memory_gb: Self::get_total_memory_gb().await,
            available_memory_gb: Self::get_available_memory_gb().await,
            terminal_type: env::var("TERM").ok(),
            shell: env::var("SHELL").ok(),
        }
    }

    /// Check if a command is available in PATH
    pub async fn is_command_available(command: &str) -> bool {
        Command::new("which")
            .arg(command)
            .output()
            .await
            .map(|output| output.status.success())
            .unwrap_or(false)
    }

    /// Execute command and return output
    pub async fn execute_command(command: &str, args: &[&str]) -> Result<String> {
        let output = Command::new(command)
            .args(args)
            .output()
            .await?;

        if output.status.success() {
            Ok(String::from_utf8_lossy(&output.stdout).trim().to_string())
        } else {
            let stderr = String::from_utf8_lossy(&output.stderr);
            Err(anyhow!("Command failed: {}", stderr))
        }
    }

    /// Get current working directory with error handling
    pub fn get_current_dir() -> PathBuf {
        env::current_dir().unwrap_or_else(|_| {
            warn!("Could not determine current directory, using /tmp");
            "/tmp".into()
        })
    }

    /// Format duration as human-readable string
    pub fn format_duration(duration: Duration) -> String {
        let total_seconds = duration.as_secs();

        if total_seconds < 60 {
            format!("{}s", total_seconds)
        } else if total_seconds < 3600 {
            format!("{}m {}s", total_seconds / 60, total_seconds % 60)
        } else {
            let hours = total_seconds / 3600;
            let minutes = (total_seconds % 3600) / 60;
            let seconds = total_seconds % 60;
            format!("{}h {}m {}s", hours, minutes, seconds)
        }
    }

    /// Format file size as human-readable string
    pub fn format_file_size(bytes: u64) -> String {
        const UNITS: &[&str] = &["B", "KB", "MB", "GB", "TB"];
        let mut size = bytes as f64;
        let mut unit_index = 0;

        while size >= 1024.0 && unit_index < UNITS.len() - 1 {
            size /= 1024.0;
            unit_index += 1;
        }

        if unit_index == 0 {
            format!("{} {}", size as u64, UNITS[unit_index])
        } else {
            format!("{:.1} {}", size, UNITS[unit_index])
        }
    }

    // Private helper methods
    async fn get_os_version() -> Option<String> {
        if cfg!(target_os = "linux") {
            Self::execute_command("lsb_release", &["-d", "-s"]).await.ok()
                .or_else(|| std::fs::read_to_string("/etc/os-release").ok().and_then(|content| {
                    content.lines()
                        .find(|line| line.starts_with("PRETTY_NAME="))
                        .and_then(|line| line.split('=').nth(1))
                        .map(|s| s.trim_matches('"').to_string())
                }))
        } else if cfg!(target_os = "macos") {
            Self::execute_command("sw_vers", &["-productVersion"]).await.ok()
        } else if cfg!(target_os = "windows") {
            Self::execute_command("ver", &[]).await.ok()
        } else {
            None
        }
    }

    async fn get_total_memory_gb() -> f64 {
        // This is a simplified implementation
        // In a real system, you'd use system-specific APIs
        4.0 // Default assumption
    }

    async fn get_available_memory_gb() -> f64 {
        // This is a simplified implementation
        // In a real system, you'd use system-specific APIs
        2.0 // Default assumption
    }
}

/// Performance monitoring utilities
pub struct PerformanceMonitor {
    start_time: Instant,
    metrics: PerformanceMetrics,
}

impl PerformanceMonitor {
    pub fn new() -> Self {
        Self {
            start_time: Instant::now(),
            metrics: PerformanceMetrics {
                memory_usage_mb: 0.0,
                cpu_usage_percent: 0.0,
                file_operations_per_sec: 0,
                network_requests_per_min: 0,
                cache_hit_rate: 0.0,
                response_time_ms: HashMap::new(),
            },
        }
    }

    pub fn elapsed(&self) -> Duration {
        self.start_time.elapsed()
    }

    pub fn record_operation(&mut self, operation: &str, duration: Duration) {
        self.metrics.response_time_ms.insert(
            operation.to_string(),
            duration.as_millis() as u64,
        );
    }

    pub fn get_metrics(&self) -> &PerformanceMetrics {
        &self.metrics
    }

    pub async fn update_system_metrics(&mut self) {
        // In a real implementation, this would collect actual system metrics
        // For now, we'll use placeholder values
        self.metrics.memory_usage_mb = 256.0; // Placeholder
        self.metrics.cpu_usage_percent = 15.0; // Placeholder
    }
}

impl Default for PerformanceMonitor {
    fn default() -> Self {
        Self::new()
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[test]
    fn test_text_utils_filename_extraction() {
        assert_eq!(
            TextUtils::extract_filename("file:test.rs"),
            Some("test.rs".to_string())
        );
        assert_eq!(
            TextUtils::extract_filename("Please edit \"main.py\""),
            Some("main.py".to_string())
        );
        assert_eq!(
            TextUtils::extract_filename("No filename here"),
            None
        );
    }

    #[test]
    fn test_text_utils_safe_filename() {
        assert_eq!(
            TextUtils::to_safe_filename("Hello World!@#$"),
            "Hello_World----"
        );
        assert_eq!(
            TextUtils::to_safe_filename("valid_filename.txt"),
            "valid_filename.txt"
        );
    }

    #[test]
    fn test_text_utils_truncate() {
        assert_eq!(
            TextUtils::truncate_with_ellipsis("Hello World", 5),
            "He..."
        );
        assert_eq!(
            TextUtils::truncate_with_ellipsis("Hi", 5),
            "Hi"
        );
    }

    #[test]
    fn test_code_block_extraction() {
        let text = r#"
Here's some Rust code:

```rust
fn main() {
    println!("Hello, world!");
}
```

And some Python:

```python
print("Hello, world!")
```
"#;

        let blocks = TextUtils::extract_code_blocks(text);
        assert_eq!(blocks.len(), 2);
        assert_eq!(blocks[0].0, Some("rust".to_string()));
        assert!(blocks[0].1.contains("fn main()"));
        assert_eq!(blocks[1].0, Some("python".to_string()));
        assert!(blocks[1].1.contains("print("));
    }

