//! LSP Manager - Centralized Language Server Protocol management
//! 
//! This module provides unified LSP server management including:
//! - Centralized server lifecycle management
//! - Intelligent server routing based on file types and projects
//! - Performance optimization with connection pooling
//! - Protocol adaptation for different editors
//! - Health monitoring and automatic recovery

use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::{Path, PathBuf};
use std::sync::Arc;
use tokio::io::{AsyncBufReadExt, AsyncWriteExt, BufReader, BufWriter};
use tokio::net::{TcpListener, TcpStream};
use tokio::process::{Child, Command as AsyncCommand};
use tokio::sync::{mpsc, Mutex, RwLock};
use tokio::time::{timeout, Duration, Instant};
use tracing::{debug, error, info, warn};
use uuid::Uuid;

use crate::config::Config;
use crate::utils::{FileSystemUtils, ProjectType, PerformanceMonitor};

/// LSP server specification and configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct LspServerConfig {
    pub name: String,
    pub command: String,
    pub args: Vec<String>,
    pub languages: Vec<String>,
    pub file_extensions: Vec<String>,
    pub initialization_options: serde_json::Value,
    pub settings: serde_json::Value,
    pub port: Option<u16>,
    pub use_stdio: bool,
    pub startup_timeout: Duration,
    pub memory_limit_mb: Option<u64>,
    pub auto_restart: bool,
    pub health_check_interval: Duration,
}

/// LSP server instance with runtime information
#[derive(Debug)]
pub struct LspServerInstance {
    pub id: String,
    pub config: LspServerConfig,
    pub process: Option<Child>,
    pub status: ServerStatus,
    pub tcp_stream: Option<TcpStream>,
    pub stdin_writer: Option<BufWriter<tokio::process::ChildStdin>>,
    pub stdout_reader: Option<BufReader<tokio::process::ChildStdout>>,
    pub capabilities: ServerCapabilities,
    pub active_documents: HashMap<PathBuf, DocumentInfo>,
    pub performance_metrics: PerformanceMetrics,
    pub health_status: HealthStatus,
    pub last_activity: Instant,
}

/// LSP server status
#[derive(Debug, Clone, PartialEq)]
pub enum ServerStatus {
    Starting,
    Initializing,
    Ready,
    Busy,
    Error(String),
    Stopping,
    Stopped,
    Hibernating,
}

/// Server capabilities as reported by initialize response
#[derive(Debug, Clone, Default)]
pub struct ServerCapabilities {
    pub completion_provider: bool,
    pub hover_provider: bool,
    pub signature_help_provider: bool,
    pub definition_provider: bool,
    pub references_provider: bool,
    pub document_highlight_provider: bool,
    pub document_symbol_provider: bool,
    pub workspace_symbol_provider: bool,
    pub code_action_provider: bool,
    pub code_lens_provider: bool,
    pub document_formatting_provider: bool,
    pub document_range_formatting_provider: bool,
    pub document_on_type_formatting_provider: bool,
    pub rename_provider: bool,
    pub document_link_provider: bool,
    pub execute_command_provider: bool,
    pub experimental: HashMap<String, serde_json::Value>,
}

/// Document information tracked by server
#[derive(Debug, Clone)]
pub struct DocumentInfo {
    pub uri: String,
    pub language_id: String,
    pub version: i32,
    pub content: String,
    pub diagnostics: Vec<Diagnostic>,
    pub last_modified: Instant,
}

/// LSP diagnostic information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Diagnostic {
    pub range: Range,
    pub severity: DiagnosticSeverity,
    pub code: Option<String>,
    pub source: Option<String>,
    pub message: String,
    pub related_information: Vec<DiagnosticRelatedInformation>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Range {
    pub start: Position,
    pub end: Position,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Position {
    pub line: u32,
    pub character: u32,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum DiagnosticSeverity {
    Error = 1,
    Warning = 2,
    Information = 3,
    Hint = 4,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DiagnosticRelatedInformation {
    pub location: Location,
    pub message: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Location {
    pub uri: String,
    pub range: Range,
}

/// Performance metrics for LSP servers
#[derive(Debug, Clone, Default)]
pub struct PerformanceMetrics {
    pub startup_time: Duration,
    pub average_response_time: Duration,
    pub total_requests: u64,
    pub failed_requests: u64,
    pub memory_usage_mb: f64,
    pub cpu_usage_percent: f64,
    pub cache_hit_rate: f64,
}

/// Health status monitoring
#[derive(Debug, Clone)]
pub struct HealthStatus {
    pub is_healthy: bool,
    pub last_health_check: Instant,
    pub consecutive_failures: u32,
    pub uptime: Duration,
    pub restart_count: u32,
}

impl Default for HealthStatus {
    fn default() -> Self {
        Self {
            is_healthy: true,
            last_health_check: Instant::now(),
            consecutive_failures: 0,
            uptime: Duration::from_secs(0),
            restart_count: 0,
        }
    }
}

/// LSP request/response types
#[derive(Debug, Serialize, Deserialize)]
pub struct LspRequest {
    pub id: String,
    pub method: String,
    pub params: serde_json::Value,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct LspResponse {
    pub id: String,
    pub result: Option<serde_json::Value>,
    pub error: Option<LspError>,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct LspNotification {
    pub method: String,
    pub params: serde_json::Value,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct LspError {
    pub code: i32,
    pub message: String,
    pub data: Option<serde_json::Value>,
}

/// Main LSP manager for centralized server management
pub struct LspManager {
    config: Config,
    server_configs: HashMap<String, LspServerConfig>,
    active_servers: Arc<RwLock<HashMap<String, Arc<Mutex<LspServerInstance>>>>>,
    language_to_server: HashMap<String, String>,
    extension_to_server: HashMap<String, String>,
    project_servers: HashMap<PathBuf, Vec<String>>,
    request_sender: mpsc::UnboundedSender<LspManagerRequest>,
    response_receiver: Arc<Mutex<mpsc::UnboundedReceiver<LspManagerResponse>>>,
    performance_monitor: Arc<Mutex<PerformanceMonitor>>,
}

/// Internal manager requests
#[derive(Debug)]
pub enum LspManagerRequest {
    StartServer { server_name: String, project_path: PathBuf },
    StopServer { server_name: String },
    RestartServer { server_name: String },
    SendRequest { server_name: String, request: LspRequest },
    SendNotification { server_name: String, notification: LspNotification },
    HealthCheck { server_name: String },
    GetServerInfo { server_name: String },
    HibernateServer { server_name: String },
    WakeupServer { server_name: String },
}

/// Manager responses
#[derive(Debug)]
pub enum LspManagerResponse {
    ServerStarted { server_name: String, capabilities: ServerCapabilities },
    ServerStopped { server_name: String },
    ServerError { server_name: String, error: String },
    Response { server_name: String, response: LspResponse },
    Notification { server_name: String, notification: LspNotification },
    HealthReport { server_name: String, status: HealthStatus },
    ServerInfo { server_name: String, info: ServerInfo },
}

#[derive(Debug, Clone)]
pub struct ServerInfo {
    pub name: String,
    pub status: ServerStatus,
    pub capabilities: ServerCapabilities,
    pub active_documents: usize,
    pub performance_metrics: PerformanceMetrics,
    pub health_status: HealthStatus,
}

impl LspManager {
    /// Create a new LSP manager with default server configurations
    pub fn new(config: Config) -> Result<Self> {
        info!("Initializing LSP manager");

        let (request_sender, request_receiver) = mpsc::unbounded_channel();
        let (response_sender, response_receiver) = mpsc::unbounded_channel();

        let server_configs = Self::create_default_server_configs();
        let language_to_server = Self::build_language_mapping(&server_configs);
        let extension_to_server = Self::build_extension_mapping(&server_configs);

        let manager = Self {
            config,
            server_configs,
            active_servers: Arc::new(RwLock::new(HashMap::new())),
            language_to_server,
            extension_to_server,
            project_servers: HashMap::new(),
            request_sender,
            response_receiver: Arc::new(Mutex::new(response_receiver)),
            performance_monitor: Arc::new(Mutex::new(PerformanceMonitor::new())),
        };

        // Start background task for request processing
        tokio::spawn(Self::process_requests(
            request_receiver,
            response_sender,
            manager.active_servers.clone(),
            manager.server_configs.clone(),
        ));

        Ok(manager)
    }

    /// Get server for a specific file path
    pub async fn get_server_for_file(&self, file_path: &Path) -> Option<String> {
        // First try extension mapping
        if let Some(extension) = file_path.extension().and_then(|e| e.to_str()) {
            if let Some(server_name) = self.extension_to_server.get(extension) {
                return Some(server_name.clone());
            }
        }

        // Try project-based detection
        if let Ok(project_info) = FileSystemUtils::detect_project_info(file_path.parent().unwrap_or(file_path)).await {
            match project_info.project_type {
                ProjectType::Rust => Some("rust_analyzer".to_string()),
                ProjectType::Python => Some("pylsp".to_string()),
                ProjectType::JavaScript | ProjectType::TypeScript => Some("tsserver".to_string()),
                ProjectType::Java => Some("jdtls".to_string()),
                ProjectType::Go => Some("gopls".to_string()),
                ProjectType::Cpp => Some("clangd".to_string()),
                _ => None,
            }
        } else {
            None
        }
    }

    /// Start LSP server for project
    pub async fn start_server_for_project(&self, project_path: &Path) -> Result<Vec<String>> {
        let project_info = FileSystemUtils::detect_project_info(project_path).await?;
        let mut started_servers = Vec::new();

        // Determine which servers to start based on project type
        let server_names = match project_info.project_type {
            ProjectType::Rust => vec!["rust_analyzer"],
            ProjectType::Python => vec!["pylsp"],
            ProjectType::JavaScript => vec!["tsserver"],
            ProjectType::TypeScript => vec!["tsserver"],
            ProjectType::Java => vec!["jdtls"],
            ProjectType::Go => vec!["gopls"],
            ProjectType::Cpp => vec!["clangd"],
            ProjectType::Mixed => vec!["rust_analyzer", "tsserver", "pylsp"], // Common combination
            _ => vec![],
        };

        for server_name in server_names {
            match self.start_server(server_name, project_path.to_path_buf()).await {
                Ok(_) => {
                    started_servers.push(server_name.to_string());
                    info!("Started LSP server {} for project {:?}", server_name, project_path);
                }
                Err(e) => {
                    warn!("Failed to start LSP server {}: {}", server_name, e);
                }
            }
        }

        Ok(started_servers)
    }

    /// Start specific LSP server
    pub async fn start_server(&self, server_name: &str, project_path: PathBuf) -> Result<()> {
        let _ = self.request_sender.send(LspManagerRequest::StartServer {
            server_name: server_name.to_string(),
            project_path,
        });
        Ok(())
    }

    /// Stop specific LSP server
    pub async fn stop_server(&self, server_name: &str) -> Result<()> {
        let _ = self.request_sender.send(LspManagerRequest::StopServer {
            server_name: server_name.to_string(),
        });
        Ok(())
    }

    /// Send LSP request to server
    pub async fn send_request(&self, server_name: &str, request: LspRequest) -> Result<()> {
        let _ = self.request_sender.send(LspManagerRequest::SendRequest {
            server_name: server_name.to_string(),
            request,
        });
        Ok(())
    }

    /// Get server information
    pub async fn get_server_info(&self, server_name: &str) -> Result<ServerInfo> {
        let servers = self.active_servers.read().await;
        if let Some(server_arc) = servers.get(server_name) {
            let server = server_arc.lock().await;
            Ok(ServerInfo {
                name: server.config.name.clone(),
                status: server.status.clone(),
                capabilities: server.capabilities.clone(),
                active_documents: server.active_documents.len(),
                performance_metrics: server.performance_metrics.clone(),
                health_status: server.health_status.clone(),
            })
        } else {
            Err(anyhow!("Server {} not found", server_name))
        }
    }

    /// List all active servers
    pub async fn list_active_servers(&self) -> Vec<ServerInfo> {
        let servers = self.active_servers.read().await;
        let mut server_infos = Vec::new();

        for (name, server_arc) in servers.iter() {
            if let Ok(server) = server_arc.try_lock() {
                server_infos.push(ServerInfo {
                    name: name.clone(),
                    status: server.status.clone(),
                    capabilities: server.capabilities.clone(),
                    active_documents: server.active_documents.len(),
                    performance_metrics: server.performance_metrics.clone(),
                    health_status: server.health_status.clone(),
                });
            }
        }

        server_infos
    }

    /// Hibernate unused servers to save resources
    pub async fn hibernate_unused_servers(&self, idle_threshold: Duration) -> Result<()> {
        let servers = self.active_servers.read().await;
        let now = Instant::now();

        for (name, server_arc) in servers.iter() {
            if let Ok(server) = server_arc.try_lock() {
                if server.active_documents.is_empty() 
                    && now.duration_since(server.last_activity) > idle_threshold 
                    && server.status == ServerStatus::Ready {
                    drop(server); // Release lock
                    let _ = self.request_sender.send(LspManagerRequest::HibernateServer {
                        server_name: name.clone(),
                    });
                }
            }
        }

        Ok(())
    }

    // Private helper methods

    fn create_default_server_configs() -> HashMap<String, LspServerConfig> {
        let mut configs = HashMap::new();

        // Rust Analyzer
        configs.insert("rust_analyzer".to_string(), LspServerConfig {
            name: "rust_analyzer".to_string(),
            command: "rust-analyzer".to_string(),
            args: vec![],
            languages: vec!["rust".to_string()],
            file_extensions: vec!["rs".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({
                "rust-analyzer": {
                    "cargo": {
                        "allFeatures": true
                    },
                    "checkOnSave": {
                        "command": "check"
                    },
                    "procMacro": {
                        "enable": true
                    }
                }
            }),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(10),
            memory_limit_mb: Some(500),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        // TypeScript Language Server
        configs.insert("tsserver".to_string(), LspServerConfig {
            name: "tsserver".to_string(),
            command: "typescript-language-server".to_string(),
            args: vec!["--stdio".to_string()],
            languages: vec!["typescript".to_string(), "javascript".to_string()],
            file_extensions: vec!["ts".to_string(), "tsx".to_string(), "js".to_string(), "jsx".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({
                "typescript": {
                    "preferences": {
                        "importModuleSpecifier": "relative"
                    },
                    "suggest": {
                        "autoImports": true
                    }
                }
            }),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(5),
            memory_limit_mb: Some(300),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        // Python Language Server
        configs.insert("pylsp".to_string(), LspServerConfig {
            name: "pylsp".to_string(),
            command: "pylsp".to_string(),
            args: vec![],
            languages: vec!["python".to_string()],
            file_extensions: vec!["py".to_string(), "pyi".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({
                "pylsp": {
                    "plugins": {
                        "pycodestyle": {
                            "enabled": true,
                            "maxLineLength": 88
                        },
                        "pylint": {
                            "enabled": true
                        },
                        "black": {
                            "enabled": true
                        }
                    }
                }
            }),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(8),
            memory_limit_mb: Some(200),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        // Java Language Server
        configs.insert("jdtls".to_string(), LspServerConfig {
            name: "jdtls".to_string(),
            command: "jdtls".to_string(),
            args: vec![],
            languages: vec!["java".to_string()],
            file_extensions: vec!["java".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(15),
            memory_limit_mb: Some(800),
            auto_restart: true,
            health_check_interval: Duration::from_secs(45),
        });

        // Add more server configurations for Kotlin, C++, Elixir, etc.
        configs.insert("kotlin_language_server".to_string(), LspServerConfig {
            name: "kotlin_language_server".to_string(),
            command: "kotlin-language-server".to_string(),
            args: vec![],
            languages: vec!["kotlin".to_string()],
            file_extensions: vec!["kt".to_string(), "kts".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(10),
            memory_limit_mb: Some(400),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        configs.insert("clangd".to_string(), LspServerConfig {
            name: "clangd".to_string(),
            command: "clangd".to_string(),
            args: vec!["--background-index".to_string()],
            languages: vec!["c".to_string(), "cpp".to_string()],
            file_extensions: vec!["c".to_string(), "cpp".to_string(), "cxx".to_string(), "cc".to_string(), "h".to_string(), "hpp".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(8),
            memory_limit_mb: Some(600),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        configs.insert("elixirls".to_string(), LspServerConfig {
            name: "elixirls".to_string(),
            command: "elixir-ls".to_string(),
            args: vec![],
            languages: vec!["elixir".to_string()],
            file_extensions: vec!["ex".to_string(), "exs".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(12),
            memory_limit_mb: Some(300),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        configs
    }

    fn build_language_mapping(configs: &HashMap<String, LspServerConfig>) -> HashMap<String, String> {
        let mut mapping = HashMap::new();

        for (server_name, config) in configs {
            for language in &config.languages {
                mapping.insert(language.clone(), server_name.clone());
            }
        }

        mapping
    }

    fn build_extension_mapping(configs: &HashMap<String, LspServerConfig>) -> HashMap<String, String> {
        let mut mapping = HashMap::new();

        for (server_name, config) in configs {
            for extension in &config.file_extensions {
                mapping.insert(extension.clone(), server_name.clone());
            }
        }

        mapping
    }

    async fn process_requests(
        mut request_receiver: mpsc::UnboundedReceiver<LspManagerRequest>,
        response_sender: mpsc::UnboundedSender<LspManagerResponse>,
        active_servers: Arc<RwLock<HashMap<String, Arc<Mutex<LspServerInstance>>>>>,
        server_configs: HashMap<String, LspServerConfig>,
    ) {
        while let Some(request) = request_receiver.recv().await {
            match request {
                LspManagerRequest::StartServer { server_name, project_path } => {
                    if let Some(config) = server_configs.get(&server_name) {
                        match Self::start_server_instance(config.clone(), project_path).await {
                            Ok(instance) => {
                                let capabilities = instance.capabilities.clone();
                                let server_arc = Arc::new(Mutex::new(instance));

                                {
                                    let mut servers = active_servers.write().await;
                                    servers.insert(server_name.clone(), server_arc);
                                }

                                let _ = response_sender.send(LspManagerResponse::ServerStarted {
                                    server_name: server_name.clone(),
                                    capabilities,
                                });
                            }
                            Err(e) => {
                                let _ = response_sender.send(LspManagerResponse::ServerError {
                                    server_name,
                                    error: e.to_string(),
                                });
                            }
                        }
                    }
                }

                LspManagerRequest::StopServer { server_name } => {
                    let mut servers = active_servers.write().await;
                    if let Some(server_arc) = servers.remove(&server_name) {
                        if let Ok(mut server) = server_arc.try_lock() {
                            let _ = Self::stop_server_instance(&mut server).await;
                        }
                        let _ = response_sender.send(LspManagerResponse::ServerStopped { server_name });
                    }
                }

                // Handle other request types...
                _ => {
                    debug!("Received LSP manager request: {:?}", request);
                }
            }
        }
    }

    async fn start_server_instance(config: LspServerConfig, project_path: PathBuf) -> Result<LspServerInstance> {
        info!("Starting LSP server: {}", config.name);

        let mut process = AsyncCommand::new(&config.command)
            .args(&config.args)
            .current_dir(&project_path)
            .stdin(std::process::Stdio::piped())
            .stdout(std::process::Stdio::piped())
            .stderr(std::process::Stdio::piped())
            .spawn()?;

        let stdin = process.stdin.take().ok_or_else(|| anyhow!("Failed to get stdin"))?;
        let stdout = process.stdout.take().ok_or_else(|| anyhow!("Failed to get stdout"))?;

        let stdin_writer = BufWriter::new(stdin);
        let stdout_reader = BufReader::new(stdout);

        let instance = LspServerInstance {
            id: Uuid::new_v4().to_string(),
            config,
            process: Some(process),
            status: ServerStatus::Starting,
            tcp_stream: None,
            stdin_writer: Some(stdin_writer),
            stdout_reader: Some(stdout_reader),
            capabilities: ServerCapabilities::default(),
            active_documents: HashMap::new(),
            performance_metrics: PerformanceMetrics::default(),
            health_status: HealthStatus::default(),
            last_activity: Instant::now(),
        };

        Ok(instance)
    }

    async fn stop_server_instance(server: &mut LspServerInstance) -> Result<()> {
        info!("Stopping LSP server: {}", server.config.name);

        server.status = ServerStatus::Stopping;

        if let Some(mut process) = server.process.take() {
            let _ = process.kill().await;
            let _ = timeout(Duration::from_secs(5), process.wait()).await;
        }

        server.status = ServerStatus::Stopped;
        Ok(())
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[tokio::test]
    async fn test_lsp_manager_creation() {
        let config = Config::default();
        let manager = LspManager::new(config);
        assert!(manager.is_ok());
    }

    #[tokio::test]
    async fn test_server_for_file_detection() {
        let config = Config::default();
        let manager = LspManager::new(config).unwrap();

        let rust_file = Path::new("test.rs");
        let server = manager.get_server_for_file(rust_file).await;
        assert_eq!(server, Some("rust_analyzer".to_string()));

        let python_file = Path::new("test.py");
        let server = manager.get_server_for_file(python_file).await;
        assert_eq!(server, Some("pylsp".to_string()));
    }

    #[test]
    fn test_server_config_creation() {
        let configs = LspManager::create_default_server_configs();
        assert!(configs.contains_key("rust_analyzer"));
        assert!(configs.contains_key("tsserver"));
        assert!(configs.contains_key("pylsp"));
        assert!(configs.contains_key("jdtls"));
    }

    #[tokio::test]
    async fn test_project_server_startup() {
        let config = Config::default();
        let manager = LspManager::new(config).unwrap();
        let temp_dir = TempDir::new().unwrap();

        // Create a mock Rust project
        let cargo_toml = temp_dir.path().join("Cargo.toml");
        tokio::fs::write(&cargo_toml, "[package]\nname = \"test\"").await.unwrap();

        let servers = manager.start_server_for_project(temp_dir.path()).await.unwrap();
        assert!(!servers.is_empty());
        // Note: This will fail in test environment without actual LSP servers installed
    }
}

toolUseResult.content:
//! LSP Manager - Centralized Language Server Protocol management
//! 
//! This module provides unified LSP server management including:
//! - Centralized server lifecycle management
//! - Intelligent server routing based on file types and projects
//! - Performance optimization with connection pooling
//! - Protocol adaptation for different editors
//! - Health monitoring and automatic recovery

use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::{Path, PathBuf};
use std::sync::Arc;
use tokio::io::{AsyncBufReadExt, AsyncWriteExt, BufReader, BufWriter};
use tokio::net::{TcpListener, TcpStream};
use tokio::process::{Child, Command as AsyncCommand};
use tokio::sync::{mpsc, Mutex, RwLock};
use tokio::time::{timeout, Duration, Instant};
use tracing::{debug, error, info, warn};
use uuid::Uuid;

use crate::config::Config;
use crate::utils::{FileSystemUtils, ProjectType, PerformanceMonitor};

/// LSP server specification and configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct LspServerConfig {
    pub name: String,
    pub command: String,
    pub args: Vec<String>,
    pub languages: Vec<String>,
    pub file_extensions: Vec<String>,
    pub initialization_options: serde_json::Value,
    pub settings: serde_json::Value,
    pub port: Option<u16>,
    pub use_stdio: bool,
    pub startup_timeout: Duration,
    pub memory_limit_mb: Option<u64>,
    pub auto_restart: bool,
    pub health_check_interval: Duration,
}

/// LSP server instance with runtime information
#[derive(Debug)]
pub struct LspServerInstance {
    pub id: String,
    pub config: LspServerConfig,
    pub process: Option<Child>,
    pub status: ServerStatus,
    pub tcp_stream: Option<TcpStream>,
    pub stdin_writer: Option<BufWriter<tokio::process::ChildStdin>>,
    pub stdout_reader: Option<BufReader<tokio::process::ChildStdout>>,
    pub capabilities: ServerCapabilities,
    pub active_documents: HashMap<PathBuf, DocumentInfo>,
    pub performance_metrics: PerformanceMetrics,
    pub health_status: HealthStatus,
    pub last_activity: Instant,
}

/// LSP server status
#[derive(Debug, Clone, PartialEq)]
pub enum ServerStatus {
    Starting,
    Initializing,
    Ready,
    Busy,
    Error(String),
    Stopping,
    Stopped,
    Hibernating,
}

/// Server capabilities as reported by initialize response
#[derive(Debug, Clone, Default)]
pub struct ServerCapabilities {
    pub completion_provider: bool,
    pub hover_provider: bool,
    pub signature_help_provider: bool,
    pub definition_provider: bool,
    pub references_provider: bool,
    pub document_highlight_provider: bool,
    pub document_symbol_provider: bool,
    pub workspace_symbol_provider: bool,
    pub code_action_provider: bool,
    pub code_lens_provider: bool,
    pub document_formatting_provider: bool,
    pub document_range_formatting_provider: bool,
    pub document_on_type_formatting_provider: bool,
    pub rename_provider: bool,
    pub document_link_provider: bool,
    pub execute_command_provider: bool,
    pub experimental: HashMap<String, serde_json::Value>,
}

/// Document information tracked by server
#[derive(Debug, Clone)]
pub struct DocumentInfo {
    pub uri: String,
    pub language_id: String,
    pub version: i32,
    pub content: String,
    pub diagnostics: Vec<Diagnostic>,
    pub last_modified: Instant,
}

/// LSP diagnostic information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Diagnostic {
    pub range: Range,
    pub severity: DiagnosticSeverity,
    pub code: Option<String>,
    pub source: Option<String>,
    pub message: String,
    pub related_information: Vec<DiagnosticRelatedInformation>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Range {
    pub start: Position,
    pub end: Position,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Position {
    pub line: u32,
    pub character: u32,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum DiagnosticSeverity {
    Error = 1,
    Warning = 2,
    Information = 3,
    Hint = 4,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DiagnosticRelatedInformation {
    pub location: Location,
    pub message: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Location {
    pub uri: String,
    pub range: Range,
}

/// Performance metrics for LSP servers
#[derive(Debug, Clone, Default)]
pub struct PerformanceMetrics {
    pub startup_time: Duration,
    pub average_response_time: Duration,
    pub total_requests: u64,
    pub failed_requests: u64,
    pub memory_usage_mb: f64,
    pub cpu_usage_percent: f64,
    pub cache_hit_rate: f64,
}

/// Health status monitoring
#[derive(Debug, Clone)]
pub struct HealthStatus {
    pub is_healthy: bool,
    pub last_health_check: Instant,
    pub consecutive_failures: u32,
    pub uptime: Duration,
    pub restart_count: u32,
}

impl Default for HealthStatus {
    fn default() -> Self {
        Self {
            is_healthy: true,
            last_health_check: Instant::now(),
            consecutive_failures: 0,
            uptime: Duration::from_secs(0),
            restart_count: 0,
        }
    }
}

/// LSP request/response types
#[derive(Debug, Serialize, Deserialize)]
pub struct LspRequest {
    pub id: String,
    pub method: String,
    pub params: serde_json::Value,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct LspResponse {
    pub id: String,
    pub result: Option<serde_json::Value>,
    pub error: Option<LspError>,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct LspNotification {
    pub method: String,
    pub params: serde_json::Value,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct LspError {
    pub code: i32,
    pub message: String,
    pub data: Option<serde_json::Value>,
}

/// Main LSP manager for centralized server management
pub struct LspManager {
    config: Config,
    server_configs: HashMap<String, LspServerConfig>,
    active_servers: Arc<RwLock<HashMap<String, Arc<Mutex<LspServerInstance>>>>>,
    language_to_server: HashMap<String, String>,
    extension_to_server: HashMap<String, String>,
    project_servers: HashMap<PathBuf, Vec<String>>,
    request_sender: mpsc::UnboundedSender<LspManagerRequest>,
    response_receiver: Arc<Mutex<mpsc::UnboundedReceiver<LspManagerResponse>>>,
    performance_monitor: Arc<Mutex<PerformanceMonitor>>,
}

/// Internal manager requests
#[derive(Debug)]
pub enum LspManagerRequest {
    StartServer { server_name: String, project_path: PathBuf },
    StopServer { server_name: String },
    RestartServer { server_name: String },
    SendRequest { server_name: String, request: LspRequest },
    SendNotification { server_name: String, notification: LspNotification },
    HealthCheck { server_name: String },
    GetServerInfo { server_name: String },
    HibernateServer { server_name: String },
    WakeupServer { server_name: String },
}

/// Manager responses
#[derive(Debug)]
pub enum LspManagerResponse {
    ServerStarted { server_name: String, capabilities: ServerCapabilities },
    ServerStopped { server_name: String },
    ServerError { server_name: String, error: String },
    Response { server_name: String, response: LspResponse },
    Notification { server_name: String, notification: LspNotification },
    HealthReport { server_name: String, status: HealthStatus },
    ServerInfo { server_name: String, info: ServerInfo },
}

#[derive(Debug, Clone)]
pub struct ServerInfo {
    pub name: String,
    pub status: ServerStatus,
    pub capabilities: ServerCapabilities,
    pub active_documents: usize,
    pub performance_metrics: PerformanceMetrics,
    pub health_status: HealthStatus,
}

impl LspManager {
    /// Create a new LSP manager with default server configurations
    pub fn new(config: Config) -> Result<Self> {
        info!("Initializing LSP manager");

        let (request_sender, request_receiver) = mpsc::unbounded_channel();
        let (response_sender, response_receiver) = mpsc::unbounded_channel();

        let server_configs = Self::create_default_server_configs();
        let language_to_server = Self::build_language_mapping(&server_configs);
        let extension_to_server = Self::build_extension_mapping(&server_configs);

        let manager = Self {
            config,
            server_configs,
            active_servers: Arc::new(RwLock::new(HashMap::new())),
            language_to_server,
            extension_to_server,
            project_servers: HashMap::new(),
            request_sender,
            response_receiver: Arc::new(Mutex::new(response_receiver)),
            performance_monitor: Arc::new(Mutex::new(PerformanceMonitor::new())),
        };

        // Start background task for request processing
        tokio::spawn(Self::process_requests(
            request_receiver,
            response_sender,
            manager.active_servers.clone(),
            manager.server_configs.clone(),
        ));

        Ok(manager)
    }

    /// Get server for a specific file path
    pub async fn get_server_for_file(&self, file_path: &Path) -> Option<String> {
        // First try extension mapping
        if let Some(extension) = file_path.extension().and_then(|e| e.to_str()) {
            if let Some(server_name) = self.extension_to_server.get(extension) {
                return Some(server_name.clone());
            }
        }

        // Try project-based detection
        if let Ok(project_info) = FileSystemUtils::detect_project_info(file_path.parent().unwrap_or(file_path)).await {
            match project_info.project_type {
                ProjectType::Rust => Some("rust_analyzer".to_string()),
                ProjectType::Python => Some("pylsp".to_string()),
                ProjectType::JavaScript | ProjectType::TypeScript => Some("tsserver".to_string()),
                ProjectType::Java => Some("jdtls".to_string()),
                ProjectType::Go => Some("gopls".to_string()),
                ProjectType::Cpp => Some("clangd".to_string()),
                _ => None,
            }
        } else {
            None
        }
    }

    /// Start LSP server for project
    pub async fn start_server_for_project(&self, project_path: &Path) -> Result<Vec<String>> {
        let project_info = FileSystemUtils::detect_project_info(project_path).await?;
        let mut started_servers = Vec::new();

        // Determine which servers to start based on project type
        let server_names = match project_info.project_type {
            ProjectType::Rust => vec!["rust_analyzer"],
            ProjectType::Python => vec!["pylsp"],
            ProjectType::JavaScript => vec!["tsserver"],
            ProjectType::TypeScript => vec!["tsserver"],
            ProjectType::Java => vec!["jdtls"],
            ProjectType::Go => vec!["gopls"],
            ProjectType::Cpp => vec!["clangd"],
            ProjectType::Mixed => vec!["rust_analyzer", "tsserver", "pylsp"], // Common combination
            _ => vec![],
        };

        for server_name in server_names {
            match self.start_server(server_name, project_path.to_path_buf()).await {
                Ok(_) => {
                    started_servers.push(server_name.to_string());
                    info!("Started LSP server {} for project {:?}", server_name, project_path);
                }
                Err(e) => {
                    warn!("Failed to start LSP server {}: {}", server_name, e);
                }
            }
        }

        Ok(started_servers)
    }

    /// Start specific LSP server
    pub async fn start_server(&self, server_name: &str, project_path: PathBuf) -> Result<()> {
        let _ = self.request_sender.send(LspManagerRequest::StartServer {
            server_name: server_name.to_string(),
            project_path,
        });
        Ok(())
    }

    /// Stop specific LSP server
    pub async fn stop_server(&self, server_name: &str) -> Result<()> {
        let _ = self.request_sender.send(LspManagerRequest::StopServer {
            server_name: server_name.to_string(),
        });
        Ok(())
    }

    /// Send LSP request to server
    pub async fn send_request(&self, server_name: &str, request: LspRequest) -> Result<()> {
        let _ = self.request_sender.send(LspManagerRequest::SendRequest {
            server_name: server_name.to_string(),
            request,
        });
        Ok(())
    }

    /// Get server information
    pub async fn get_server_info(&self, server_name: &str) -> Result<ServerInfo> {
        let servers = self.active_servers.read().await;
        if let Some(server_arc) = servers.get(server_name) {
            let server = server_arc.lock().await;
            Ok(ServerInfo {
                name: server.config.name.clone(),
                status: server.status.clone(),
                capabilities: server.capabilities.clone(),
                active_documents: server.active_documents.len(),
                performance_metrics: server.performance_metrics.clone(),
                health_status: server.health_status.clone(),
            })
        } else {
            Err(anyhow!("Server {} not found", server_name))
        }
    }

    /// List all active servers
    pub async fn list_active_servers(&self) -> Vec<ServerInfo> {
        let servers = self.active_servers.read().await;
        let mut server_infos = Vec::new();

        for (name, server_arc) in servers.iter() {
            if let Ok(server) = server_arc.try_lock() {
                server_infos.push(ServerInfo {
                    name: name.clone(),
                    status: server.status.clone(),
                    capabilities: server.capabilities.clone(),
                    active_documents: server.active_documents.len(),
                    performance_metrics: server.performance_metrics.clone(),
                    health_status: server.health_status.clone(),
                });
            }
        }

        server_infos
    }

    /// Hibernate unused servers to save resources
    pub async fn hibernate_unused_servers(&self, idle_threshold: Duration) -> Result<()> {
        let servers = self.active_servers.read().await;
        let now = Instant::now();

        for (name, server_arc) in servers.iter() {
            if let Ok(server) = server_arc.try_lock() {
                if server.active_documents.is_empty() 
                    && now.duration_since(server.last_activity) > idle_threshold 
                    && server.status == ServerStatus::Ready {
                    drop(server); // Release lock
                    let _ = self.request_sender.send(LspManagerRequest::HibernateServer {
                        server_name: name.clone(),
                    });
                }
            }
        }

        Ok(())
    }

    // Private helper methods

    fn create_default_server_configs() -> HashMap<String, LspServerConfig> {
        let mut configs = HashMap::new();

        // Rust Analyzer
        configs.insert("rust_analyzer".to_string(), LspServerConfig {
            name: "rust_analyzer".to_string(),
            command: "rust-analyzer".to_string(),
            args: vec![],
            languages: vec!["rust".to_string()],
            file_extensions: vec!["rs".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({
                "rust-analyzer": {
                    "cargo": {
                        "allFeatures": true
                    },
                    "checkOnSave": {
                        "command": "check"
                    },
                    "procMacro": {
                        "enable": true
                    }
                }
            }),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(10),
            memory_limit_mb: Some(500),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        // TypeScript Language Server
        configs.insert("tsserver".to_string(), LspServerConfig {
            name: "tsserver".to_string(),
            command: "typescript-language-server".to_string(),
            args: vec!["--stdio".to_string()],
            languages: vec!["typescript".to_string(), "javascript".to_string()],
            file_extensions: vec!["ts".to_string(), "tsx".to_string(), "js".to_string(), "jsx".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({
                "typescript": {
                    "preferences": {
                        "importModuleSpecifier": "relative"
                    },
                    "suggest": {
                        "autoImports": true
                    }
                }
            }),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(5),
            memory_limit_mb: Some(300),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        // Python Language Server
        configs.insert("pylsp".to_string(), LspServerConfig {
            name: "pylsp".to_string(),
            command: "pylsp".to_string(),
            args: vec![],
            languages: vec!["python".to_string()],
            file_extensions: vec!["py".to_string(), "pyi".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({
                "pylsp": {
                    "plugins": {
                        "pycodestyle": {
                            "enabled": true,
                            "maxLineLength": 88
                        },
                        "pylint": {
                            "enabled": true
                        },
                        "black": {
                            "enabled": true
                        }
                    }
                }
            }),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(8),
            memory_limit_mb: Some(200),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        // Java Language Server
        configs.insert("jdtls".to_string(), LspServerConfig {
            name: "jdtls".to_string(),
            command: "jdtls".to_string(),
            args: vec![],
            languages: vec!["java".to_string()],
            file_extensions: vec!["java".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(15),
            memory_limit_mb: Some(800),
            auto_restart: true,
            health_check_interval: Duration::from_secs(45),
        });

        // Add more server configurations for Kotlin, C++, Elixir, etc.
        configs.insert("kotlin_language_server".to_string(), LspServerConfig {
            name: "kotlin_language_server".to_string(),
            command: "kotlin-language-server".to_string(),
            args: vec![],
            languages: vec!["kotlin".to_string()],
            file_extensions: vec!["kt".to_string(), "kts".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(10),
            memory_limit_mb: Some(400),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        configs.insert("clangd".to_string(), LspServerConfig {
            name: "clangd".to_string(),
            command: "clangd".to_string(),
            args: vec!["--background-index".to_string()],
            languages: vec!["c".to_string(), "cpp".to_string()],
            file_extensions: vec!["c".to_string(), "cpp".to_string(), "cxx".to_string(), "cc".to_string(), "h".to_string(), "hpp".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(8),
            memory_limit_mb: Some(600),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        configs.insert("elixirls".to_string(), LspServerConfig {
            name: "elixirls".to_string(),
            command: "elixir-ls".to_string(),
            args: vec![],
            languages: vec!["elixir".to_string()],
            file_extensions: vec!["ex".to_string(), "exs".to_string()],
            initialization_options: serde_json::json!({}),
            settings: serde_json::json!({}),
            port: None,
            use_stdio: true,
            startup_timeout: Duration::from_secs(12),
            memory_limit_mb: Some(300),
            auto_restart: true,
            health_check_interval: Duration::from_secs(30),
        });

        configs
    }

    fn build_language_mapping(configs: &HashMap<String, LspServerConfig>) -> HashMap<String, String> {
        let mut mapping = HashMap::new();

        for (server_name, config) in configs {
            for language in &config.languages {
                mapping.insert(language.clone(), server_name.clone());
            }
        }

        mapping
    }

    fn build_extension_mapping(configs: &HashMap<String, LspServerConfig>) -> HashMap<String, String> {
        let mut mapping = HashMap::new();

        for (server_name, config) in configs {
            for extension in &config.file_extensions {
                mapping.insert(extension.clone(), server_name.clone());
            }
        }

        mapping
    }

    async fn process_requests(
        mut request_receiver: mpsc::UnboundedReceiver<LspManagerRequest>,
        response_sender: mpsc::UnboundedSender<LspManagerResponse>,
        active_servers: Arc<RwLock<HashMap<String, Arc<Mutex<LspServerInstance>>>>>,
        server_configs: HashMap<String, LspServerConfig>,
    ) {
        while let Some(request) = request_receiver.recv().await {
            match request {
                LspManagerRequest::StartServer { server_name, project_path } => {
                    if let Some(config) = server_configs.get(&server_name) {
                        match Self::start_server_instance(config.clone(), project_path).await {
                            Ok(instance) => {
                                let capabilities = instance.capabilities.clone();
                                let server_arc = Arc::new(Mutex::new(instance));

                                {
                                    let mut servers = active_servers.write().await;
                                    servers.insert(server_name.clone(), server_arc);
                                }

                                let _ = response_sender.send(LspManagerResponse::ServerStarted {
                                    server_name: server_name.clone(),
                                    capabilities,
                                });
                            }
                            Err(e) => {
                                let _ = response_sender.send(LspManagerResponse::ServerError {
                                    server_name,
                                    error: e.to_string(),
                                });
                            }
                        }
                    }
                }

                LspManagerRequest::StopServer { server_name } => {
                    let mut servers = active_servers.write().await;
                    if let Some(server_arc) = servers.remove(&server_name) {
                        if let Ok(mut server) = server_arc.try_lock() {
                            let _ = Self::stop_server_instance(&mut server).await;
                        }
                        let _ = response_sender.send(LspManagerResponse::ServerStopped { server_name });
                    }
                }

                // Handle other request types...
                _ => {
                    debug!("Received LSP manager request: {:?}", request);
                }
            }
        }
    }

    async fn start_server_instance(config: LspServerConfig, project_path: PathBuf) -> Result<LspServerInstance> {
        info!("Starting LSP server: {}", config.name);

        let mut process = AsyncCommand::new(&config.command)
            .args(&config.args)
            .current_dir(&project_path)
            .stdin(std::process::Stdio::piped())
            .stdout(std::process::Stdio::piped())
            .stderr(std::process::Stdio::piped())
            .spawn()?;

        let stdin = process.stdin.take().ok_or_else(|| anyhow!("Failed to get stdin"))?;
        let stdout = process.stdout.take().ok_or_else(|| anyhow!("Failed to get stdout"))?;

        let stdin_writer = BufWriter::new(stdin);
        let stdout_reader = BufReader::new(stdout);

        let instance = LspServerInstance {
            id: Uuid::new_v4().to_string(),
            config,
            process: Some(process),
            status: ServerStatus::Starting,
            tcp_stream: None,
            stdin_writer: Some(stdin_writer),
            stdout_reader: Some(stdout_reader),
            capabilities: ServerCapabilities::default(),
            active_documents: HashMap::new(),
            performance_metrics: PerformanceMetrics::default(),
            health_status: HealthStatus::default(),
            last_activity: Instant::now(),
        };

        Ok(instance)
    }

    async fn stop_server_instance(server: &mut LspServerInstance) -> Result<()> {
        info!("Stopping LSP server: {}", server.config.name);

        server.status = ServerStatus::Stopping;

        if let Some(mut process) = server.process.take() {
            let _ = process.kill().await;
            let _ = timeout(Duration::from_secs(5), process.wait()).await;
        }

        server.status = ServerStatus::Stopped;
        Ok(())
    }
}

