-- Auto commands for optimization
local autocmd = vim.api.nvim_create_autocmd
local augroup = vim.api.nvim_create_augroup

-- Highlight yanked text
augroup("highlight_yank", { clear = true })
autocmd("TextYankPost", {
  group = "highlight_yank",
  callback = function()
    vim.highlight.on_yank({ higroup = "IncSearch", timeout = 200 })
  end,
})

-- Remove trailing whitespace on save
augroup("trim_whitespace", { clear = true })
autocmd("BufWritePre", {
  group = "trim_whitespace",
  pattern = "*",
  command = "%s/\\s\\+$//e",
})

-- Auto format on save for specific filetypes
augroup("format_on_save", { clear = true })
autocmd("BufWritePre", {
  group = "format_on_save",
  pattern = { "*.rs", "*.lua", "*.py", "*.ts", "*.js", "*.java" },
  callback = function()
    vim.lsp.buf.format({ async = false })
  end,
})

-- Restore cursor position
augroup("restore_cursor", { clear = true })
autocmd("BufReadPost", {
  group = "restore_cursor",
  callback = function()
    local mark = vim.api.nvim_buf_get_mark(0, '"')
    if mark[1] > 1 and mark[1] <= vim.api.nvim_buf_line_count(0) then
      vim.api.nvim_win_set_cursor(0, mark)
    end
  end,
})

-- Disable diagnostics in insert mode for performance
augroup("lsp_diagnostics", { clear = true })
autocmd("InsertEnter", {
  group = "lsp_diagnostics",
  callback = function()
    vim.diagnostic.disable(0)
  end,
