// Performance monitoring interval
let mut performance_interval = tokio::time::interval(std::time::Duration::from_secs(30));

// Main event loop
loop {
    tokio::select! {
        // Handle AI requests
        result = self.ai_client.handle_request() => {
            if let Err(e) = result {
                error!("AI request failed: {}", e);
            }
        }

        // Handle editor events
        result = self.editor_bridge.handle_event() => {
            if let Err(e) = result {
                error!("Editor event failed: {}", e);
            }
        }

        // Handle terminal events
        result = self.terminal_mux.handle_event() => {
            if let Err(e) = result {
                error!("Terminal event failed: {}", e);
            }
        }

        // Performance monitoring and optimization
        _ = performance_interval.tick() => {
            if self.performance_manager.is_performance_degraded() {
                warn!("Performance degradation detected, running optimizations");
                if let Err(e) = self.performance_manager.optimize_performance().await {
                    error!("Performance optimization failed: {}", e);
                }
            }

            // Log performance recommendations
            let recommendations = self.performance_manager.get_recommendations();
            if !recommendations.is_empty() {
                info!("Performance recommendations: {:?}", recommendations);
            }
        }

        // Graceful shutdown signal
        _ = tokio::signal::ctrl_c() => {
            info!("Received shutdown signal");
            break;
        }
    }

