use anyhow::Result;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::PathBuf;
use tokio::sync::{broadcast, mpsc};
use tracing::{debug, info, warn};

/// UI synchronization manager for maintaining consistent experience across editors
pub struct UiSyncManager {
    state: UiState,
    state_sender: broadcast::Sender<UiStateChange>,
    command_receiver: mpsc::UnboundedReceiver<SyncCommand>,
    editor_connections: HashMap<String, EditorConnection>,
}

/// Current UI state shared across editors
#[derive(Debug, Clone, Serialize, Deserialize, Default)]
pub struct UiState {
    pub active_file: Option<PathBuf>,
    pub open_buffers: Vec<PathBuf>,
    pub current_project: Option<PathBuf>,
    pub theme: String,
    pub font_size: u32,
    pub split_layout: SplitLayout,
    pub terminal_visible: bool,
    pub sidebar_visible: bool,
    pub sidebar_width: u32,
    pub cursor_position: CursorPosition,
    pub search_results: Vec<SearchResult>,
    pub diagnostics: Vec<Diagnostic>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CursorPosition {
    pub line: u32,
    pub column: u32,
    pub file: Option<PathBuf>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SearchResult {
    pub file: PathBuf,
    pub line: u32,
    pub column: u32,
    pub text: String,
    pub query: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Diagnostic {
    pub file: PathBuf,
    pub line: u32,
    pub column: u32,
    pub severity: DiagnosticSeverity,
    pub message: String,
    pub source: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum DiagnosticSeverity {
    Error,
    Warning,
    Info,
    Hint,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SplitLayout {
    Single,
    VerticalSplit,
    HorizontalSplit,
    Grid(u32, u32), // rows, columns
}

/// Changes to UI state that need to be synchronized
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum UiStateChange {
    FileOpened(PathBuf),
    FileClosed(PathBuf),
    FileActivated(PathBuf),
    ProjectChanged(PathBuf),
    ThemeChanged(String),
    FontSizeChanged(u32),
    LayoutChanged(SplitLayout),
    TerminalToggled(bool),
    SidebarToggled(bool),
    CursorMoved(CursorPosition),
    SearchCompleted(Vec<SearchResult>),
    DiagnosticsUpdated(Vec<Diagnostic>),
}

/// Commands for synchronization
#[derive(Debug, Clone)]
pub enum SyncCommand {
    RegisterEditor(String, EditorConnection),
    UnregisterEditor(String),
    UpdateState(UiStateChange),
    RequestFullSync(String),
    SyncTheme(String, String),
    SyncLayout(String, SplitLayout),
}

/// Connection to an editor instance
#[derive(Debug, Clone)]
pub struct EditorConnection {
    pub editor_type: EditorType,
    pub instance_id: String,
    pub rpc_port: Option<u16>,
    pub command_sender: mpsc::UnboundedSender<EditorCommand>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum EditorType {
    Neovim,
    Emacs,
}

/// Commands sent to editor instances
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum EditorCommand {
    OpenFile(PathBuf),
    CloseFile(PathBuf),
    ActivateFile(PathBuf),
    SetTheme(String),
    SetFontSize(u32),
    SetLayout(SplitLayout),
    ShowTerminal(bool),
    ShowSidebar(bool, u32), // visible, width
    MoveCursor(u32, u32),   // line, column
    ApplyDiagnostics(Vec<Diagnostic>),
    HighlightSearchResults(Vec<SearchResult>),
}

impl Default for CursorPosition {
    fn default() -> Self {
        Self {
            line: 1,
            column: 1,
            file: None,
        }
    }
}

impl Default for SplitLayout {
    fn default() -> Self {
        Self::Single
    }
}

impl UiSyncManager {
    pub fn new() -> (Self, mpsc::UnboundedSender<SyncCommand>) {
        let (state_sender, _) = broadcast::channel(1000);
        let (command_sender, command_receiver) = mpsc::unbounded_channel();

        let manager = Self {
            state: UiState {
                theme: "catppuccin-mocha".to_string(),
                font_size: 14,
                sidebar_width: 300,
                ..Default::default()
            },
            state_sender,
            command_receiver,
            editor_connections: HashMap::new(),
        };

        (manager, command_sender)
    }

    /// Start the UI synchronization manager
    pub async fn run(&mut self) -> Result<()> {
        info!("Starting UI synchronization manager");

        while let Some(command) = self.command_receiver.recv().await {
            if let Err(e) = self.handle_command(command).await {
                warn!("Failed to handle sync command: {}", e);
            }
        }

        Ok(())
    }

    async fn handle_command(&mut self, command: SyncCommand) -> Result<()> {
        match command {
            SyncCommand::RegisterEditor(id, connection) => {
                info!("Registering editor: {} ({:?})", id, connection.editor_type);
                self.editor_connections.insert(id.clone(), connection);

                // Send initial state to new editor
                self.sync_full_state_to_editor(&id).await?;
            }

            SyncCommand::UnregisterEditor(id) => {
                info!("Unregistering editor: {}", id);
                self.editor_connections.remove(&id);
            }

            SyncCommand::UpdateState(change) => {
                self.apply_state_change(change.clone()).await?;

                // Broadcast change to all connected editors
                if let Err(e) = self.state_sender.send(change) {
                    warn!("Failed to broadcast state change: {}", e);
                }
            }

            SyncCommand::RequestFullSync(id) => {
                debug!("Full sync requested for editor: {}", id);
                self.sync_full_state_to_editor(&id).await?;
            }

            SyncCommand::SyncTheme(editor_id, theme) => {
                self.state.theme = theme.clone();

                // Sync theme to all other editors
                for (id, connection) in &self.editor_connections {
                    if id != &editor_id {
                        let _ = connection
                            .command_sender
                            .send(EditorCommand::SetTheme(theme.clone()));
                    }
                }
            }

            SyncCommand::SyncLayout(editor_id, layout) => {
                self.state.split_layout = layout.clone();

                // Sync layout to all other editors
                for (id, connection) in &self.editor_connections {
                    if id != &editor_id {
                        let _ = connection
                            .command_sender
                            .send(EditorCommand::SetLayout(layout.clone()));
                    }
                }
            }
        }

        Ok(())
    }

    async fn apply_state_change(&mut self, change: UiStateChange) -> Result<()> {
        match change {
            UiStateChange::FileOpened(path) => {
                if !self.state.open_buffers.contains(&path) {
                    self.state.open_buffers.push(path);
                }
            }

            UiStateChange::FileClosed(path) => {
                self.state.open_buffers.retain(|p| p != &path);
            }

            UiStateChange::FileActivated(path) => {
                self.state.active_file = Some(path);
            }

            UiStateChange::ProjectChanged(path) => {
                self.state.current_project = Some(path);
            }

            UiStateChange::ThemeChanged(theme) => {
                self.state.theme = theme;
            }

            UiStateChange::FontSizeChanged(size) => {
                self.state.font_size = size;
            }

            UiStateChange::LayoutChanged(layout) => {
                self.state.split_layout = layout;
            }

            UiStateChange::TerminalToggled(visible) => {
                self.state.terminal_visible = visible;
            }

            UiStateChange::SidebarToggled(visible) => {
                self.state.sidebar_visible = visible;
            }

            UiStateChange::CursorMoved(pos) => {
                self.state.cursor_position = pos;
            }

            UiStateChange::SearchCompleted(results) => {
                self.state.search_results = results;
            }

            UiStateChange::DiagnosticsUpdated(diagnostics) => {
                self.state.diagnostics = diagnostics;
            }
        }

        Ok(())
    }

    async fn sync_full_state_to_editor(&self, editor_id: &str) -> Result<()> {
        if let Some(connection) = self.editor_connections.get(editor_id) {
            let commands = vec![
                EditorCommand::SetTheme(self.state.theme.clone()),
                EditorCommand::SetFontSize(self.state.font_size),
                EditorCommand::SetLayout(self.state.split_layout.clone()),
                EditorCommand::ShowTerminal(self.state.terminal_visible),
                EditorCommand::ShowSidebar(self.state.sidebar_visible, self.state.sidebar_width),
                EditorCommand::ApplyDiagnostics(self.state.diagnostics.clone()),
                EditorCommand::HighlightSearchResults(self.state.search_results.clone()),
            ];

            for command in commands {
                if let Err(e) = connection.command_sender.send(command) {
                    warn!("Failed to send command to editor {}: {}", editor_id, e);
                }
            }

            // Open all currently open buffers
            for buffer in &self.state.open_buffers {
                let _ = connection.command_sender.send(EditorCommand::OpenFile(buffer.clone()));
            }

            // Activate the currently active file
            if let Some(active_file) = &self.state.active_file {
                let _ = connection
                    .command_sender
                    .send(EditorCommand::ActivateFile(active_file.clone()));
            }
        }

        Ok(())
    }

    /// Get current UI state
    pub fn get_state(&self) -> &UiState {
        &self.state
    }

    /// Get a subscriber for state changes
    pub fn subscribe(&self) -> broadcast::Receiver<UiStateChange> {
        self.state_sender.subscribe()
    }

    /// Generate theme configuration for both editors
    pub fn generate_theme_configs(&self) -> Result<(String, String)> {
        let nvim_theme = self.generate_nvim_theme_config();
        let emacs_theme = self.generate_emacs_theme_config();
        Ok((nvim_theme, emacs_theme))
    }

    fn generate_nvim_theme_config(&self) -> String {
        format!(
            r#"-- Auto-generated theme configuration
-- Theme: {}
-- Font size: {}

vim.cmd('colorscheme {}')
vim.opt.guifont = "JetBrains Mono:h{}"

-- Unified UI settings
vim.opt.number = true
vim.opt.relativenumber = true
vim.opt.signcolumn = "yes"
vim.opt.cursorline = true
vim.opt.termguicolors = true

-- Split layout preferences
vim.opt.splitbelow = true
vim.opt.splitright = true
"#,
            self.state.theme,
            self.state.font_size,
            self.state.theme.replace("-", "_"),
            self.state.font_size
        )
    }

    fn generate_emacs_theme_config(&self) -> String {
        format!(
            r#";;; Auto-generated theme configuration
;;; Theme: {}
;;; Font size: {}

(load-theme '{} t)

(set-face-attribute 'default nil 
                    :family "JetBrains Mono" 
                    :height {})

;; Unified UI settings
(global-display-line-numbers-mode 1)
(setq display-line-numbers-type 'relative)
(global-hl-line-mode 1)

;; Split layout preferences
(setq split-height-threshold nil)
(setq split-width-threshold 0)
"#,
            self.state.theme,
            self.state.font_size,
            self.state.theme.replace("-", "_"),
            self.state.font_size * 10 // Emacs uses different scale
        )
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use std::path::PathBuf;

    #[tokio::test]
    async fn test_ui_sync_manager_creation() {
        let (manager, _sender) = UiSyncManager::new();
        assert_eq!(manager.state.theme, "catppuccin-mocha");
        assert_eq!(manager.state.font_size, 14);
        assert_eq!(manager.editor_connections.len(), 0);
    }

    #[tokio::test]
    async fn test_file_operations() {
        let (mut manager, sender) = UiSyncManager::new();
        let test_file = PathBuf::from("/tmp/test.rs");

        // Test file opening
        sender
            .send(SyncCommand::UpdateState(UiStateChange::FileOpened(
                test_file.clone(),
            )))
            .unwrap();

        tokio::time::sleep(tokio::time::Duration::from_millis(10)).await;

        assert!(manager.state.open_buffers.contains(&test_file));
    }

    #[test]
    fn test_theme_config_generation() {
        let (manager, _) = UiSyncManager::new();
        let (nvim_config, emacs_config) = manager.generate_theme_configs().unwrap();

        assert!(nvim_config.contains("catppuccin_mocha"));
        assert!(nvim_config.contains("JetBrains Mono:h14"));
        assert!(emacs_config.contains("catppuccin_mocha"));
        assert!(emacs_config.contains(":height 140"));
    }
}

