//! Vimmacs WebAssembly Core
//! 
//! This module provides the core Vimmacs functionality compiled for WebAssembly,
//! enabling secure execution in sandboxed environments with WASI support.

#![deny(unsafe_code)]
#![warn(clippy::all, clippy::pedantic)]

use anyhow::Result;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use tracing::{debug, error, info, warn};
use wasm_bindgen::prelude::*;

pub mod config;
pub mod editor;
pub mod security;
pub mod lsp;
pub mod utils;

// Re-export for WebAssembly bindings
pub use config::WasmConfig;
pub use editor::WasmEditorBridge;
pub use security::WasmSecurityManager;

/// WebAssembly-compatible Vimmacs core instance
#[wasm_bindgen]
pub struct VimmacsWasm {
    config: WasmConfig,
    editor_bridge: WasmEditorBridge,
    security_manager: WasmSecurityManager,
    session_id: String,
    initialized: bool,
}

/// WebAssembly-compatible command structure
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WasmCommand {
    pub id: String,
    pub command_type: String,
    pub payload: serde_json::Value,
    pub timestamp: i64,
    pub session_id: String,
}

/// WebAssembly-compatible response structure
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WasmResponse {
    pub id: String,
    pub command_id: String,
    pub status: ResponseStatus,
    pub data: serde_json::Value,
    pub timestamp: i64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ResponseStatus {
    Success,
    Error(String),
    Pending,
}

/// WebAssembly-compatible file operation
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WasmFileOperation {
    pub operation_type: FileOperationType,
    pub path: String,
    pub content: Option<String>,
    pub metadata: HashMap<String, String>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum FileOperationType {
    Read,
    Write,
    Create,
    Delete,
    List,
    Watch,
}

#[wasm_bindgen]
impl VimmacsWasm {
    /// Create a new WebAssembly Vimmacs instance
    #[wasm_bindgen(constructor)]
    pub fn new() -> Result<VimmacsWasm, JsValue> {
        // Initialize logging for WebAssembly
        #[cfg(feature = "browser")]
        {
            tracing_wasm::set_as_global_default();
        }

        info!("Initializing Vimmacs WebAssembly core");

        let session_id = uuid::Uuid::new_v4().to_string();
        let config = WasmConfig::default();
        let security_manager = WasmSecurityManager::new(&config)
            .map_err(|e| JsValue::from_str(&format!("Security manager init failed: {}", e)))?;
        let editor_bridge = WasmEditorBridge::new(&config)
            .map_err(|e| JsValue::from_str(&format!("Editor bridge init failed: {}", e)))?;

        Ok(Self {
            config,
            editor_bridge,
            security_manager,
            session_id,
            initialized: false,
        })
    }

    /// Initialize the WebAssembly instance with configuration
    #[wasm_bindgen]
    pub async fn initialize(&mut self, config_json: &str) -> Result<(), JsValue> {
        info!("Initializing Vimmacs WASM with configuration");

        // Parse configuration
        let config: WasmConfig = serde_json::from_str(config_json)
            .map_err(|e| JsValue::from_str(&format!("Config parse error: {}", e)))?;

        // Validate configuration
        self.security_manager.validate_config(&config)
            .map_err(|e| JsValue::from_str(&format!("Config validation failed: {}", e)))?;

        self.config = config;

        // Initialize components
        self.editor_bridge.initialize(&self.config).await
            .map_err(|e| JsValue::from_str(&format!("Editor initialization failed: {}", e)))?;

        self.initialized = true;
        info!("Vimmacs WASM initialized successfully");

        Ok(())
    }

    /// Execute a command in the WebAssembly environment
    #[wasm_bindgen]
    pub async fn execute_command(&self, command_json: &str) -> Result<String, JsValue> {
        if !self.initialized {
            return Err(JsValue::from_str("Vimmacs WASM not initialized"));
        }

        // Parse command
        let command: WasmCommand = serde_json::from_str(command_json)
            .map_err(|e| JsValue::from_str(&format!("Command parse error: {}", e)))?;

        // Validate command security
        self.security_manager.validate_command(&command)
            .map_err(|e| JsValue::from_str(&format!("Command validation failed: {}", e)))?;

        debug!("Executing command: {} ({})", command.command_type, command.id);

        let response = match command.command_type.as_str() {
            "file_operation" => self.handle_file_operation(command).await,
            "editor_command" => self.handle_editor_command(command).await,
            "lsp_request" => self.handle_lsp_request(command).await,
            "system_query" => self.handle_system_query(command).await,
            _ => {
                warn!("Unknown command type: {}", command.command_type);
                WasmResponse {
                    id: uuid::Uuid::new_v4().to_string(),
                    command_id: command.id,
                    status: ResponseStatus::Error("Unknown command type".to_string()),
                    data: serde_json::Value::Null,
                    timestamp: chrono::Utc::now().timestamp(),
                }
            }
        };

        serde_json::to_string(&response)
            .map_err(|e| JsValue::from_str(&format!("Response serialization error: {}", e)))
    }

    /// Get the current session information
    #[wasm_bindgen(getter)]
    pub fn session_id(&self) -> String {
        self.session_id.clone()
    }

    /// Check if the instance is initialized
    #[wasm_bindgen(getter)]
    pub fn initialized(&self) -> bool {
        self.initialized
    }

    /// Get configuration as JSON string
    #[wasm_bindgen]
    pub fn get_config(&self) -> Result<String, JsValue> {
        serde_json::to_string(&self.config)
            .map_err(|e| JsValue::from_str(&format!("Config serialization error: {}", e)))
    }

    /// Shutdown the WebAssembly instance
    #[wasm_bindgen]
    pub async fn shutdown(&mut self) -> Result<(), JsValue> {
        info!("Shutting down Vimmacs WASM");

        self.editor_bridge.shutdown().await
            .map_err(|e| JsValue::from_str(&format!("Editor shutdown failed: {}", e)))?;

        self.initialized = false;
        info!("Vimmacs WASM shutdown complete");

        Ok(())
    }
}

impl VimmacsWasm {
    /// Handle file operations
    async fn handle_file_operation(&self, command: WasmCommand) -> WasmResponse {
        let file_op: Result<WasmFileOperation, _> = serde_json::from_value(command.payload.clone());

        match file_op {
            Ok(operation) => {
                debug!("Handling file operation: {:?}", operation.operation_type);

                let result = match operation.operation_type {
                    FileOperationType::Read => self.read_file(&operation.path).await,
                    FileOperationType::Write => {
                        if let Some(content) = &operation.content {
                            self.write_file(&operation.path, content).await
                        } else {
                            Err(anyhow::anyhow!("Write operation requires content"))
                        }
                    }
                    FileOperationType::Create => self.create_file(&operation.path).await,
                    FileOperationType::Delete => self.delete_file(&operation.path).await,
                    FileOperationType::List => self.list_files(&operation.path).await,
                    FileOperationType::Watch => self.watch_file(&operation.path).await,
                };

                match result {
                    Ok(data) => WasmResponse {
                        id: uuid::Uuid::new_v4().to_string(),
                        command_id: command.id,
                        status: ResponseStatus::Success,
                        data,
                        timestamp: chrono::Utc::now().timestamp(),
                    },
                    Err(e) => WasmResponse {
                        id: uuid::Uuid::new_v4().to_string(),
                        command_id: command.id,
                        status: ResponseStatus::Error(e.to_string()),
                        data: serde_json::Value::Null,
                        timestamp: chrono::Utc::now().timestamp(),
                    },
                }
            }
            Err(e) => WasmResponse {
                id: uuid::Uuid::new_v4().to_string(),
                command_id: command.id,
                status: ResponseStatus::Error(format!("Invalid file operation: {}", e)),
                data: serde_json::Value::Null,
                timestamp: chrono::Utc::now().timestamp(),
            },
        }
    }

    /// Handle editor commands
    async fn handle_editor_command(&self, command: WasmCommand) -> WasmResponse {
        debug!("Handling editor command");

        match self.editor_bridge.execute_command(command.payload).await {
            Ok(result) => WasmResponse {
                id: uuid::Uuid::new_v4().to_string(),
                command_id: command.id,
                status: ResponseStatus::Success,
                data: result,
                timestamp: chrono::Utc::now().timestamp(),
            },
            Err(e) => WasmResponse {
                id: uuid::Uuid::new_v4().to_string(),
                command_id: command.id,
                status: ResponseStatus::Error(e.to_string()),
                data: serde_json::Value::Null,
                timestamp: chrono::Utc::now().timestamp(),
            },
        }
    }

    /// Handle LSP requests
    async fn handle_lsp_request(&self, command: WasmCommand) -> WasmResponse {
        debug!("Handling LSP request");

        // LSP handling would be implemented here
        WasmResponse {
            id: uuid::Uuid::new_v4().to_string(),
            command_id: command.id,
            status: ResponseStatus::Error("LSP not yet implemented".to_string()),
            data: serde_json::Value::Null,
            timestamp: chrono::Utc::now().timestamp(),
        }
    }

    /// Handle system queries
    async fn handle_system_query(&self, command: WasmCommand) -> WasmResponse {
        debug!("Handling system query");

        let query_type = command.payload.get("type")
            .and_then(|v| v.as_str())
            .unwrap_or("unknown");

        let result = match query_type {
            "session_info" => serde_json::json!({
                "session_id": self.session_id,
                "initialized": self.initialized,
                "timestamp": chrono::Utc::now().timestamp()
            }),
            "capabilities" => serde_json::json!({
                "file_operations": true,
                "editor_bridge": true,
                "lsp_support": false,  // Not implemented yet
                "security": true
            }),
            _ => serde_json::json!({
                "error": format!("Unknown query type: {}", query_type)
            })
        };

        WasmResponse {
            id: uuid::Uuid::new_v4().to_string(),
            command_id: command.id,
            status: ResponseStatus::Success,
            data: result,
            timestamp: chrono::Utc::now().timestamp(),
        }
    }

    /// Read file via WASI
    async fn read_file(&self, path: &str) -> Result<serde_json::Value> {
        // WASI file operations would be implemented here
        // For now, return a placeholder
        debug!("Reading file: {}", path);

        Ok(serde_json::json!({
            "path": path,
            "content": "File content would be read via WASI",
            "size": 0,
            "timestamp": chrono::Utc::now().timestamp()
        }))
    }

    /// Write file via WASI
    async fn write_file(&self, path: &str, content: &str) -> Result<serde_json::Value> {
        debug!("Writing file: {} ({} bytes)", path, content.len());

        Ok(serde_json::json!({
            "path": path,
            "bytes_written": content.len(),
            "timestamp": chrono::Utc::now().timestamp()
        }))
    }

    /// Create file via WASI
    async fn create_file(&self, path: &str) -> Result<serde_json::Value> {
        debug!("Creating file: {}", path);

        Ok(serde_json::json!({
            "path": path,
            "created": true,
            "timestamp": chrono::Utc::now().timestamp()
        }))
    }

    /// Delete file via WASI
    async fn delete_file(&self, path: &str) -> Result<serde_json::Value> {
        debug!("Deleting file: {}", path);

        Ok(serde_json::json!({
            "path": path,
            "deleted": true,
            "timestamp": chrono::Utc::now().timestamp()
        }))
    }

    /// List files in directory via WASI
    async fn list_files(&self, path: &str) -> Result<serde_json::Value> {
        debug!("Listing files in: {}", path);

        Ok(serde_json::json!({
            "path": path,
            "files": [],
            "timestamp": chrono::Utc::now().timestamp()
        }))
    }

    /// Watch file for changes via WASI
    async fn watch_file(&self, path: &str) -> Result<serde_json::Value> {
        debug!("Watching file: {}", path);

        Ok(serde_json::json!({
            "path": path,
            "watching": true,
            "timestamp": chrono::Utc::now().timestamp()
        }))
    }
}

