    pub enable_network: bool,
    /// Allowed domains for outbound connections
    pub allowed_domains: Vec<String>,
    /// Connection timeout in milliseconds
    pub connection_timeout: u64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WasmPerformanceConfig {
    /// Maximum memory usage in bytes
    pub max_memory_usage: usize,
    /// Enable performance monitoring
    pub enable_monitoring: bool,
    /// Command execution timeout in milliseconds
    pub command_timeout: u64,
}

impl Default for WasmConfig {
    fn default() -> Self {
        Self {
            security: WasmSecurityConfig {
                max_payload_size: 1024 * 1024, // 1MB
                sanitize_inputs: true,
                allowed_extensions: vec![
                    "rs".to_string(),
                    "js".to_string(),
                    "ts".to_string(),
                    "py".to_string(),
                    "md".to_string(),
                    "txt".to_string(),
                    "json".to_string(),
                    "toml".to_string(),
                    "yaml".to_string(),
                    "yml".to_string(),
                ],
                blocked_patterns: vec![
                    "*.exe".to_string(),
                    "*.dll".to_string(),
                    "*.so".to_string(),
                    "/etc/*".to_string(),
                    "/proc/*".to_string(),
                    "/sys/*".to_string(),
                ],
                session_timeout: 3600, // 1 hour
            },
            editor: WasmEditorConfig {
                default_editor: "nvim".to_string(),
                editor_settings: HashMap::new(),
                supported_filetypes: vec![
                    "rust".to_string(),
                    "javascript".to_string(),
                    "typescript".to_string(),
                    "python".to_string(),
                    "markdown".to_string(),
                    "json".to_string(),
                    "toml".to_string(),
                    "yaml".to_string(),
                ],
                syntax_highlighting: true,
            },
            filesystem: WasmFilesystemConfig {
                base_directory: "/workspace".to_string(),
                max_file_size: 10 * 1024 * 1024, // 10MB
                enable_file_watching: true,
                temp_directory: "/tmp".to_string(),
            },
            network: WasmNetworkConfig {
                enable_network: false, // Disabled by default for security
                allowed_domains: vec![],
                connection_timeout: 5000, // 5 seconds
            },
            performance: WasmPerformanceConfig {
                max_memory_usage: 256 * 1024 * 1024, // 256MB
                enable_monitoring: true,
                command_timeout: 10000, // 10 seconds
            },
        }
    }
}

impl WasmConfig {
    /// Validate the configuration
    pub fn validate(&self) -> Result<()> {
        // Validate security settings
        if self.security.max_payload_size == 0 {
            return Err(anyhow::anyhow!("Maximum payload size must be greater than 0"));
        }

        if self.security.session_timeout == 0 {
            return Err(anyhow::anyhow!("Session timeout must be greater than 0"));
        }

        // Validate filesystem settings
        if self.filesystem.max_file_size == 0 {
            return Err(anyhow::anyhow!("Maximum file size must be greater than 0"));
        }

        if self.filesystem.base_directory.is_empty() {
            return Err(anyhow::anyhow!("Base directory cannot be empty"));
        }

        // Validate performance settings
        if self.performance.max_memory_usage == 0 {
            return Err(anyhow::anyhow!("Maximum memory usage must be greater than 0"));
        }

        if self.performance.command_timeout == 0 {
            return Err(anyhow::anyhow!("Command timeout must be greater than 0"));
        }

        Ok(())
    }

    /// Get a setting value by path (dot-separated)
    pub fn get_setting(&self, path: &str) -> Option<serde_json::Value> {
        let config_json = serde_json::to_value(self).ok()?;

        let parts: Vec<&str> = path.split('.').collect();
        let mut current = &config_json;

        for part in parts {
            current = current.get(part)?;
        }

        Some(current.clone())
    }

    /// Update a setting value by path
    pub fn update_setting(&mut self, path: &str, value: serde_json::Value) -> Result<()> {
        // For simplicity, we'll re-deserialize the entire config
        // In a production implementation, this could be more efficient
        let mut config_json = serde_json::to_value(self)?;

        let parts: Vec<&str> = path.split('.').collect();
        let mut current = &mut config_json;

        for (i, part) in parts.iter().enumerate() {
            if i == parts.len() - 1 {
                // Last part - set the value
                if let Some(object) = current.as_object_mut() {
                    object.insert(part.to_string(), value);
                } else {
                    return Err(anyhow::anyhow!("Invalid path: {}", path));
                }
            } else {
                // Navigate to the next level
                current = current.get_mut(part)
                    .ok_or_else(|| anyhow::anyhow!("Invalid path: {}", path))?;
            }
        }

        *self = serde_json::from_value(config_json)?;
        Ok(())
    }

    /// Check if a file extension is allowed
    pub fn is_extension_allowed(&self, extension: &str) -> bool {
        self.security.allowed_extensions.contains(&extension.to_lowercase())
    }

    /// Check if a file path matches blocked patterns
    pub fn is_path_blocked(&self, path: &str) -> bool {
        use glob_match::glob_match;

        for pattern in &self.security.blocked_patterns {
            if glob_match(pattern, path) {
                return true;
            }
        }

        false
    }

    /// Get the effective file size limit
    pub fn get_max_file_size(&self) -> usize {
        self.filesystem.max_file_size
    }

    /// Get the session timeout
    pub fn get_session_timeout(&self) -> std::time::Duration {
        std::time::Duration::from_secs(self.security.session_timeout)
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_default_config() {
        let config = WasmConfig::default();
        assert!(config.validate().is_ok());
        assert!(config.is_extension_allowed("rs"));
        assert!(config.is_extension_allowed("js"));
        assert!(!config.is_extension_allowed("exe"));
    }

    #[test]
    fn test_blocked_patterns() {
        let config = WasmConfig::default();
        assert!(config.is_path_blocked("/etc/passwd"));
        assert!(config.is_path_blocked("/proc/version"));
        assert!(!config.is_path_blocked("/workspace/src/main.rs"));
    }

    #[test]
    fn test_setting_access() {
        let config = WasmConfig::default();
        let max_size = config.get_setting("security.max_payload_size");
        assert!(max_size.is_some());
        assert_eq!(max_size.unwrap().as_u64(), Some(1048576));
    }

    #[test]
    fn test_config_validation() {
        let mut config = WasmConfig::default();

        // Test invalid max_payload_size
        config.security.max_payload_size = 0;
        assert!(config.validate().is_err());

        // Test invalid session_timeout
        config.security.max_payload_size = 1024;
        config.security.session_timeout = 0;
        assert!(config.validate().is_err());

        // Test valid config
        config.security.session_timeout = 3600;
        assert!(config.validate().is_ok());
    }
}

