            "command": "open_file",
            "args": ["/workspace/test.rs"],
            "options": {}
        });

        let result = bridge.execute_command(command_payload).await;
        assert!(result.is_ok());

        let response: EditorResponse = serde_json::from_value(result.unwrap()).unwrap();
        assert!(response.success);
        assert!(response.error.is_none());
    }
