            }
        };

        Ok(LspResponse {
            id: request_id.to_string(),
            result,
            error: if result.is_none() {
                Some(LspError {
                    code: -32601,
                    message: format!("Method not found: {}", method),
                    data: None,
                })
            } else {
                None
            },
        })
    }

    /// Get LSP statistics
    pub fn get_stats(&self) -> HashMap<String, serde_json::Value> {
        let mut stats = HashMap::new();

        stats.insert(
            "total_servers".to_string(),
            serde_json::Value::from(self.language_servers.len())
        );

        stats.insert(
            "active_requests".to_string(),
