//! WebAssembly utility functions and helpers

use anyhow::Result;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use tracing::{debug, warn};

/// WebAssembly-compatible utility functions
pub struct WasmUtils;

/// File information structure
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct FileInfo {
    pub path: String,
    pub size: u64,
    pub modified: i64,
    pub is_directory: bool,
    pub extension: Option<String>,
    pub mime_type: Option<String>,
}

/// System information structure
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SystemInfo {
    pub platform: String,
    pub arch: String,
    pub memory_usage: u64,
    pub timestamp: i64,
}

impl WasmUtils {
    /// Get file extension from path
    pub fn get_file_extension(path: &str) -> Option<String> {
        std::path::Path::new(path)
            .extension()
            .and_then(|ext| ext.to_str())
            .map(|s| s.to_lowercase())
    }

    /// Get MIME type from file extension
    pub fn get_mime_type(extension: &str) -> Option<String> {
        let mime_types = [
            ("rs", "text/x-rust"),
            ("js", "application/javascript"),
            ("ts", "application/typescript"),
            ("py", "text/x-python"),
            ("html", "text/html"),
            ("css", "text/css"),
            ("json", "application/json"),
            ("xml", "application/xml"),
            ("md", "text/markdown"),
            ("txt", "text/plain"),
            ("toml", "application/toml"),
            ("yaml", "application/yaml"),
            ("yml", "application/yaml"),
            ("png", "image/png"),
            ("jpg", "image/jpeg"),
            ("jpeg", "image/jpeg"),
            ("gif", "image/gif"),
            ("svg", "image/svg+xml"),
            ("pdf", "application/pdf"),
            ("zip", "application/zip"),
        ];

        mime_types.iter()
            .find(|(ext, _)| ext == &extension.to_lowercase())
            .map(|(_, mime)| mime.to_string())
    }

    /// Validate file path for security
    pub fn is_safe_path(path: &str) -> bool {
        // Check for path traversal attempts
        if path.contains("../") || path.contains("..\\") {
            return false;
        }

        // Check for absolute paths to system directories
        let dangerous_paths = [
            "/etc/",
            "/proc/",
            "/sys/",
            "/dev/",
            "/boot/",
            "/root/",
            "C:\\Windows\\",
            "C:\\System32\\",
        ];

        for dangerous in dangerous_paths.iter() {
            if path.starts_with(dangerous) {
                return false;
            }
        }

        // Check for null bytes
        if path.contains('\0') {
            return false;
        }

        true
    }

    /// Sanitize filename for filesystem
    pub fn sanitize_filename(filename: &str) -> String {
        debug!("Sanitizing filename: {}", filename);

        let invalid_chars = ['<', '>', ':', '"', '|', '?', '*', '/', '\\', '\0'];
        let mut sanitized = String::new();

        for ch in filename.chars() {
            if invalid_chars.contains(&ch) || ch.is_control() {
                sanitized.push('_');
            } else {
                sanitized.push(ch);
            }
        }

        // Limit length
        if sanitized.len() > 255 {
            sanitized.truncate(252);
            sanitized.push_str("...");
        }

        // Ensure it's not empty or just dots
        if sanitized.is_empty() || sanitized.chars().all(|c| c == '.') {
            sanitized = "unnamed".to_string();
        }

        debug!("Sanitized filename: {}", sanitized);
        sanitized
    }

    /// Format file size in human-readable format
    pub fn format_file_size(size: u64) -> String {
        const UNITS: &[&str] = &["B", "KB", "MB", "GB", "TB"];
        const THRESHOLD: f64 = 1024.0;

        if size == 0 {
            return "0 B".to_string();
        }

        let size_f = size as f64;
        let exp = (size_f.ln() / THRESHOLD.ln()).floor() as usize;
        let exp = exp.min(UNITS.len() - 1);

        let size_in_unit = size_f / THRESHOLD.powi(exp as i32);

        if exp == 0 {
            format!("{} {}", size, UNITS[exp])
        } else {
            format!("{:.1} {}", size_in_unit, UNITS[exp])
        }
    }

    /// Parse command line arguments
    pub fn parse_args(args: &str) -> Vec<String> {
        if args.trim().is_empty() {
            return Vec::new();
        }

        let mut result = Vec::new();
        let mut current = String::new();
        let mut in_quotes = false;
        let mut escape_next = false;

        for ch in args.chars() {
            if escape_next {
                current.push(ch);
                escape_next = false;
            } else if ch == '\\' {
                escape_next = true;
            } else if ch == '"' && !in_quotes {
                in_quotes = true;
            } else if ch == '"' && in_quotes {
                in_quotes = false;
            } else if ch == ' ' && !in_quotes {
                if !current.is_empty() {
                    result.push(current);
                    current = String::new();
                }
            } else {
                current.push(ch);
            }
        }

        if !current.is_empty() {
            result.push(current);
        }

        result
    }

    /// Generate unique identifier
    pub fn generate_id() -> String {
        uuid::Uuid::new_v4().to_string()
    }

    /// Get current timestamp in milliseconds
    pub fn current_timestamp_ms() -> i64 {
        chrono::Utc::now().timestamp_millis()
    }

    /// Get current timestamp in seconds
    pub fn current_timestamp() -> i64 {
        chrono::Utc::now().timestamp()
    }

    /// Format duration in human-readable format
    pub fn format_duration(duration_ms: u64) -> String {
        let duration_s = duration_ms / 1000;

        if duration_s < 60 {
            format!("{}s", duration_s)
        } else if duration_s < 3600 {
            let minutes = duration_s / 60;
            let seconds = duration_s % 60;
            if seconds == 0 {
                format!("{}m", minutes)
            } else {
                format!("{}m {}s", minutes, seconds)
            }
        } else {
            let hours = duration_s / 3600;
            let minutes = (duration_s % 3600) / 60;
            if minutes == 0 {
                format!("{}h", hours)
            } else {
                format!("{}h {}m", hours, minutes)
            }
        }
    }

    /// Convert bytes to hexadecimal string
    pub fn bytes_to_hex(bytes: &[u8]) -> String {
        hex::encode(bytes)
    }

    /// Convert hexadecimal string to bytes
    pub fn hex_to_bytes(hex: &str) -> Result<Vec<u8>> {
        hex::decode(hex).map_err(|e| anyhow::anyhow!("Invalid hex string: {}", e))
    }

    /// Truncate string to specified length
    pub fn truncate_string(s: &str, max_len: usize) -> String {
        if s.len() <= max_len {
            s.to_string()
        } else {
            let mut truncated = s.chars().take(max_len.saturating_sub(3)).collect::<String>();
            truncated.push_str("...");
            truncated
        }
    }

    /// Escape special characters for JSON
    pub fn escape_json_string(s: &str) -> String {
        s.chars()
            .map(|c| match c {
                '"' => "\\\"".to_string(),
                '\\' => "\\\\".to_string(), 
                '\n' => "\\n".to_string(),
