}

/// WebAssembly performance monitoring utilities
pub struct WasmPerformanceMonitor {
    start_times: HashMap<String, i64>,
}

impl WasmPerformanceMonitor {
    pub fn new() -> Self {
        Self {
            start_times: HashMap::new(),
        }
    }

    /// Start timing an operation
    pub fn start_timer(&mut self, operation: &str) {
        self.start_times.insert(
            operation.to_string(),
            WasmUtils::current_timestamp_ms(),
        );
    }

    /// Stop timing an operation and return elapsed time
    pub fn stop_timer(&mut self, operation: &str) -> Option<u64> {
        if let Some(start_time) = self.start_times.remove(operation) {
            let elapsed = WasmUtils::current_timestamp_ms() - start_time;
            Some(elapsed as u64)
        } else {
            warn!("Timer not found for operation: {}", operation);
            None
        }
    }

    /// Get all active timers
    pub fn get_active_timers(&self) -> Vec<String> {
        self.start_times.keys().cloned().collect()
    }
}

impl Default for WasmPerformanceMonitor {
    fn default() -> Self {
        Self::new()
    }
}

