//! Vimmacs WebAssembly Runtime
//! 
//! High-performance WebAssembly runtime for Vimmacs using Wasmer with Arch Linux integration,
//! Kata Container security, and remote access capabilities.

#![warn(clippy::all, clippy::pedantic)]
#![allow(clippy::module_name_repetitions)]

use anyhow::Result;
use std::sync::Arc;
use tokio::sync::RwLock;
use tracing::{error, info, warn};

pub mod arch_integration;
pub mod auth;
pub mod config;
pub mod container;
pub mod network;
pub mod runtime;
pub mod security;
pub mod storage;

use crate::config::RuntimeConfig;
use crate::runtime::WasmRuntimeManager;
use crate::container::KataContainer;
use crate::auth::AuthenticationManager;
use crate::network::NetworkManager;

/// Main Vimmacs WebAssembly runtime service
#[derive(Clone)]
pub struct VimmacsWasmRuntime {
    config: Arc<RuntimeConfig>,
    runtime_manager: Arc<RwLock<WasmRuntimeManager>>,
    container_manager: Arc<RwLock<KataContainer>>,
    auth_manager: Arc<RwLock<AuthenticationManager>>,
    network_manager: Arc<RwLock<NetworkManager>>,
    shutdown_tx: Option<tokio::sync::oneshot::Sender<()>>,
}

/// Runtime statistics
#[derive(Debug, Clone, serde::Serialize, serde::Deserialize)]
pub struct RuntimeStats {
    pub uptime: std::time::Duration,
    pub active_instances: u32,
    pub memory_usage: u64,
    pub total_requests: u64,
    pub error_count: u32,
    pub container_stats: container::ContainerStats,
}

impl VimmacsWasmRuntime {
    /// Create a new WebAssembly runtime instance
    pub async fn new(config: RuntimeConfig) -> Result<Self> {
        info!("Initializing Vimmacs WebAssembly Runtime");

        let config = Arc::new(config);

        // Initialize components
        let runtime_manager = Arc::new(RwLock::new(
            WasmRuntimeManager::new(&config).await?
        ));

        let container_manager = Arc::new(RwLock::new(
            KataContainer::new(&config).await?
        ));

        let auth_manager = Arc::new(RwLock::new(
            AuthenticationManager::new(&config).await?
        ));

        let network_manager = Arc::new(RwLock::new(
            NetworkManager::new(&config).await?
        ));

        info!("Vimmacs WebAssembly Runtime initialized successfully");

        Ok(Self {
            config,
            runtime_manager,
            container_manager,
            auth_manager,
            network_manager,
            shutdown_tx: None,
        })
    }

    /// Start the WebAssembly runtime service
    pub async fn start(&mut self) -> Result<()> {
        info!("Starting Vimmacs WebAssembly Runtime service");

        let (shutdown_tx, mut shutdown_rx) = tokio::sync::oneshot::channel();
        self.shutdown_tx = Some(shutdown_tx);

        // Start all components
        {
            let mut runtime_manager = self.runtime_manager.write().await;
            runtime_manager.start().await?;
        }

        {
            let mut container_manager = self.container_manager.write().await;
            container_manager.start().await?;
        }

        {
            let mut auth_manager = self.auth_manager.write().await;
            auth_manager.start().await?;
        }

        {
            let mut network_manager = self.network_manager.write().await;
            network_manager.start().await?;
        }

        // Start HTTP API server
        self.start_api_server().await?;

        // Start WebSocket server for real-time communication
        self.start_websocket_server().await?;

        info!("All runtime services started successfully");

        // Main service loop
        tokio::select! {
            _ = &mut shutdown_rx => {
                info!("Received shutdown signal");
            }
            _ = self.health_check_loop() => {
                warn!("Health check loop exited");
            }
            _ = self.metrics_collection_loop() => {
                warn!("Metrics collection loop exited");
            }
        }

        self.shutdown().await?;
        Ok(())
    }

    /// Gracefully shutdown the runtime
    pub async fn shutdown(&mut self) -> Result<()> {
        info!("Shutting down Vimmacs WebAssembly Runtime");

        // Stop all components in reverse order
        {
            let mut network_manager = self.network_manager.write().await;
            network_manager.stop().await?;
        }

        {
            let mut auth_manager = self.auth_manager.write().await;
            auth_manager.stop().await?;
        }

        {
            let mut container_manager = self.container_manager.write().await;
            container_manager.stop().await?;
        }

        {
            let mut runtime_manager = self.runtime_manager.write().await;
            runtime_manager.shutdown().await?;
        }

        info!("Vimmacs WebAssembly Runtime shutdown complete");
        Ok(())
    }

    /// Execute WebAssembly code in a secure container
    pub async fn execute_wasm(
        &self,
        wasm_bytes: &[u8],
        function_name: &str,
        args: Vec<wasmtime::Val>,
        client_auth: &str,
    ) -> Result<Vec<wasmtime::Val>> {
        // Authenticate request
        {
            let auth_manager = self.auth_manager.read().await;
            auth_manager.verify_token(client_auth).await?;
        }

        // Execute in secure container
        {
            let container_manager = self.container_manager.read().await;
            let container_id = container_manager.create_secure_container().await?;

            let runtime_manager = self.runtime_manager.read().await;
            let result = runtime_manager
                .execute_in_container(&container_id, wasm_bytes, function_name, args)
                .await;

            // Cleanup container
            container_manager.destroy_container(&container_id).await?;

            result
        }
    }

    /// Get runtime statistics
    pub async fn get_stats(&self) -> Result<RuntimeStats> {
        let runtime_manager = self.runtime_manager.read().await;
        let container_manager = self.container_manager.read().await;

        Ok(RuntimeStats {
            uptime: runtime_manager.get_uptime(),
            active_instances: runtime_manager.get_active_instance_count(),
            memory_usage: runtime_manager.get_memory_usage(),
            total_requests: runtime_manager.get_total_requests(),
            error_count: runtime_manager.get_error_count(),
            container_stats: container_manager.get_stats().await?,
        })
    }

    /// Start HTTP API server
    async fn start_api_server(&self) -> Result<()> {
        use axum::{routing::get, routing::post, Router};

        let app = Router::new()
            .route("/health", get(self.health_endpoint()))
            .route("/stats", get(self.stats_endpoint()))
            .route("/execute", post(self.execute_endpoint()))
            .route("/auth/login", post(self.auth_login_endpoint()))
            .route("/auth/refresh", post(self.auth_refresh_endpoint()))
            .layer(tower_http::cors::CorsLayer::permissive())
            .layer(tower_http::trace::TraceLayer::new_for_http());

        let addr = format!("{}:{}", 
            self.config.network.bind_address,
            self.config.network.http_port
        );

        info!("Starting HTTP API server on {}", addr);

        let listener = tokio::net::TcpListener::bind(&addr).await?;

        tokio::spawn(async move {
            if let Err(e) = axum::serve(listener, app).await {
                error!("HTTP API server error: {}", e);
            }
        });

        Ok(())
    }

    /// Start WebSocket server for real-time communication
    async fn start_websocket_server(&self) -> Result<()> {
        let addr = format!("{}:{}", 
            self.config.network.bind_address,
            self.config.network.websocket_port
        );

        info!("Starting WebSocket server on {}", addr);

        let listener = tokio::net::TcpListener::bind(&addr).await?;

        let runtime_clone = self.clone();
        tokio::spawn(async move {
            while let Ok((stream, addr)) = listener.accept().await {
                let runtime = runtime_clone.clone();
                tokio::spawn(async move {
                    if let Err(e) = runtime.handle_websocket_connection(stream, addr).await {
                        error!("WebSocket connection error: {}", e);
                    }
                });
            }
        });

        Ok(())
    }

    /// Handle WebSocket connection
    async fn handle_websocket_connection(
        &self,
        stream: tokio::net::TcpStream,
        addr: std::net::SocketAddr,
    ) -> Result<()> {
        use tokio_tungstenite::{accept_async, tungstenite::Message};

        info!("New WebSocket connection from {}", addr);

        let ws_stream = accept_async(stream).await?;
        let (mut ws_sender, mut ws_receiver) = ws_stream.split();

        while let Some(msg) = ws_receiver.next().await {
            use futures::SinkExt;
            use futures::StreamExt;

            match msg? {
                Message::Text(text) => {
                    // Handle WebSocket command
                    match self.handle_websocket_command(&text).await {
                        Ok(response) => {
                            ws_sender.send(Message::Text(response)).await?;
                        }
                        Err(e) => {
                            let error_response = serde_json::json!({
                                "error": e.to_string(),
                                "timestamp": chrono::Utc::now().timestamp()
                            });
                            ws_sender.send(Message::Text(error_response.to_string())).await?;
                        }
                    }
                }
                Message::Binary(data) => {
                    // Handle binary WebAssembly module
                    match self.handle_wasm_binary(&data).await {
                        Ok(response) => {
                            ws_sender.send(Message::Text(response)).await?;
                        }
                        Err(e) => {
                            error!("WebAssembly execution error: {}", e);
                        }
                    }
                }
                Message::Close(_) => {
                    info!("WebSocket connection closed by client");
                    break;
                }
                _ => {}
            }
        }

        Ok(())
    }

    /// Handle WebSocket text command
    async fn handle_websocket_command(&self, command: &str) -> Result<String> {
        let cmd: serde_json::Value = serde_json::from_str(command)?;

        let response = match cmd.get("type").and_then(|v| v.as_str()) {
            Some("ping") => serde_json::json!({
                "type": "pong",
                "timestamp": chrono::Utc::now().timestamp()
            }),
            Some("get_stats") => {
                let stats = self.get_stats().await?;
                serde_json::to_value(stats)?
            }
            Some("list_containers") => {
                let container_manager = self.container_manager.read().await;
                let containers = container_manager.list_containers().await?;
                serde_json::to_value(containers)?
            }
            _ => serde_json::json!({
                "error": "Unknown command type",
                "received": cmd
            }),
        };

        Ok(response.to_string())
    }

    /// Handle WebAssembly binary execution
    async fn handle_wasm_binary(&self, data: &[u8]) -> Result<String> {
        // This would validate and execute the WebAssembly binary
        // For now, return a placeholder response
        let response = serde_json::json!({
            "type": "wasm_execution_result",
            "size": data.len(),
            "timestamp": chrono::Utc::now().timestamp(),
            "message": "WebAssembly binary received and processed"
        });

        Ok(response.to_string())
    }

    /// Health check loop
    async fn health_check_loop(&self) -> Result<()> {
        let mut interval = tokio::time::interval(std::time::Duration::from_secs(30));

        loop {
            interval.tick().await;

            // Check component health
            let runtime_healthy = {
                let runtime_manager = self.runtime_manager.read().await;
                runtime_manager.is_healthy().await
            };

            let container_healthy = {
                let container_manager = self.container_manager.read().await;
                container_manager.is_healthy().await
            };

            if !runtime_healthy || !container_healthy {
                warn!("Health check failed - runtime: {}, containers: {}", 
                    runtime_healthy, container_healthy);
            }
        }
    }

    /// Metrics collection loop
    async fn metrics_collection_loop(&self) -> Result<()> {
        let mut interval = tokio::time::interval(std::time::Duration::from_secs(10));

        loop {
            interval.tick().await;

            // Collect and export metrics
            if let Ok(stats) = self.get_stats().await {
                // Export to Prometheus/metrics system
                metrics::gauge!("vimmacs_runtime_active_instances", stats.active_instances as f64);
                metrics::gauge!("vimmacs_runtime_memory_usage", stats.memory_usage as f64);
                metrics::counter!("vimmacs_runtime_total_requests", stats.total_requests);
                metrics::counter!("vimmacs_runtime_error_count", stats.error_count as u64);
            }
        }
    }

    // API endpoint handlers
    fn health_endpoint(&self) -> impl axum::response::IntoResponse + Clone {
        || async {
            axum::Json(serde_json::json!({
                "status": "healthy",
                "timestamp": chrono::Utc::now().timestamp(),
                "version": env!("CARGO_PKG_VERSION")
            }))
        }
    }

    fn stats_endpoint(&self) -> impl axum::response::IntoResponse + Clone {
        let runtime = self.clone();
        || async move {
            match runtime.get_stats().await {
                Ok(stats) => axum::Json(serde_json::to_value(stats).unwrap()),
                Err(e) => axum::Json(serde_json::json!({
                    "error": e.to_string()
                })),
            }
        }
    }

    fn execute_endpoint(&self) -> impl axum::response::IntoResponse + Clone {
        let runtime = self.clone();
        |payload: axum::extract::Json<serde_json::Value>| async move {
            // Extract WebAssembly execution request
            let wasm_data = payload.get("wasm")
                .and_then(|v| v.as_str())
                .ok_or_else(|| anyhow::anyhow!("Missing wasm data"))?;

            let function_name = payload.get("function")
                .and_then(|v| v.as_str())
                .unwrap_or("main");

            let auth_token = payload.get("auth")
                .and_then(|v| v.as_str())
                .ok_or_else(|| anyhow::anyhow!("Missing auth token"))?;

            // Decode base64 WebAssembly data
            let wasm_bytes = base64::decode(wasm_data)?;

            // Execute WebAssembly
            match runtime.execute_wasm(&wasm_bytes, function_name, vec![], auth_token).await {
                Ok(result) => axum::Json(serde_json::json!({
                    "success": true,
                    "result": format!("{:?}", result),
                    "timestamp": chrono::Utc::now().timestamp()
                })),
                Err(e) => axum::Json(serde_json::json!({
                    "success": false,
                    "error": e.to_string(),
                    "timestamp": chrono::Utc::now().timestamp()
                })),
            }
        }
    }

    fn auth_login_endpoint(&self) -> impl axum::response::IntoResponse + Clone {
        let runtime = self.clone();
        |payload: axum::extract::Json<serde_json::Value>| async move {
            let auth_manager = runtime.auth_manager.read().await;

            let username = payload.get("username")
                .and_then(|v| v.as_str())
                .ok_or_else(|| anyhow::anyhow!("Missing username"))?;

            let password = payload.get("password")
                .and_then(|v| v.as_str())
                .ok_or_else(|| anyhow::anyhow!("Missing password"))?;

            match auth_manager.authenticate(username, password).await {
                Ok(token) => axum::Json(serde_json::json!({
                    "success": true,
                    "token": token,
                    "expires_in": 3600
                })),
                Err(e) => axum::Json(serde_json::json!({
                    "success": false,
                    "error": e.to_string()
                })),
            }
        }
    }

    fn auth_refresh_endpoint(&self) -> impl axum::response::IntoResponse + Clone {
        let runtime = self.clone();
        |payload: axum::extract::Json<serde_json::Value>| async move {
            let auth_manager = runtime.auth_manager.read().await;

            let token = payload.get("token")
                .and_then(|v| v.as_str())
                .ok_or_else(|| anyhow::anyhow!("Missing token"))?;

            match auth_manager.refresh_token(token).await {
                Ok(new_token) => axum::Json(serde_json::json!({
                    "success": true,
                    "token": new_token,
                    "expires_in": 3600
                })),
                Err(e) => axum::Json(serde_json::json!({
                    "success": false,
                    "error": e.to_string()
                })),
            }
        }
    }
}

impl Drop for VimmacsWasmRuntime {
    fn drop(&mut self) {
        if let Some(shutdown_tx) = self.shutdown_tx.take() {
            let _ = shutdown_tx.send(());
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[tokio::test]
    async fn test_runtime_creation() {
        let temp_dir = TempDir::new().unwrap();
        let config = RuntimeConfig::new_test(temp_dir.path());

        let runtime = VimmacsWasmRuntime::new(config).await;
        assert!(runtime.is_ok());
    }
}

