//! Runtime configuration management

use anyhow::Result;
use serde::{Deserialize, Serialize};
use std::path::{Path, PathBuf};

/// Main runtime configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct RuntimeConfig {
    pub server: ServerConfig,
    pub network: NetworkConfig,
    pub security: SecurityConfig,
    pub wasm: WasmConfig,
    pub container: ContainerConfig,
    pub arch: ArchLinuxConfig,
    pub storage: StorageConfig,
    pub monitoring: MonitoringConfig,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ServerConfig {
    pub bind_address: String,
    pub max_connections: u32,
    pub request_timeout: u64, // seconds
    pub worker_threads: Option<usize>,
    pub enable_compression: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct NetworkConfig {
    pub bind_address: String,
    pub http_port: u16,
    pub websocket_port: u16,
    pub tls_enabled: bool,
    pub cert_path: Option<PathBuf>,
    pub key_path: Option<PathBuf>,
    pub allowed_origins: Vec<String>,
    pub max_frame_size: usize,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityConfig {
    pub enable_authentication: bool,
    pub jwt_secret: String,
    pub token_lifetime: u64, // seconds
    pub max_execution_time: u64, // milliseconds
    pub memory_limit: usize, // bytes
    pub allowed_imports: Vec<String>,
    pub blocked_functions: Vec<String>,
    pub enable_sandboxing: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WasmConfig {
    pub engine: WasmEngine,
    pub compilation_strategy: CompilationStrategy,
    pub enable_bulk_memory: bool,
    pub enable_reference_types: bool,
    pub enable_simd: bool,
    pub enable_multi_value: bool,
    pub enable_threads: bool,
    pub max_instances: u32,
    pub instance_pool_size: u32,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum WasmEngine {
    Wasmer,
    Wasmtime,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum CompilationStrategy {
    Auto,
    Cranelift,
    LLVM,
    Singlepass,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContainerConfig {
    pub runtime_type: ContainerRuntime,
    pub kata_config_path: Option<PathBuf>,
    pub max_containers: u32,
    pub container_timeout: u64, // seconds
    pub enable_resource_limits: bool,
    pub cpu_limit: Option<f64>,
    pub memory_limit: Option<String>, // e.g., "256m", "1g"
    pub storage_limit: Option<String>,
    pub enable_networking: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ContainerRuntime {
    Kata,
    Docker,
    Podman,
    Native,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ArchLinuxConfig {
    pub enable_arch_integration: bool,
    pub base_image: String,
    pub package_manager: String, // "pacman"
    pub mirror_list: Vec<String>,
    pub enable_aur: bool,
    pub user_name: String,
    pub home_directory: PathBuf,
    pub shell: String,
    pub additional_packages: Vec<String>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StorageConfig {
    pub data_directory: PathBuf,
    pub temp_directory: PathBuf,
    pub wasm_cache_directory: PathBuf,
    pub max_cache_size: usize, // bytes
    pub enable_compression: bool,
    pub backup_enabled: bool,
    pub backup_interval: u64, // seconds
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MonitoringConfig {
    pub enable_metrics: bool,
    pub metrics_port: u16,
    pub prometheus_enabled: bool,
    pub log_level: String,
    pub enable_tracing: bool,
    pub jaeger_endpoint: Option<String>,
    pub health_check_interval: u64, // seconds
}

impl Default for RuntimeConfig {
    fn default() -> Self {
        Self {
            server: ServerConfig {
                bind_address: "127.0.0.1".to_string(),
                max_connections: 1000,
                request_timeout: 30,
                worker_threads: None, // Use system default
                enable_compression: true,
            },
            network: NetworkConfig {
                bind_address: "127.0.0.1".to_string(),
                http_port: 8080,
                websocket_port: 8081,
                tls_enabled: false,
                cert_path: None,
                key_path: None,
                allowed_origins: vec!["http://localhost:3000".to_string()],
                max_frame_size: 16 * 1024 * 1024, // 16MB
            },
            security: SecurityConfig {
                enable_authentication: true,
                jwt_secret: "change-this-in-production".to_string(),
                token_lifetime: 3600, // 1 hour
                max_execution_time: 30000, // 30 seconds
                memory_limit: 128 * 1024 * 1024, // 128MB
                allowed_imports: vec![
                    "env".to_string(),
                    "wasi_snapshot_preview1".to_string(),
                ],
                blocked_functions: vec![
                    "fd_write".to_string(), // Can be restricted for security
                ],
                enable_sandboxing: true,
            },
            wasm: WasmConfig {
                engine: WasmEngine::Wasmer,
                compilation_strategy: CompilationStrategy::Auto,
                enable_bulk_memory: true,
                enable_reference_types: true,
                enable_simd: true,
                enable_multi_value: true,
                enable_threads: false, // Disabled for security
                max_instances: 100,
                instance_pool_size: 10,
            },
            container: ContainerConfig {
                runtime_type: ContainerRuntime::Kata,
                kata_config_path: Some("/etc/kata-containers/configuration.toml".into()),
                max_containers: 50,
                container_timeout: 300, // 5 minutes
                enable_resource_limits: true,
                cpu_limit: Some(1.0), // 1 CPU core
                memory_limit: Some("256m".to_string()),
                storage_limit: Some("1g".to_string()),
                enable_networking: false, // Disabled for security
            },
            arch: ArchLinuxConfig {
                enable_arch_integration: true,
                base_image: "archlinux:latest".to_string(),
                package_manager: "pacman".to_string(),
                mirror_list: vec![
                    "https://mirror.rackspace.com/archlinux/$repo/os/$arch".to_string(),
                    "https://mirrors.kernel.org/archlinux/$repo/os/$arch".to_string(),
                ],
                enable_aur: false, // Disabled for security
                user_name: "vimmacs".to_string(),
                home_directory: "/home/vimmacs".into(),
                shell: "/bin/zsh".to_string(),
                additional_packages: vec![
                    "base-devel".to_string(),
                    "git".to_string(),
                    "curl".to_string(),
                    "wget".to_string(),
                    "neovim".to_string(),
                    "emacs".to_string(),
                    "rust".to_string(),
                    "nodejs".to_string(),
                    "npm".to_string(),
                    "python".to_string(),
                    "python-pip".to_string(),
                ],
            },
            storage: StorageConfig {
                data_directory: "/var/lib/vimmacs".into(),
                temp_directory: "/tmp/vimmacs".into(),
                wasm_cache_directory: "/var/cache/vimmacs/wasm".into(),
                max_cache_size: 1024 * 1024 * 1024, // 1GB
                enable_compression: true,
                backup_enabled: false,
                backup_interval: 3600, // 1 hour
            },
            monitoring: MonitoringConfig {
                enable_metrics: true,
                metrics_port: 9090,
                prometheus_enabled: true,
                log_level: "info".to_string(),
                enable_tracing: true,
                jaeger_endpoint: None,
                health_check_interval: 30,
            },
        }
    }
}

impl RuntimeConfig {
    /// Load configuration from file
    pub fn load_from_file<P: AsRef<Path>>(path: P) -> Result<Self> {
        let content = std::fs::read_to_string(path)?;
        let config: Self = toml::from_str(&content)?;
        config.validate()?;
        Ok(config)
    }

    /// Save configuration to file
    pub fn save_to_file<P: AsRef<Path>>(&self, path: P) -> Result<()> {
        let content = toml::to_string_pretty(self)?;
        std::fs::write(path, content)?;
        Ok(())
    }

    /// Load configuration from environment variables and file
    pub fn load() -> Result<Self> {
        use figment::{Figment, providers::{Format, Toml, Env}};

        let config: Self = Figment::new()
            .merge(Toml::file("vimmacs-runtime.toml"))
            .merge(Env::prefixed("VIMMACS_"))
            .extract()?;

        config.validate()?;
        Ok(config)
    }

    /// Validate configuration
    pub fn validate(&self) -> Result<()> {
        // Validate server configuration
        if self.server.max_connections == 0 {
            return Err(anyhow::anyhow!("max_connections must be greater than 0"));
        }

        if self.server.request_timeout == 0 {
            return Err(anyhow::anyhow!("request_timeout must be greater than 0"));
        }

        // Validate network configuration
        if self.network.http_port == 0 {
            return Err(anyhow::anyhow!("http_port must be greater than 0"));
        }

        if self.network.websocket_port == 0 {
            return Err(anyhow::anyhow!("websocket_port must be greater than 0"));
        }

        if self.network.http_port == self.network.websocket_port {
            return Err(anyhow::anyhow!("HTTP and WebSocket ports must be different"));
        }

        // Validate TLS configuration
        if self.network.tls_enabled {
            if self.network.cert_path.is_none() {
                return Err(anyhow::anyhow!("cert_path is required when TLS is enabled"));
            }
            if self.network.key_path.is_none() {
                return Err(anyhow::anyhow!("key_path is required when TLS is enabled"));
            }
        }

        // Validate security configuration
        if self.security.enable_authentication && self.security.jwt_secret.is_empty() {
            return Err(anyhow::anyhow!("jwt_secret is required when authentication is enabled"));
        }

        if self.security.jwt_secret == "change-this-in-production" {
            eprintln!("WARNING: Using default JWT secret in production is not secure!");
        }

        if self.security.token_lifetime == 0 {
            return Err(anyhow::anyhow!("token_lifetime must be greater than 0"));
        }

        if self.security.memory_limit == 0 {
            return Err(anyhow::anyhow!("memory_limit must be greater than 0"));
        }

        // Validate WebAssembly configuration
        if self.wasm.max_instances == 0 {
            return Err(anyhow::anyhow!("max_instances must be greater than 0"));
        }

        if self.wasm.instance_pool_size > self.wasm.max_instances {
            return Err(anyhow::anyhow!("instance_pool_size cannot exceed max_instances"));
        }

        // Validate container configuration
        if self.container.max_containers == 0 {
            return Err(anyhow::anyhow!("max_containers must be greater than 0"));
        }

        // Validate storage configuration
        if !self.storage.data_directory.is_absolute() {
            return Err(anyhow::anyhow!("data_directory must be an absolute path"));
        }

        if self.storage.max_cache_size == 0 {
            return Err(anyhow::anyhow!("max_cache_size must be greater than 0"));
        }

        // Validate monitoring configuration
        if self.monitoring.enable_metrics && self.monitoring.metrics_port == 0 {
            return Err(anyhow::anyhow!("metrics_port must be greater than 0 when metrics are enabled"));
        }

        Ok(())
    }

    /// Create directories required by configuration
    pub fn create_directories(&self) -> Result<()> {
        std::fs::create_dir_all(&self.storage.data_directory)?;
        std::fs::create_dir_all(&self.storage.temp_directory)?;
        std::fs::create_dir_all(&self.storage.wasm_cache_directory)?;
        Ok(())
    }

    /// Get WebAssembly engine features
    pub fn get_wasm_features(&self) -> Vec<&str> {
        let mut features = Vec::new();

        if self.wasm.enable_bulk_memory {
            features.push("bulk-memory");
        }
        if self.wasm.enable_reference_types {
            features.push("reference-types");
        }
        if self.wasm.enable_simd {
            features.push("simd");
        }
        if self.wasm.enable_multi_value {
            features.push("multi-value");
        }
        if self.wasm.enable_threads {
            features.push("threads");
        }

        features
    }

    /// Check if a function is allowed for execution
    pub fn is_function_allowed(&self, function_name: &str) -> bool {
        !self.security.blocked_functions.contains(&function_name.to_string())
    }

    /// Check if an import is allowed
    pub fn is_import_allowed(&self, import_name: &str) -> bool {
        self.security.allowed_imports.iter()
            .any(|allowed| allowed == import_name || import_name.starts_with(allowed))
    }

    /// Get container resource limits
    pub fn get_container_resources(&self) -> ContainerResources {
        ContainerResources {
            cpu_limit: self.container.cpu_limit,
            memory_limit: self.container.memory_limit.clone(),
            storage_limit: self.container.storage_limit.clone(),
        }
    }

    #[cfg(test)]
    pub fn new_test(temp_dir: &Path) -> Self {
        let mut config = Self::default();
        config.storage.data_directory = temp_dir.join("data");
        config.storage.temp_directory = temp_dir.join("tmp");
        config.storage.wasm_cache_directory = temp_dir.join("cache");
        config.security.jwt_secret = "test-secret-key-for-testing-only".to_string();
        config.network.http_port = 0; // Let OS choose
        config.network.websocket_port = 0; // Let OS choose
        config
    }
}

/// Container resource limits
#[derive(Debug, Clone)]
pub struct ContainerResources {
    pub cpu_limit: Option<f64>,
    pub memory_limit: Option<String>,
    pub storage_limit: Option<String>,
}

#[cfg(test)]
mod tests {
    use super::*;
    use tempfile::TempDir;

    #[test]
    fn test_default_config_validation() {
        let mut config = RuntimeConfig::default();
        config.security.jwt_secret = "test-secret".to_string();
        assert!(config.validate().is_ok());
    }

    #[test]
    fn test_invalid_config_validation() {
        let mut config = RuntimeConfig::default();
        config.server.max_connections = 0;
        assert!(config.validate().is_err());
    }

    #[test]
    fn test_config_serialization() {
        let config = RuntimeConfig::default();
        let toml_str = toml::to_string(&config).unwrap();
        let deserialized: RuntimeConfig = toml::from_str(&toml_str).unwrap();

        assert_eq!(config.server.bind_address, deserialized.server.bind_address);
        assert_eq!(config.network.http_port, deserialized.network.http_port);
    }

    #[test]
    fn test_config_file_operations() {
        let temp_dir = TempDir::new().unwrap();
        let config_path = temp_dir.path().join("config.toml");

        let mut config = RuntimeConfig::default();
        config.security.jwt_secret = "test-secret".to_string();

        // Save and load
        config.save_to_file(&config_path).unwrap();
        let loaded_config = RuntimeConfig::load_from_file(&config_path).unwrap();

        assert_eq!(config.security.jwt_secret, loaded_config.security.jwt_secret);
    }

    #[test]
    fn test_wasm_features() {
        let config = RuntimeConfig::default();
        let features = config.get_wasm_features();

        assert!(features.contains(&"bulk-memory"));
        assert!(features.contains(&"reference-types"));
        assert!(features.contains(&"simd"));
        assert!(features.contains(&"multi-value"));
    }

    #[test]
    fn test_security_checks() {
        let config = RuntimeConfig::default();

        assert!(config.is_import_allowed("env"));
        assert!(config.is_import_allowed("wasi_snapshot_preview1"));
        assert!(!config.is_import_allowed("unknown_import"));

        assert!(!config.is_function_allowed("fd_write"));
        assert!(config.is_function_allowed("allowed_function"));
    }

    #[test]
    fn test_directory_creation() {
