    pub wasm: WasmConfig,
    pub container: ContainerConfig,
    pub arch: ArchLinuxConfig,
    pub storage: StorageConfig,
    pub monitoring: MonitoringConfig,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ServerConfig {
    pub bind_address: String,
    pub max_connections: u32,
    pub request_timeout: u64, // seconds
    pub worker_threads: Option<usize>,
    pub enable_compression: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct NetworkConfig {
    pub bind_address: String,
    pub http_port: u16,
    pub websocket_port: u16,
    pub tls_enabled: bool,
    pub cert_path: Option<PathBuf>,
    pub key_path: Option<PathBuf>,
    pub allowed_origins: Vec<String>,
    pub max_frame_size: usize,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityConfig {
    pub enable_authentication: bool,
    pub jwt_secret: String,
    pub token_lifetime: u64, // seconds
    pub max_execution_time: u64, // milliseconds
    pub memory_limit: usize, // bytes
    pub allowed_imports: Vec<String>,
    pub blocked_functions: Vec<String>,
    pub enable_sandboxing: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct WasmConfig {
    pub engine: WasmEngine,
    pub compilation_strategy: CompilationStrategy,
    pub enable_bulk_memory: bool,
    pub enable_reference_types: bool,
    pub enable_simd: bool,
    pub enable_multi_value: bool,
    pub enable_threads: bool,
    pub max_instances: u32,
    pub instance_pool_size: u32,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum WasmEngine {
    Wasmer,
    Wasmtime,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum CompilationStrategy {
    Auto,
    Cranelift,
    LLVM,
    Singlepass,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContainerConfig {
    pub runtime_type: ContainerRuntime,
    pub kata_config_path: Option<PathBuf>,
    pub max_containers: u32,
    pub container_timeout: u64, // seconds
    pub enable_resource_limits: bool,
    pub cpu_limit: Option<f64>,
    pub memory_limit: Option<String>, // e.g., "256m", "1g"
    pub storage_limit: Option<String>,
    pub enable_networking: bool,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ContainerRuntime {
    Kata,
    Docker,
    Podman,
    Native,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ArchLinuxConfig {
