                enable_arch_integration: true,
                base_image: "archlinux:latest".to_string(),
                package_manager: "pacman".to_string(),
                mirror_list: vec![
                    "https://mirror.rackspace.com/archlinux/$repo/os/$arch".to_string(),
                    "https://mirrors.kernel.org/archlinux/$repo/os/$arch".to_string(),
                ],
                enable_aur: false, // Disabled for security
                user_name: "vimmacs".to_string(),
                home_directory: "/home/vimmacs".into(),
                shell: "/bin/zsh".to_string(),
                additional_packages: vec![
                    "base-devel".to_string(),
                    "git".to_string(),
                    "curl".to_string(),
                    "wget".to_string(),
                    "neovim".to_string(),
                    "emacs".to_string(),
                    "rust".to_string(),
                    "nodejs".to_string(),
