}

impl RuntimeConfig {
    /// Load configuration from file
    pub fn load_from_file<P: AsRef<Path>>(path: P) -> Result<Self> {
        let content = std::fs::read_to_string(path)?;
        let config: Self = toml::from_str(&content)?;
        config.validate()?;
        Ok(config)
    }

    /// Save configuration to file
    pub fn save_to_file<P: AsRef<Path>>(&self, path: P) -> Result<()> {
        let content = toml::to_string_pretty(self)?;
        std::fs::write(path, content)?;
        Ok(())
    }

    /// Load configuration from environment variables and file
    pub fn load() -> Result<Self> {
        use figment::{Figment, providers::{Format, Toml, Env}};

        let config: Self = Figment::new()
            .merge(Toml::file("vimmacs-runtime.toml"))
            .merge(Env::prefixed("VIMMACS_"))
            .extract()?;

        config.validate()?;
        Ok(config)
    }

    /// Validate configuration
    pub fn validate(&self) -> Result<()> {
        // Validate server configuration
        if self.server.max_connections == 0 {
            return Err(anyhow::anyhow!("max_connections must be greater than 0"));
        }

        if self.server.request_timeout == 0 {
            return Err(anyhow::anyhow!("request_timeout must be greater than 0"));
        }

        // Validate network configuration
        if self.network.http_port == 0 {
            return Err(anyhow::anyhow!("http_port must be greater than 0"));
        }

        if self.network.websocket_port == 0 {
            return Err(anyhow::anyhow!("websocket_port must be greater than 0"));
        }

        if self.network.http_port == self.network.websocket_port {
            return Err(anyhow::anyhow!("HTTP and WebSocket ports must be different"));
        }

        // Validate TLS configuration
        if self.network.tls_enabled {
            if self.network.cert_path.is_none() {
                return Err(anyhow::anyhow!("cert_path is required when TLS is enabled"));
            }
            if self.network.key_path.is_none() {
                return Err(anyhow::anyhow!("key_path is required when TLS is enabled"));
            }
        }

        // Validate security configuration
        if self.security.enable_authentication && self.security.jwt_secret.is_empty() {
            return Err(anyhow::anyhow!("jwt_secret is required when authentication is enabled"));
        }

        if self.security.jwt_secret == "change-this-in-production" {
            eprintln!("WARNING: Using default JWT secret in production is not secure!");
        }

        if self.security.token_lifetime == 0 {
            return Err(anyhow::anyhow!("token_lifetime must be greater than 0"));
        }

        if self.security.memory_limit == 0 {
            return Err(anyhow::anyhow!("memory_limit must be greater than 0"));
        }

        // Validate WebAssembly configuration
        if self.wasm.max_instances == 0 {
            return Err(anyhow::anyhow!("max_instances must be greater than 0"));
        }

        if self.wasm.instance_pool_size > self.wasm.max_instances {
            return Err(anyhow::anyhow!("instance_pool_size cannot exceed max_instances"));
        }

        // Validate container configuration
        if self.container.max_containers == 0 {
            return Err(anyhow::anyhow!("max_containers must be greater than 0"));
        }

        // Validate storage configuration
        if !self.storage.data_directory.is_absolute() {
            return Err(anyhow::anyhow!("data_directory must be an absolute path"));
        }

        if self.storage.max_cache_size == 0 {
            return Err(anyhow::anyhow!("max_cache_size must be greater than 0"));
        }

        // Validate monitoring configuration
        if self.monitoring.enable_metrics && self.monitoring.metrics_port == 0 {
            return Err(anyhow::anyhow!("metrics_port must be greater than 0 when metrics are enabled"));
        }

        Ok(())
    }

    /// Create directories required by configuration
    pub fn create_directories(&self) -> Result<()> {
        std::fs::create_dir_all(&self.storage.data_directory)?;
        std::fs::create_dir_all(&self.storage.temp_directory)?;
        std::fs::create_dir_all(&self.storage.wasm_cache_directory)?;
        Ok(())
    }

    /// Get WebAssembly engine features
    pub fn get_wasm_features(&self) -> Vec<&str> {
        let mut features = Vec::new();

        if self.wasm.enable_bulk_memory {
            features.push("bulk-memory");
        }
        if self.wasm.enable_reference_types {
            features.push("reference-types");
        }
        if self.wasm.enable_simd {
            features.push("simd");
        }
        if self.wasm.enable_multi_value {
            features.push("multi-value");
        }
        if self.wasm.enable_threads {
            features.push("threads");
        }

        features
    }

    /// Check if a function is allowed for execution
    pub fn is_function_allowed(&self, function_name: &str) -> bool {
        !self.security.blocked_functions.contains(&function_name.to_string())
    }

    /// Check if an import is allowed
    pub fn is_import_allowed(&self, import_name: &str) -> bool {
        self.security.allowed_imports.iter()
            .any(|allowed| allowed == import_name || import_name.starts_with(allowed))
    }

    /// Get container resource limits
    pub fn get_container_resources(&self) -> ContainerResources {
        ContainerResources {
            cpu_limit: self.container.cpu_limit,
            memory_limit: self.container.memory_limit.clone(),
            storage_limit: self.container.storage_limit.clone(),
        }
    }

    #[cfg(test)]
    pub fn new_test(temp_dir: &Path) -> Self {
        let mut config = Self::default();
        config.storage.data_directory = temp_dir.join("data");
        config.storage.temp_directory = temp_dir.join("tmp");
        config.storage.wasm_cache_directory = temp_dir.join("cache");
        config.security.jwt_secret = "test-secret-key-for-testing-only".to_string();
        config.network.http_port = 0; // Let OS choose
        config.network.websocket_port = 0; // Let OS choose
        config
    }
}

/// Container resource limits
#[derive(Debug, Clone)]
pub struct ContainerResources {
    pub cpu_limit: Option<f64>,
    pub memory_limit: Option<String>,
    pub storage_limit: Option<String>,
}

#[cfg(test)]
mod tests {
