//! WebAssembly runtime management with Wasmer integration

use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::PathBuf;
use std::sync::{Arc, RwLock};
use tokio::sync::Mutex;
use tracing::{debug, error, info, warn};
use wasmer::*;
use wasmer_wasi::{WasiState, WasiVersion};

use crate::config::{RuntimeConfig, WasmConfig};
use vimmacs_wasm_core::{VimmacsWasm, WasmConfig as CoreWasmConfig};

/// WebAssembly runtime manager for executing WASM modules
#[derive(Debug)]
pub struct WasmRuntimeManager {
    config: Arc<RuntimeConfig>,
    engine: Engine,
    store: Arc<Mutex<Store>>,
    instances: Arc<RwLock<HashMap<String, WasmInstance>>>,
    modules: Arc<RwLock<HashMap<String, Module>>>,
    wasi_environments: Arc<RwLock<HashMap<String, WasiEnv>>>,
    metrics: Arc<RwLock<RuntimeMetrics>>,
}

/// WebAssembly instance information
#[derive(Debug, Clone)]
pub struct WasmInstance {
    pub id: String,
    pub module_name: String,
    pub instance: Arc<Mutex<Instance>>,
    pub memory: Option<Memory>,
    pub created_at: chrono::DateTime<chrono::Utc>,
    pub last_execution: Option<chrono::DateTime<chrono::Utc>>,
    pub execution_count: u64,
    pub status: InstanceStatus,
}

/// Instance execution status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum InstanceStatus {
    Created,
    Running,
    Paused,
    Failed(String),
    Terminated,
}

/// Runtime performance metrics
#[derive(Debug, Default, Clone, Serialize, Deserialize)]
pub struct RuntimeMetrics {
    pub total_instances: u64,
    pub active_instances: u64,
    pub total_executions: u64,
    pub failed_executions: u64,
    pub average_execution_time_ms: f64,
    pub memory_usage_bytes: u64,
    pub uptime_seconds: u64,
}

/// WASI environment configuration
#[derive(Debug, Clone)]
pub struct WasiEnv {
    pub state: WasiState,
    pub imports: Imports,
    pub allowed_paths: Vec<PathBuf>,
    pub environment_vars: HashMap<String, String>,
}

impl WasmRuntimeManager {
    /// Create a new WebAssembly runtime manager
    pub fn new(config: Arc<RuntimeConfig>) -> Result<Self> {
        info!("Initializing WebAssembly runtime manager");

        // Initialize Wasmer engine based on configuration
        let engine = match config.wasm.engine_type.as_str() {
            "cranelift" => Engine::from(Cranelift::default()),
            "llvm" => Engine::from(LLVM::default()),
            "singlepass" => Engine::from(Singlepass::default()),
            _ => {
                warn!("Unknown engine type '{}', defaulting to Cranelift", config.wasm.engine_type);
                Engine::from(Cranelift::default())
            }
        };

        let store = Arc::new(Mutex::new(Store::new(&engine)));

        Ok(Self {
            config,
            engine,
            store,
            instances: Arc::new(RwLock::new(HashMap::new())),
            modules: Arc::new(RwLock::new(HashMap::new())),
            wasi_environments: Arc::new(RwLock::new(HashMap::new())),
            metrics: Arc::new(RwLock::new(RuntimeMetrics::default())),
        })
    }

    /// Load a WebAssembly module from bytes
    pub async fn load_module(&self, name: &str, wasm_bytes: &[u8]) -> Result<()> {
        debug!("Loading WebAssembly module: {}", name);

        let store = self.store.lock().await;
        let module = Module::new(&store, wasm_bytes)
            .map_err(|e| anyhow!("Failed to compile WASM module '{}': {}", name, e))?;

        drop(store);

        // Store the compiled module
        self.modules.write().unwrap().insert(name.to_string(), module);

        info!("Successfully loaded WASM module: {}", name);
        Ok(())
    }

    /// Create a new WebAssembly instance
    pub async fn create_instance(&self, module_name: &str, instance_id: &str) -> Result<String> {
        debug!("Creating WASM instance '{}' from module '{}'", instance_id, module_name);

        let module = {
            let modules = self.modules.read().unwrap();
            modules.get(module_name)
                .ok_or_else(|| anyhow!("Module '{}' not found", module_name))?
                .clone()
        };

        // Create WASI environment
        let wasi_env = self.create_wasi_environment(instance_id).await?;

        // Create instance with WASI imports
        let store = self.store.lock().await;
        let instance = Instance::new(&*store, &module, &wasi_env.imports)
            .map_err(|e| anyhow!("Failed to instantiate WASM module: {}", e))?;

        // Get memory if available
        let memory = instance.exports.get_memory("memory").ok().cloned();

        drop(store);

        let wasm_instance = WasmInstance {
            id: instance_id.to_string(),
            module_name: module_name.to_string(),
            instance: Arc::new(Mutex::new(instance)),
            memory,
            created_at: chrono::Utc::now(),
            last_execution: None,
            execution_count: 0,
            status: InstanceStatus::Created,
        };

        // Store the instance
        self.instances.write().unwrap().insert(instance_id.to_string(), wasm_instance);
        self.wasi_environments.write().unwrap().insert(instance_id.to_string(), wasi_env);

        // Update metrics
        {
            let mut metrics = self.metrics.write().unwrap();
            metrics.total_instances += 1;
            metrics.active_instances += 1;
        }

        info!("Successfully created WASM instance: {}", instance_id);
        Ok(instance_id.to_string())
    }

    /// Execute a function in a WebAssembly instance
    pub async fn execute_function(
        &self,
        instance_id: &str,
        function_name: &str,
        args: &[Value],
    ) -> Result<Vec<Value>> {
        debug!("Executing function '{}' in instance '{}'", function_name, instance_id);

        let start_time = std::time::Instant::now();

        // Get the instance
        let instance = {
            let instances = self.instances.read().unwrap();
            let wasm_instance = instances.get(instance_id)
                .ok_or_else(|| anyhow!("Instance '{}' not found", instance_id))?;
            wasm_instance.instance.clone()
        };

        // Execute the function
        let result = {
            let instance = instance.lock().await;
            let function = instance.exports.get_function(function_name)
                .map_err(|e| anyhow!("Function '{}' not found: {}", function_name, e))?;

            function.call(args)
                .map_err(|e| anyhow!("Function execution failed: {}", e))?
        };

        let execution_time = start_time.elapsed();

        // Update instance statistics
        {
            let mut instances = self.instances.write().unwrap();
            if let Some(wasm_instance) = instances.get_mut(instance_id) {
                wasm_instance.last_execution = Some(chrono::Utc::now());
                wasm_instance.execution_count += 1;
                wasm_instance.status = InstanceStatus::Running;
            }
        }

        // Update metrics
        {
            let mut metrics = self.metrics.write().unwrap();
            metrics.total_executions += 1;

            // Update average execution time
            let total_time = metrics.average_execution_time_ms * (metrics.total_executions - 1) as f64;
            metrics.average_execution_time_ms = 
                (total_time + execution_time.as_millis() as f64) / metrics.total_executions as f64;
        }

        debug!("Function execution completed in {:?}", execution_time);
        Ok(result)
    }

    /// Create a WASI environment for an instance
    async fn create_wasi_environment(&self, instance_id: &str) -> Result<WasiEnv> {
        debug!("Creating WASI environment for instance: {}", instance_id);

        let mut wasi_state_builder = WasiState::new(&format!("vimmacs-{}", instance_id));

        // Configure environment variables
        let mut env_vars = HashMap::new();
        env_vars.insert("VIMMACS_INSTANCE_ID".to_string(), instance_id.to_string());
        env_vars.insert("VIMMACS_VERSION".to_string(), "1.0.0".to_string());

        for (key, value) in &env_vars {
            wasi_state_builder = wasi_state_builder.env(key, value);
        }

        // Configure allowed paths based on security settings
        let mut allowed_paths = Vec::new();

        if self.config.security.sandbox_enabled {
            // Restricted sandbox mode
            let workspace_path = PathBuf::from("/tmp/vimmacs-workspace");
            allowed_paths.push(workspace_path.clone());

            wasi_state_builder = wasi_state_builder
                .preopen_dir(&workspace_path, "workspace")
                .map_err(|e| anyhow!("Failed to preopen workspace directory: {}", e))?;
        } else {
            // Development mode with broader access
            let current_dir = std::env::current_dir()
                .map_err(|e| anyhow!("Failed to get current directory: {}", e))?;
            allowed_paths.push(current_dir.clone());

            wasi_state_builder = wasi_state_builder
                .preopen_dir(&current_dir, ".")
                .map_err(|e| anyhow!("Failed to preopen current directory: {}", e))?;
        }

        // Build WASI state
        let wasi_state = wasi_state_builder
            .finalize()
            .map_err(|e| anyhow!("Failed to create WASI state: {}", e))?;

        // Create imports
        let store = self.store.lock().await;
        let imports = generate_import_object_for_version(
            &*store,
            WasiVersion::Snapshot1,
            &wasi_state,
            &[],
        );
        drop(store);

        Ok(WasiEnv {
            state: wasi_state,
            imports,
            allowed_paths,
            environment_vars: env_vars,
        })
    }

    /// Terminate a WebAssembly instance
    pub async fn terminate_instance(&self, instance_id: &str) -> Result<()> {
        debug!("Terminating WASM instance: {}", instance_id);

        // Remove from active instances
        let removed = self.instances.write().unwrap().remove(instance_id).is_some();
        self.wasi_environments.write().unwrap().remove(instance_id);

        if removed {
            // Update metrics
            let mut metrics = self.metrics.write().unwrap();
            if metrics.active_instances > 0 {
                metrics.active_instances -= 1;
            }

            info!("Successfully terminated WASM instance: {}", instance_id);
            Ok(())
        } else {
            Err(anyhow!("Instance '{}' not found", instance_id))
        }
    }

    /// Get runtime metrics
    pub fn get_metrics(&self) -> RuntimeMetrics {
        self.metrics.read().unwrap().clone()
    }

    /// Get instance information
    pub fn get_instance_info(&self, instance_id: &str) -> Option<WasmInstance> {
        self.instances.read().unwrap().get(instance_id).cloned()
    }

    /// List all active instances
    pub fn list_instances(&self) -> Vec<String> {
        self.instances.read().unwrap().keys().cloned().collect()
    }

    /// Get memory usage for an instance
    pub async fn get_instance_memory_usage(&self, instance_id: &str) -> Result<u64> {
        let instances = self.instances.read().unwrap();
        let wasm_instance = instances.get(instance_id)
            .ok_or_else(|| anyhow!("Instance '{}' not found", instance_id))?;

        if let Some(memory) = &wasm_instance.memory {
            Ok(memory.size().bytes().0 as u64)
        } else {
            Ok(0)
        }
    }

    /// Validate WebAssembly module before loading
    pub fn validate_module(&self, wasm_bytes: &[u8]) -> Result<ModuleValidationResult> {
        debug!("Validating WebAssembly module");

        let store = self.store.try_lock()
            .map_err(|_| anyhow!("Failed to acquire store lock for validation"))?;

        match Module::validate(&*store, wasm_bytes) {
            Ok(_) => {
                // Additional security checks
                let mut warnings = Vec::new();
                let mut security_score = 100;

                // Check module size
                if wasm_bytes.len() > self.config.wasm.max_module_size {
                    return Ok(ModuleValidationResult {
                        valid: false,
                        security_score: 0,
                        warnings: vec![format!("Module size {} exceeds maximum allowed size {}", 
                            wasm_bytes.len(), self.config.wasm.max_module_size)],
                        errors: vec!["Module too large".to_string()],
                    });
                }

                // Check for suspicious patterns
                if wasm_bytes.windows(4).any(|w| w == b"eval" || w == b"exec") {
                    warnings.push("Module contains potentially dangerous function names".to_string());
                    security_score -= 20;
                }

                Ok(ModuleValidationResult {
                    valid: true,
                    security_score,
                    warnings,
                    errors: Vec::new(),
                })
            }
            Err(e) => Ok(ModuleValidationResult {
                valid: false,
                security_score: 0,
                warnings: Vec::new(),
                errors: vec![format!("Module validation failed: {}", e)],
            }),
        }
    }

    /// Clean up resources and shutdown runtime
    pub async fn shutdown(&self) -> Result<()> {
        info!("Shutting down WebAssembly runtime manager");

        // Terminate all active instances
        let instance_ids: Vec<String> = self.instances.read().unwrap().keys().cloned().collect();
        for instance_id in instance_ids {
            if let Err(e) = self.terminate_instance(&instance_id).await {
                warn!("Failed to terminate instance '{}' during shutdown: {}", instance_id, e);
            }
        }

        // Clear all data structures
        self.instances.write().unwrap().clear();
        self.modules.write().unwrap().clear();
        self.wasi_environments.write().unwrap().clear();

        info!("WebAssembly runtime manager shutdown complete");
        Ok(())
    }
}

/// Module validation result
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ModuleValidationResult {
    pub valid: bool,
    pub security_score: i32,
    pub warnings: Vec<String>,
    pub errors: Vec<String>,
}

#[cfg(test)]
mod tests {
