//! Kata Container integration for secure WebAssembly execution

use anyhow::{anyhow, Result};
use bollard::container::{
    Config, CreateContainerOptions, ListContainersOptions, RemoveContainerOptions,
    StartContainerOptions, StopContainerOptions,
};
use bollard::{Docker, API_DEFAULT_VERSION};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::PathBuf;
use std::sync::{Arc, RwLock};
use tracing::{debug, error, info, warn};

use crate::config::{ContainerConfig, RuntimeConfig};

/// Kata Container manager for secure WebAssembly execution
#[derive(Debug)]
pub struct KataContainer {
    config: Arc<RuntimeConfig>,
    docker: Docker,
    containers: Arc<RwLock<HashMap<String, ContainerInfo>>>,
    networks: Arc<RwLock<HashMap<String, NetworkInfo>>>,
    volumes: Arc<RwLock<HashMap<String, VolumeInfo>>>,
}

/// Container information and metadata
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContainerInfo {
    pub id: String,
    pub name: String,
    pub image: String,
    pub status: ContainerStatus,
    pub wasm_instance_id: Option<String>,
    pub created_at: chrono::DateTime<chrono::Utc>,
    pub started_at: Option<chrono::DateTime<chrono::Utc>>,
    pub stopped_at: Option<chrono::DateTime<chrono::Utc>>,
    pub resource_limits: ResourceLimits,
    pub network_config: ContainerNetworkConfig,
    pub security_profile: SecurityProfile,
}

/// Container execution status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ContainerStatus {
    Created,
    Starting,
    Running,
    Pausing,
    Paused,
    Stopping,
    Stopped,
    Failed(String),
    Removed,
}

/// Resource limits for containers
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ResourceLimits {
    pub memory_mb: u64,
    pub cpu_shares: u64,
    pub disk_mb: u64,
    pub network_bandwidth_mbps: Option<u32>,
    pub max_file_descriptors: u32,
    pub max_processes: u32,
}

/// Container network configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContainerNetworkConfig {
    pub network_mode: String,
    pub port_mappings: Vec<PortMapping>,
    pub dns_servers: Vec<String>,
    pub hostname: Option<String>,
    pub isolated: bool,
}

/// Port mapping configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PortMapping {
    pub host_port: u16,
    pub container_port: u16,
    pub protocol: String, // tcp, udp
    pub host_ip: Option<String>,
}

/// Security profile for containers
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityProfile {
    pub runtime: String, // kata-runtime, kata-qemu, kata-clh
    pub read_only_rootfs: bool,
    pub no_new_privileges: bool,
    pub apparmor_profile: Option<String>,
    pub seccomp_profile: Option<String>,
    pub capabilities_drop: Vec<String>,
    pub capabilities_add: Vec<String>,
    pub user: Option<String>,
    pub group: Option<String>,
}

/// Network information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct NetworkInfo {
    pub id: String,
    pub name: String,
    pub driver: String,
    pub subnet: Option<String>,
    pub gateway: Option<String>,
    pub isolated: bool,
}

/// Volume information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VolumeInfo {
    pub name: String,
    pub mount_point: PathBuf,
    pub driver: String,
    pub read_only: bool,
    pub size_mb: Option<u64>,
}

impl KataContainer {
    /// Create a new Kata Container manager
    pub fn new(config: Arc<RuntimeConfig>) -> Result<Self> {
        info!("Initializing Kata Container manager");

        // Connect to Docker daemon
        let docker = Docker::connect_with_socket_defaults()
            .map_err(|e| anyhow!("Failed to connect to Docker daemon: {}", e))?;

        Ok(Self {
            config,
            docker,
            containers: Arc::new(RwLock::new(HashMap::new())),
            networks: Arc::new(RwLock::new(HashMap::new())),
            volumes: Arc::new(RwLock::new(HashMap::new())),
        })
    }

    /// Create a new secure container for WebAssembly execution
    pub async fn create_container(
        &self,
        container_name: &str,
        wasm_instance_id: Option<String>,
    ) -> Result<String> {
        info!("Creating Kata container: {}", container_name);

        // Build security profile
        let security_profile = self.build_security_profile();

        // Build resource limits
        let resource_limits = ResourceLimits {
            memory_mb: self.config.container.memory_limit_mb,
            cpu_shares: self.config.container.cpu_limit_shares,
            disk_mb: self.config.container.disk_limit_mb,
            network_bandwidth_mbps: Some(self.config.container.network_bandwidth_limit_mbps),
            max_file_descriptors: self.config.container.max_file_descriptors,
            max_processes: self.config.container.max_processes,
        };

        // Build network configuration
        let network_config = ContainerNetworkConfig {
            network_mode: if self.config.container.isolated_network {
                "none".to_string()
            } else {
                "bridge".to_string()
            },
            port_mappings: Vec::new(), // No port mappings for security
            dns_servers: self.config.container.allowed_dns_servers.clone(),
            hostname: Some(format!("vimmacs-{}", container_name)),
            isolated: self.config.container.isolated_network,
        };

        // Prepare container configuration
        let mut host_config = bollard::container::HostConfig {
            runtime: Some(self.config.container.runtime.clone()),
            memory: Some(resource_limits.memory_mb * 1024 * 1024), // Convert to bytes
            cpu_shares: Some(resource_limits.cpu_shares),
            read_only_rootfs: Some(security_profile.read_only_rootfs),
            security_opt: Some(self.build_security_options(&security_profile)),
            cap_drop: Some(security_profile.capabilities_drop.clone()),
            cap_add: Some(security_profile.capabilities_add.clone()),
            network_mode: Some(network_config.network_mode.clone()),
            ..Default::default()
        };

        // Add volume mounts
        if !self.config.container.allowed_mount_paths.is_empty() {
            let mut binds = Vec::new();
            for mount_path in &self.config.container.allowed_mount_paths {
                let bind = format!("{}:{}:ro", mount_path, mount_path);
                binds.push(bind);
            }
            host_config.binds = Some(binds);
        }

        // Prepare environment variables
        let mut env_vars = Vec::new();
        env_vars.push(format!("VIMMACS_CONTAINER_NAME={}", container_name));
        if let Some(instance_id) = &wasm_instance_id {
            env_vars.push(format!("VIMMACS_WASM_INSTANCE={}", instance_id));
        }
        env_vars.push("VIMMACS_SECURITY_MODE=kata".to_string());

        // Create container configuration
        let container_config = Config {
            image: Some(self.config.container.base_image.clone()),
            hostname: network_config.hostname.clone(),
            env: Some(env_vars),
            working_dir: Some("/app".to_string()),
            user: security_profile.user.clone(),
            host_config: Some(host_config),
            ..Default::default()
        };

        // Create the container
        let options = CreateContainerOptions {
            name: container_name,
            ..Default::default()
        };

        let response = self.docker
            .create_container(Some(options), container_config)
            .await
            .map_err(|e| anyhow!("Failed to create container '{}': {}", container_name, e))?;

        let container_id = response.id;

        // Store container information
        let container_info = ContainerInfo {
            id: container_id.clone(),
            name: container_name.to_string(),
            image: self.config.container.base_image.clone(),
            status: ContainerStatus::Created,
            wasm_instance_id,
            created_at: chrono::Utc::now(),
            started_at: None,
            stopped_at: None,
            resource_limits,
            network_config,
            security_profile,
        };

        self.containers.write().unwrap()
            .insert(container_id.clone(), container_info);

        info!("Successfully created Kata container '{}' with ID: {}", container_name, container_id);
        Ok(container_id)
    }

    /// Start a container
    pub async fn start_container(&self, container_id: &str) -> Result<()> {
        debug!("Starting container: {}", container_id);

        let options = StartContainerOptions::<String> { ..Default::default() };

        self.docker
            .start_container(container_id, Some(options))
            .await
            .map_err(|e| anyhow!("Failed to start container '{}': {}", container_id, e))?;

        // Update container status
        if let Some(container_info) = self.containers.write().unwrap().get_mut(container_id) {
            container_info.status = ContainerStatus::Running;
            container_info.started_at = Some(chrono::Utc::now());
        }

        info!("Successfully started container: {}", container_id);
        Ok(())
    }

    /// Stop a container
    pub async fn stop_container(&self, container_id: &str, timeout_seconds: Option<i64>) -> Result<()> {
        debug!("Stopping container: {}", container_id);

        let options = StopContainerOptions { t: timeout_seconds.unwrap_or(30) };

        self.docker
            .stop_container(container_id, Some(options))
            .await
            .map_err(|e| anyhow!("Failed to stop container '{}': {}", container_id, e))?;

        // Update container status
        if let Some(container_info) = self.containers.write().unwrap().get_mut(container_id) {
            container_info.status = ContainerStatus::Stopped;
            container_info.stopped_at = Some(chrono::Utc::now());
        }

        info!("Successfully stopped container: {}", container_id);
        Ok(())
    }

    /// Remove a container
    pub async fn remove_container(&self, container_id: &str, force: bool) -> Result<()> {
        debug!("Removing container: {}", container_id);

        let options = RemoveContainerOptions {
            force,
            v: true, // Remove volumes
            ..Default::default()
        };

        self.docker
            .remove_container(container_id, Some(options))
            .await
            .map_err(|e| anyhow!("Failed to remove container '{}': {}", container_id, e))?;

        // Remove from our tracking
        if let Some(mut container_info) = self.containers.write().unwrap().remove(container_id) {
            container_info.status = ContainerStatus::Removed;
        }

        info!("Successfully removed container: {}", container_id);
        Ok(())
    }

    /// Get container information
    pub fn get_container_info(&self, container_id: &str) -> Option<ContainerInfo> {
        self.containers.read().unwrap().get(container_id).cloned()
    }

    /// List all managed containers
    pub fn list_containers(&self) -> Vec<ContainerInfo> {
        self.containers.read().unwrap().values().cloned().collect()
    }

    /// Execute command in container
    pub async fn exec_in_container(
        &self,
        container_id: &str,
        cmd: Vec<String>,
        working_dir: Option<String>,
    ) -> Result<String> {
        debug!("Executing command in container '{}': {:?}", container_id, cmd);

        use bollard::exec::{CreateExecOptions, StartExecResults};
        use futures::StreamExt;

        // Create exec instance
        let exec_options = CreateExecOptions {
            cmd: Some(cmd),
            attach_stdout: Some(true),
            attach_stderr: Some(true),
            working_dir,
            ..Default::default()
        };

        let exec_id = self.docker
            .create_exec(container_id, exec_options)
            .await
            .map_err(|e| anyhow!("Failed to create exec instance: {}", e))?
            .id;

        // Start execution
        let mut output = String::new();

        match self.docker.start_exec(&exec_id, None).await {
            Ok(StartExecResults::Attached { mut output_stream, .. }) => {
                while let Some(chunk) = output_stream.next().await {
                    match chunk {
                        Ok(bollard::container::LogOutput::StdOut { message }) => {
                            output.push_str(&String::from_utf8_lossy(&message));
                        }
                        Ok(bollard::container::LogOutput::StdErr { message }) => {
                            output.push_str(&String::from_utf8_lossy(&message));
                        }
                        Err(e) => return Err(anyhow!("Error reading exec output: {}", e)),
                        _ => {}
                    }
                }
            }
            Ok(StartExecResults::Detached) => {
                return Err(anyhow!("Exec started in detached mode"));
            }
            Err(e) => return Err(anyhow!("Failed to start exec: {}", e)),
        }

        debug!("Command execution completed in container '{}'", container_id);
        Ok(output)
    }

    /// Build security profile for Kata containers
    fn build_security_profile(&self) -> SecurityProfile {
        SecurityProfile {
            runtime: self.config.container.runtime.clone(),
            read_only_rootfs: true,
            no_new_privileges: true,
            apparmor_profile: Some("docker-default".to_string()),
            seccomp_profile: Some("default".to_string()),
            capabilities_drop: vec![
                "AUDIT_CONTROL".to_string(),
                "AUDIT_READ".to_string(),
                "DAC_READ_SEARCH".to_string(),
                "IPC_LOCK".to_string(),
                "IPC_OWNER".to_string(),
                "LEASE".to_string(),
                "LINUX_IMMUTABLE".to_string(),
                "MAC_ADMIN".to_string(),
                "MAC_OVERRIDE".to_string(),
                "NET_ADMIN".to_string(),
                "NET_BROADCAST".to_string(),
                "SYSLOG".to_string(),
                "SYS_ADMIN".to_string(),
                "SYS_BOOT".to_string(),
                "SYS_MODULE".to_string(),
                "SYS_NICE".to_string(),
                "SYS_RAWIO".to_string(),
                "SYS_RESOURCE".to_string(),
                "SYS_TIME".to_string(),
                "WAKE_ALARM".to_string(),
            ],
            capabilities_add: vec![], // No additional capabilities
            user: Some("nobody:nobody".to_string()),
            group: Some("nobody".to_string()),
        }
    }

    /// Build security options for container
    fn build_security_options(&self, profile: &SecurityProfile) -> Vec<String> {
        let mut options = Vec::new();

        if profile.no_new_privileges {
            options.push("no-new-privileges:true".to_string());
        }

        if let Some(apparmor) = &profile.apparmor_profile {
            options.push(format!("apparmor:{}", apparmor));
        }

        if let Some(seccomp) = &profile.seccomp_profile {
            options.push(format!("seccomp:{}", seccomp));
        }

        options
    }

    /// Verify Kata runtime is available
    pub async fn verify_kata_runtime(&self) -> Result<()> {
        debug!("Verifying Kata runtime availability");

        let info = self.docker.info().await
            .map_err(|e| anyhow!("Failed to get Docker info: {}", e))?;

        // Check if Kata runtime is available
        if let Some(runtimes) = &info.runtimes {
            if !runtimes.contains_key(&self.config.container.runtime) {
                return Err(anyhow!(
                    "Kata runtime '{}' not available. Available runtimes: {:?}",
                    self.config.container.runtime,
                    runtimes.keys().collect::<Vec<_>>()
                ));
            }
        } else {
            warn!("Unable to verify available runtimes");
        }

        info!("Kata runtime '{}' is available", self.config.container.runtime);
        Ok(())
    }

    /// Get resource usage for a container
    pub async fn get_container_stats(&self, container_id: &str) -> Result<ContainerStats> {
        use bollard::container::StatsOptions;
        use futures::StreamExt;

        let options = StatsOptions {
            stream: false,
            one_shot: true,
        };

        let mut stats_stream = self.docker.stats(container_id, Some(options));

        if let Some(stats_result) = stats_stream.next().await {
            let stats = stats_result
                .map_err(|e| anyhow!("Failed to get container stats: {}", e))?;

            let memory_usage = stats.memory_stats.usage.unwrap_or(0);
            let memory_limit = stats.memory_stats.limit.unwrap_or(0);
            let cpu_usage = stats.cpu_stats.cpu_usage.total_usage;

            Ok(ContainerStats {
                memory_usage_bytes: memory_usage,
                memory_limit_bytes: memory_limit,
                cpu_usage_total: cpu_usage,
                network_rx_bytes: stats.networks.as_ref()
                    .and_then(|n| n.values().map(|s| s.rx_bytes).sum::<Option<u64>>())
                    .unwrap_or(0),
                network_tx_bytes: stats.networks.as_ref()
                    .and_then(|n| n.values().map(|s| s.tx_bytes).sum::<Option<u64>>())
                    .unwrap_or(0),
            })
        } else {
            Err(anyhow!("No stats available for container '{}'", container_id))
        }
    }

    /// Cleanup and shutdown container manager
    pub async fn shutdown(&self) -> Result<()> {
        info!("Shutting down Kata container manager");

        // Stop and remove all managed containers
        let container_ids: Vec<String> = self.containers.read().unwrap()
            .keys().cloned().collect();

        for container_id in container_ids {
            if let Err(e) = self.stop_container(&container_id, Some(10)).await {
                warn!("Failed to stop container '{}' during shutdown: {}", container_id, e);
            }

            if let Err(e) = self.remove_container(&container_id, true).await {
                warn!("Failed to remove container '{}' during shutdown: {}", container_id, e);
            }
        }

        self.containers.write().unwrap().clear();

        info!("Kata container manager shutdown complete");
        Ok(())
    }
}

/// Container resource usage statistics
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ContainerStats {
    pub memory_usage_bytes: u64,
    pub memory_limit_bytes: u64,
    pub cpu_usage_total: u64,
    pub network_rx_bytes: u64,
    pub network_tx_bytes: u64,
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::config::{ContainerConfig, RuntimeConfig};

    fn create_test_config() -> Arc<RuntimeConfig> {
        let mut config = RuntimeConfig::default();
        config.container = ContainerConfig {
            runtime: "kata-runtime".to_string(),
            base_image: "alpine:latest".to_string(),
            memory_limit_mb: 512,
            cpu_limit_shares: 1024,
            disk_limit_mb: 1024,
            network_bandwidth_limit_mbps: 100,
            max_file_descriptors: 1024,
            max_processes: 64,
            isolated_network: true,
            allowed_mount_paths: vec!["/tmp/vimmacs".to_string()],
            allowed_dns_servers: vec!["8.8.8.8".to_string()],
        };
        Arc::new(config)
    }

    #[test]
    fn test_security_profile_creation() {
        let config = create_test_config();
        let container = KataContainer::new(config).unwrap();
        let profile = container.build_security_profile();

        assert!(profile.read_only_rootfs);
        assert!(profile.no_new_privileges);
        assert!(!profile.capabilities_drop.is_empty());
        assert!(profile.capabilities_add.is_empty());
    }

    #[test]
    fn test_resource_limits() {
        let limits = ResourceLimits {
            memory_mb: 512,
            cpu_shares: 1024,
            disk_mb: 1024,
            network_bandwidth_mbps: Some(100),
            max_file_descriptors: 1024,
            max_processes: 64,
        };

        assert_eq!(limits.memory_mb, 512);
        assert_eq!(limits.cpu_shares, 1024);
    }
}

