//! Authentication and authorization management with PKI support

use anyhow::{anyhow, Result};
use ed25519_dalek::{Keypair, PublicKey, SecretKey, Signature, Signer, Verifier};
use ring::aead::{Aad, BoundKey, Nonce, NonceSequence, OpeningKey, SealingKey, UnboundKey, AES_256_GCM};
use ring::pbkdf2::{self, PBKDF2_HMAC_SHA256};
use ring::rand::{SecureRandom, SystemRandom};
use rustls::{Certificate, PrivateKey, RootCertStore, ServerConfig};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::fs;
use std::path::PathBuf;
use std::sync::{Arc, RwLock};
use std::time::{Duration, SystemTime, UNIX_EPOCH};
use tracing::{debug, error, info, warn};
use x25519_dalek::{EphemeralSecret, PublicKey as X25519PublicKey, SharedSecret};

use crate::config::{RuntimeConfig, SecurityConfig};

/// Authentication manager with PKI support
#[derive(Debug)]
pub struct AuthenticationManager {
    config: Arc<RuntimeConfig>,
    keypairs: Arc<RwLock<HashMap<String, AuthKeyPair>>>,
    certificates: Arc<RwLock<HashMap<String, ClientCertificate>>>,
    sessions: Arc<RwLock<HashMap<String, AuthSession>>>,
    revoked_certificates: Arc<RwLock<Vec<String>>>,
    ca_certificates: RootCertStore,
    rng: SystemRandom,
}

/// Authentication key pair
#[derive(Debug, Clone)]
pub struct AuthKeyPair {
    pub identity: String,
    pub ed25519_keypair: Keypair,
    pub x25519_secret: EphemeralSecret,
    pub x25519_public: X25519PublicKey,
    pub created_at: SystemTime,
    pub expires_at: Option<SystemTime>,
}

/// Client certificate information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ClientCertificate {
    pub identity: String,
    pub certificate_pem: String,
    pub fingerprint: String,
    pub issued_at: SystemTime,
    pub expires_at: SystemTime,
    pub permissions: Vec<String>,
    pub revoked: bool,
}

/// Authentication session
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AuthSession {
    pub session_id: String,
    pub identity: String,
    pub client_ip: String,
    pub created_at: SystemTime,
    pub last_activity: SystemTime,
    pub expires_at: SystemTime,
    pub permissions: Vec<String>,
    pub authenticated: bool,
    pub mfa_verified: bool,
}

/// Authentication token
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AuthToken {
    pub token_id: String,
    pub identity: String,
    pub issued_at: u64,
    pub expires_at: u64,
    pub permissions: Vec<String>,
    pub session_id: String,
    pub signature: String,
}

/// Authentication challenge for client
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AuthChallenge {
    pub challenge_id: String,
    pub nonce: Vec<u8>,
    pub timestamp: u64,
    pub required_permissions: Vec<String>,
    pub expires_at: u64,
}

/// Authentication response from client
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AuthResponse {
    pub challenge_id: String,
    pub identity: String,
    pub signature: Vec<u8>,
    pub certificate_pem: Option<String>,
    pub client_info: ClientInfo,
}

/// Client information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ClientInfo {
    pub user_agent: String,
    pub platform: String,
    pub version: String,
    pub ip_address: String,
}

/// Encrypted data container
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EncryptedData {
    pub ciphertext: Vec<u8>,
    pub nonce: Vec<u8>,
    pub aad: Vec<u8>,
}

/// Nonce sequence for AEAD encryption
struct CounterNonceSequence(u32);

impl NonceSequence for CounterNonceSequence {
    fn advance(&mut self) -> Result<Nonce, ring::error::Unspecified> {
        let mut nonce_bytes = vec![0u8; 12];
        nonce_bytes[8..].copy_from_slice(&self.0.to_be_bytes());
        self.0 = self.0.wrapping_add(1);
        Nonce::try_assume_unique_for_key(&nonce_bytes)
    }
}

impl AuthenticationManager {
    /// Create a new authentication manager
    pub fn new(config: Arc<RuntimeConfig>) -> Result<Self> {
        info!("Initializing authentication manager");

        let mut ca_certificates = RootCertStore::empty();

        // Load CA certificates if specified
        if let Some(ca_cert_path) = &config.security.ca_certificate_path {
            let ca_cert_bytes = fs::read(ca_cert_path)
                .map_err(|e| anyhow!("Failed to read CA certificate: {}", e))?;

            let ca_cert = Certificate(ca_cert_bytes);
            ca_certificates.add(&ca_cert)
                .map_err(|e| anyhow!("Failed to add CA certificate: {:?}", e))?;

            info!("Loaded CA certificate from: {}", ca_cert_path);
        }

        Ok(Self {
            config,
            keypairs: Arc::new(RwLock::new(HashMap::new())),
            certificates: Arc::new(RwLock::new(HashMap::new())),
            sessions: Arc::new(RwLock::new(HashMap::new())),
            revoked_certificates: Arc::new(RwLock::new(Vec::new())),
            ca_certificates,
            rng: SystemRandom::new(),
        })
    }

    /// Generate a new key pair for identity
    pub fn generate_keypair(&self, identity: &str) -> Result<String> {
        debug!("Generating keypair for identity: {}", identity);

        // Generate Ed25519 keypair for signing
        let mut ed25519_seed = [0u8; 32];
        self.rng.fill(&mut ed25519_seed)
            .map_err(|_| anyhow!("Failed to generate random seed"))?;

        let ed25519_secret = SecretKey::from_bytes(&ed25519_seed)
            .map_err(|e| anyhow!("Failed to create Ed25519 secret key: {}", e))?;
        let ed25519_public = PublicKey::from(&ed25519_secret);
        let ed25519_keypair = Keypair { secret: ed25519_secret, public: ed25519_public };

        // Generate X25519 keypair for key exchange
        let x25519_secret = EphemeralSecret::new(rand::rngs::OsRng);
        let x25519_public = X25519PublicKey::from(&x25519_secret);

        let keypair = AuthKeyPair {
            identity: identity.to_string(),
            ed25519_keypair,
            x25519_secret,
            x25519_public,
            created_at: SystemTime::now(),
            expires_at: Some(SystemTime::now() + Duration::from_secs(86400 * 365)), // 1 year
        };

        let keypair_id = self.generate_keypair_id(identity);
        self.keypairs.write().unwrap().insert(keypair_id.clone(), keypair);

        info!("Generated keypair for identity '{}' with ID: {}", identity, keypair_id);
        Ok(keypair_id)
    }

    /// Create authentication challenge
    pub fn create_challenge(&self, required_permissions: Vec<String>) -> Result<AuthChallenge> {
        debug!("Creating authentication challenge");

        let challenge_id = self.generate_challenge_id();
        let mut nonce = vec![0u8; 32];
        self.rng.fill(&mut nonce)
            .map_err(|_| anyhow!("Failed to generate challenge nonce"))?;

        let timestamp = SystemTime::now()
            .duration_since(UNIX_EPOCH)
            .unwrap()
            .as_secs();

        let challenge = AuthChallenge {
            challenge_id,
            nonce,
            timestamp,
            required_permissions,
            expires_at: timestamp + self.config.security.auth_token_expiry_seconds,
        };

        debug!("Created authentication challenge: {}", challenge.challenge_id);
        Ok(challenge)
    }

    /// Verify authentication response
    pub async fn verify_response(
        &self,
        challenge: &AuthChallenge,
        response: &AuthResponse,
    ) -> Result<String> {
        debug!("Verifying authentication response for challenge: {}", challenge.challenge_id);

        // Verify challenge hasn't expired
        let now = SystemTime::now()
            .duration_since(UNIX_EPOCH)
            .unwrap()
            .as_secs();

        if now > challenge.expires_at {
            return Err(anyhow!("Authentication challenge has expired"));
        }

        // Verify challenge ID matches
        if response.challenge_id != challenge.challenge_id {
            return Err(anyhow!("Challenge ID mismatch"));
        }

        // Get or register client certificate
        let certificate = if let Some(cert_pem) = &response.certificate_pem {
            self.register_client_certificate(&response.identity, cert_pem.clone()).await?
        } else {
            self.get_client_certificate(&response.identity)
                .ok_or_else(|| anyhow!("No certificate found for identity: {}", response.identity))?
        };

        // Verify certificate is not revoked
        if certificate.revoked {
            return Err(anyhow!("Certificate is revoked for identity: {}", response.identity));
        }

        // Verify certificate hasn't expired
        if SystemTime::now() > certificate.expires_at {
            return Err(anyhow!("Certificate has expired for identity: {}", response.identity));
        }

        // Get keypair for signature verification
        let keypair_id = self.generate_keypair_id(&response.identity);
        let keypair = self.keypairs.read().unwrap()
            .get(&keypair_id)
            .cloned()
            .ok_or_else(|| anyhow!("Keypair not found for identity: {}", response.identity))?;

        // Verify signature
        let message = self.build_challenge_message(challenge, &response.identity)?;
        let signature = Signature::from_bytes(&response.signature)
            .map_err(|e| anyhow!("Invalid signature format: {}", e))?;

        keypair.ed25519_keypair.public.verify(&message, &signature)
            .map_err(|e| anyhow!("Signature verification failed: {}", e))?;

        // Create session
        let session_id = self.create_session(&response.identity, &response.client_info, certificate.permissions.clone()).await?;

        info!("Successfully authenticated identity '{}' with session: {}", response.identity, session_id);
        Ok(session_id)
    }

    /// Create authentication session
    pub async fn create_session(
        &self,
        identity: &str,
        client_info: &ClientInfo,
        permissions: Vec<String>,
    ) -> Result<String> {
        debug!("Creating session for identity: {}", identity);

        let session_id = self.generate_session_id();
        let now = SystemTime::now();

        let session = AuthSession {
            session_id: session_id.clone(),
            identity: identity.to_string(),
            client_ip: client_info.ip_address.clone(),
            created_at: now,
            last_activity: now,
            expires_at: now + Duration::from_secs(self.config.security.auth_token_expiry_seconds),
            permissions,
            authenticated: true,
            mfa_verified: false, // TODO: Implement MFA
        };

        self.sessions.write().unwrap().insert(session_id.clone(), session);

        info!("Created session '{}' for identity '{}'", session_id, identity);
        Ok(session_id)
    }

    /// Validate session token
    pub fn validate_session(&self, session_id: &str) -> Result<AuthSession> {
        debug!("Validating session: {}", session_id);

        let mut sessions = self.sessions.write().unwrap();
        let session = sessions.get_mut(session_id)
            .ok_or_else(|| anyhow!("Session not found: {}", session_id))?;

        // Check if session has expired
        if SystemTime::now() > session.expires_at {
            sessions.remove(session_id);
            return Err(anyhow!("Session has expired: {}", session_id));
        }

        // Update last activity
        session.last_activity = SystemTime::now();

        Ok(session.clone())
    }

    /// Generate JWT token for session
    pub fn generate_token(&self, session_id: &str) -> Result<AuthToken> {
        debug!("Generating JWT token for session: {}", session_id);

        let session = self.validate_session(session_id)?;

        let token_id = self.generate_token_id();
        let now = SystemTime::now()
            .duration_since(UNIX_EPOCH)
            .unwrap()
            .as_secs();

        let token = AuthToken {
            token_id: token_id.clone(),
            identity: session.identity.clone(),
            issued_at: now,
            expires_at: session.expires_at
                .duration_since(UNIX_EPOCH)
                .unwrap()
                .as_secs(),
            permissions: session.permissions.clone(),
            session_id: session_id.to_string(),
            signature: String::new(), // Will be filled by signing
        };

        // Sign the token
        let signed_token = self.sign_token(token)?;

        debug!("Generated JWT token: {}", signed_token.token_id);
        Ok(signed_token)
    }

    /// Sign authentication token
    fn sign_token(&self, mut token: AuthToken) -> Result<AuthToken> {
        let message = format!("{}:{}:{}:{}:{}",
            token.token_id,
            token.identity,
            token.issued_at,
            token.expires_at,
            token.permissions.join(",")
        );

        // Use a server keypair for signing (would be loaded from configuration)
        let server_keypair_id = "server".to_string();
        let keypairs = self.keypairs.read().unwrap();
        let keypair = keypairs.get(&server_keypair_id)
            .ok_or_else(|| anyhow!("Server keypair not found"))?;

        let signature = keypair.ed25519_keypair.sign(message.as_bytes());
        token.signature = hex::encode(signature.to_bytes());

        Ok(token)
    }

    /// Encrypt data using session key
    pub fn encrypt_data(&self, session_id: &str, plaintext: &[u8], aad: &[u8]) -> Result<EncryptedData> {
        debug!("Encrypting data for session: {}", session_id);

        // Derive session key from session ID
        let session_key = self.derive_session_key(session_id)?;

        // Generate random nonce
        let mut nonce_bytes = vec![0u8; 12];
        self.rng.fill(&mut nonce_bytes)
            .map_err(|_| anyhow!("Failed to generate nonce"))?;

        // Create sealing key
        let unbound_key = UnboundKey::new(&AES_256_GCM, &session_key)
            .map_err(|e| anyhow!("Failed to create unbound key: {:?}", e))?;
        let mut sealing_key = SealingKey::new(unbound_key, CounterNonceSequence(0));

        // Encrypt data
        let mut ciphertext = plaintext.to_vec();
        let nonce = Nonce::try_assume_unique_for_key(&nonce_bytes)
            .map_err(|e| anyhow!("Invalid nonce: {:?}", e))?;

        sealing_key.seal_in_place_append_tag(nonce, Aad::from(aad), &mut ciphertext)
            .map_err(|e| anyhow!("Encryption failed: {:?}", e))?;

        Ok(EncryptedData {
            ciphertext,
            nonce: nonce_bytes,
            aad: aad.to_vec(),
        })
    }

    /// Decrypt data using session key
    pub fn decrypt_data(&self, session_id: &str, encrypted: &EncryptedData) -> Result<Vec<u8>> {
        debug!("Decrypting data for session: {}", session_id);

        // Derive session key from session ID
        let session_key = self.derive_session_key(session_id)?;

        // Create opening key
        let unbound_key = UnboundKey::new(&AES_256_GCM, &session_key)
            .map_err(|e| anyhow!("Failed to create unbound key: {:?}", e))?;
        let mut opening_key = OpeningKey::new(unbound_key, CounterNonceSequence(0));

        // Decrypt data
        let mut ciphertext = encrypted.ciphertext.clone();
        let nonce = Nonce::try_assume_unique_for_key(&encrypted.nonce)
            .map_err(|e| anyhow!("Invalid nonce: {:?}", e))?;

        let plaintext = opening_key.open_in_place(nonce, Aad::from(&encrypted.aad), &mut ciphertext)
            .map_err(|e| anyhow!("Decryption failed: {:?}", e))?;

        Ok(plaintext.to_vec())
    }

    /// Register client certificate
    pub async fn register_client_certificate(
        &self,
        identity: &str,
        certificate_pem: String,
    ) -> Result<ClientCertificate> {
        debug!("Registering client certificate for identity: {}", identity);

        // Parse certificate to extract information
        let fingerprint = self.calculate_certificate_fingerprint(&certificate_pem)?;

        let certificate = ClientCertificate {
            identity: identity.to_string(),
            certificate_pem,
            fingerprint,
            issued_at: SystemTime::now(),
            expires_at: SystemTime::now() + Duration::from_secs(86400 * 365), // 1 year
            permissions: vec!["basic".to_string()], // Default permissions
            revoked: false,
        };

        self.certificates.write().unwrap()
            .insert(identity.to_string(), certificate.clone());

        info!("Registered certificate for identity '{}' with fingerprint: {}", identity, certificate.fingerprint);
        Ok(certificate)
    }

    /// Get client certificate
    pub fn get_client_certificate(&self, identity: &str) -> Option<ClientCertificate> {
        self.certificates.read().unwrap().get(identity).cloned()
    }

    /// Revoke client certificate
    pub fn revoke_certificate(&self, identity: &str) -> Result<()> {
        debug!("Revoking certificate for identity: {}", identity);

        let mut certificates = self.certificates.write().unwrap();
        if let Some(certificate) = certificates.get_mut(identity) {
            certificate.revoked = true;
            self.revoked_certificates.write().unwrap().push(certificate.fingerprint.clone());
            info!("Revoked certificate for identity: {}", identity);
            Ok(())
        } else {
            Err(anyhow!("Certificate not found for identity: {}", identity))
        }
    }

    /// Terminate session
    pub fn terminate_session(&self, session_id: &str) -> Result<()> {
        debug!("Terminating session: {}", session_id);

        if self.sessions.write().unwrap().remove(session_id).is_some() {
            info!("Terminated session: {}", session_id);
            Ok(())
        } else {
            Err(anyhow!("Session not found: {}", session_id))
        }
    }

    /// Helper functions
    fn generate_keypair_id(&self, identity: &str) -> String {
        format!("keypair-{}-{}", identity, uuid::Uuid::new_v4())
    }

    fn generate_challenge_id(&self) -> String {
        format!("challenge-{}", uuid::Uuid::new_v4())
    }

    fn generate_session_id(&self) -> String {
        format!("session-{}", uuid::Uuid::new_v4())
    }

    fn generate_token_id(&self) -> String {
        format!("token-{}", uuid::Uuid::new_v4())
    }

    fn build_challenge_message(&self, challenge: &AuthChallenge, identity: &str) -> Result<Vec<u8>> {
        let message = format!("{}:{}:{}:{}",
            challenge.challenge_id,
            hex::encode(&challenge.nonce),
            challenge.timestamp,
            identity
        );
        Ok(message.into_bytes())
    }

    fn derive_session_key(&self, session_id: &str) -> Result<[u8; 32]> {
        let salt = b"vimmacs-session-salt";
        let mut key = [0u8; 32];
        pbkdf2::derive(
            PBKDF2_HMAC_SHA256,
            std::num::NonZeroU32::new(10000).unwrap(),
            salt,
            session_id.as_bytes(),
            &mut key,
        );
        Ok(key)
    }

    fn calculate_certificate_fingerprint(&self, certificate_pem: &str) -> Result<String> {
        use ring::digest::{digest, SHA256};
        let hash = digest(&SHA256, certificate_pem.as_bytes());
        Ok(hex::encode(hash.as_ref()))
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::config::RuntimeConfig;

    fn create_test_config() -> Arc<RuntimeConfig> {
        Arc::new(RuntimeConfig::default())
    }

    #[tokio::test]
    async fn test_keypair_generation() {
        let config = create_test_config();
        let auth_manager = AuthenticationManager::new(config).unwrap();

        let keypair_id = auth_manager.generate_keypair("test-user").unwrap();
        assert!(keypair_id.starts_with("keypair-test-user"));
    }

    #[tokio::test]
    async fn test_challenge_creation() {
        let config = create_test_config();
        let auth_manager = AuthenticationManager::new(config).unwrap();

        let challenge = auth_manager.create_challenge(vec!["read".to_string()]).unwrap();
        assert!(challenge.challenge_id.starts_with("challenge-"));
        assert!(!challenge.nonce.is_empty());
    }

    #[tokio::test]
    async fn test_session_creation() {
        let config = create_test_config();
        let auth_manager = AuthenticationManager::new(config).unwrap();

        let client_info = ClientInfo {
            user_agent: "test-agent".to_string(),
            platform: "test".to_string(),
            version: "1.0.0".to_string(),
            ip_address: "127.0.0.1".to_string(),
        };

        let session_id = auth_manager.create_session(
            "test-user",
            &client_info,
            vec!["read".to_string()]
        ).await.unwrap();

        assert!(session_id.starts_with("session-"));

        let session = auth_manager.validate_session(&session_id).unwrap();
        assert_eq!(session.identity, "test-user");
        assert!(session.authenticated);
    }
}

