    AuthChallenge,

    // WebAssembly execution
    WasmExecute,
    WasmResult,
    WasmError,

    // Editor bridge
    EditorCommand,
    EditorResponse,
    EditorNotification,

    // System notifications
    SystemStatus,
    SystemAlert,
    SystemMetrics,

    // Client management
    ClientConnected,
    ClientDisconnected,
    ClientHeartbeat,

    // Custom messages
    Custom(String),
}

/// Network performance statistics
#[derive(Debug, Default, Clone, Serialize, Deserialize)]
pub struct NetworkStats {
    pub total_connections: u64,
    pub active_websocket_connections: u64,
    pub active_http_sessions: u64,
    pub messages_sent: u64,
    pub messages_received: u64,
    pub bytes_sent: u64,
    pub bytes_received: u64,
    pub connection_errors: u64,
    pub authentication_failures: u64,
}

/// HTTP API request/response structures
#[derive(Debug, Serialize, Deserialize)]
pub struct ApiRequest {
    pub session_id: Option<String>,
    pub command: String,
    pub parameters: serde_json::Value,
}

#[derive(Debug, Serialize, Deserialize)]
pub struct ApiResponse {
    pub success: bool,
    pub message: String,
    pub data: Option<serde_json::Value>,
    pub session_id: Option<String>,
}

/// WebSocket message structure
#[derive(Debug, Serialize, Deserialize)]
pub struct WebSocketMessage {
    pub id: String,
    pub type_: MessageType,
    pub payload: serde_json::Value,
    pub timestamp: u64,
}

impl NetworkManager {
    /// Create a new network manager
    pub fn new(
        config: Arc<RuntimeConfig>,
        auth_manager: Arc<RwLock<AuthenticationManager>>,
    ) -> Self {
        let (message_broadcaster, _) = broadcast::channel(1000);

        Self {
            config,
            auth_manager,
            websocket_connections: Arc::new(RwLock::new(HashMap::new())),
            http_sessions: Arc::new(RwLock::new(HashMap::new())),
            message_broadcaster,
            connection_stats: Arc::new(RwLock::new(NetworkStats::default())),
        }
    }

    /// Start HTTP and WebSocket servers
    pub async fn start_servers(&self) -> Result<()> {
        info!("Starting network servers");

        // Create application router
        let app = self.create_router();

        // Configure server address
        let addr = format!("{}:{}", 
            self.config.network.bind_address, 
            self.config.network.http_port
        ).parse::<SocketAddr>()
            .map_err(|e| anyhow!("Invalid server address: {}", e))?;

        // Start HTTP server
        info!("Starting HTTP server on {}", addr);

        let server_future = Server::bind(&addr)
            .serve(app.into_make_service_with_connect_info::<SocketAddr>())
            .with_graceful_shutdown(self.shutdown_signal());

        // Also start WebSocket server if configured differently
        if self.config.network.websocket_port != self.config.network.http_port {
            let ws_addr = format!("{}:{}", 
                self.config.network.bind_address, 
                self.config.network.websocket_port
            ).parse::<SocketAddr>()
                .map_err(|e| anyhow!("Invalid WebSocket address: {}", e))?;

            let ws_app = Router::new()
                .route("/ws", get(Self::websocket_handler))
                .with_state(Arc::new(self.clone()));

            let ws_server_future = Server::bind(&ws_addr)
                .serve(ws_app.into_make_service_with_connect_info::<SocketAddr>());

            info!("Starting WebSocket server on {}", ws_addr);

            // Run both servers concurrently
            tokio::select! {
                result = server_future => {
                    result.map_err(|e| anyhow!("HTTP server error: {}", e))?;
                }
                result = ws_server_future => {
                    result.map_err(|e| anyhow!("WebSocket server error: {}", e))?;
                }
            }
        } else {
            // Run single server with both HTTP and WebSocket
            server_future.await
                .map_err(|e| anyhow!("Server error: {}", e))?;
        }

        info!("Network servers started successfully");
        Ok(())
    }

    /// Create application router with routes
    fn create_router(&self) -> Router {
        let cors = CorsLayer::new()
            .allow_methods([Method::GET, Method::POST, Method::PUT, Method::DELETE])
            .allow_origin(self.config.network.cors_allowed_origins.iter()
                .map(|origin| origin.parse::<HeaderValue>().unwrap())
                .collect::<Vec<_>>())
            .allow_headers(Any);

        Router::new()
            // WebSocket endpoint
            .route("/ws", get(Self::websocket_handler))
            // HTTP API endpoints
            .route("/api/auth/challenge", post(Self::auth_challenge_handler))
            .route("/api/auth/verify", post(Self::auth_verify_handler))
            .route("/api/wasm/execute", post(Self::wasm_execute_handler))
            .route("/api/editor/command", post(Self::editor_command_handler))
            .route("/api/system/status", get(Self::system_status_handler))
            .route("/api/system/metrics", get(Self::system_metrics_handler))
            // Health check
            .route("/health", get(Self::health_check_handler))
            .with_state(Arc::new(self.clone()))
            .layer(
                ServiceBuilder::new()
                    .layer(TraceLayer::new_for_http())
                    .layer(cors)
            )
    }

    /// Handle WebSocket connections
    async fn websocket_handler(
        ws: WebSocketUpgrade,
        ConnectInfo(addr): ConnectInfo<SocketAddr>,
        State(network_manager): State<Arc<NetworkManager>>,
    ) -> impl IntoResponse {
        debug!("WebSocket connection attempt from: {}", addr);

        ws.on_upgrade(move |socket| {
            network_manager.handle_websocket_connection(socket, addr)
        })
    }

    /// Handle individual WebSocket connection
    async fn handle_websocket_connection(&self, socket: WebSocket, addr: SocketAddr) {
        let connection_id = format!("ws-{}-{}", addr, uuid::Uuid::new_v4());
        info!("WebSocket connected: {} from {}", connection_id, addr);

        // Register connection
        let connection = WebSocketConnection {
            connection_id: connection_id.clone(),
            session_id: None,
            client_addr: addr,
            connected_at: chrono::Utc::now(),
            last_activity: chrono::Utc::now(),
            message_count: 0,
            is_authenticated: false,
        };

        self.websocket_connections.write().unwrap()
            .insert(connection_id.clone(), connection);

        // Update stats
        {
            let mut stats = self.connection_stats.write().unwrap();
            stats.total_connections += 1;
            stats.active_websocket_connections += 1;
        }

        // Handle WebSocket messages
        let (mut sender, mut receiver) = socket.split();
        let mut message_receiver = self.message_broadcaster.subscribe();

        // Spawn task to handle outgoing messages
        let network_manager_clone = Arc::new(self.clone());
        let connection_id_clone = connection_id.clone();

        let send_task = tokio::spawn(async move {
            while let Ok(network_message) = message_receiver.recv().await {
                // Check if message is for this connection or broadcast
                if network_message.target.is_none() || 
                   network_message.target.as_ref() == Some(&connection_id_clone) {

                    let ws_message = WebSocketMessage {
                        id: network_message.message_id,
                        type_: network_message.message_type,
                        payload: network_message.payload,
                        timestamp: network_message.timestamp.timestamp_millis() as u64,
                    };

                    if let Ok(json_str) = serde_json::to_string(&ws_message) {
                        if sender.send(Message::Text(json_str)).await.is_err() {
                            break;
                        }
                    }
                }
            }
        });

        // Handle incoming messages
        while let Some(msg) = receiver.next().await {
            match msg {
                Ok(Message::Text(text)) => {
                    if let Err(e) = self.handle_websocket_message(&connection_id, &text).await {
                        warn!("Error handling WebSocket message: {}", e);
                    }

                    // Update connection activity
                    if let Some(conn) = self.websocket_connections.write().unwrap().get_mut(&connection_id) {
                        conn.last_activity = chrono::Utc::now();
                        conn.message_count += 1;
                    }
                }
                Ok(Message::Binary(_)) => {
                    warn!("Binary messages not supported from {}", addr);
                }
                Ok(Message::Close(_)) => {
                    info!("WebSocket closed by client: {}", connection_id);
                    break;
                }
                Err(e) => {
                    error!("WebSocket error for {}: {}", connection_id, e);
                    break;
                }
                _ => {}
            }
        }

        // Cleanup
        send_task.abort();
        self.websocket_connections.write().unwrap().remove(&connection_id);

        // Update stats
        let mut stats = self.connection_stats.write().unwrap();
        if stats.active_websocket_connections > 0 {
            stats.active_websocket_connections -= 1;
        }

        info!("WebSocket disconnected: {}", connection_id);
    }

    /// Handle WebSocket message
    async fn handle_websocket_message(&self, connection_id: &str, message: &str) -> Result<()> {
        debug!("Handling WebSocket message from {}: {}", connection_id, message);

        let ws_message: WebSocketMessage = serde_json::from_str(message)
            .map_err(|e| anyhow!("Invalid WebSocket message format: {}", e))?;

        match ws_message.type_ {
            MessageType::AuthRequest => {
                self.handle_auth_request(connection_id, &ws_message).await?;
            }
            MessageType::WasmExecute => {
                self.handle_wasm_execute_request(connection_id, &ws_message).await?;
            }
            MessageType::EditorCommand => {
                self.handle_editor_command(connection_id, &ws_message).await?;
            }
            MessageType::ClientHeartbeat => {
                self.handle_client_heartbeat(connection_id).await?;
            }
            _ => {
                warn!("Unsupported WebSocket message type: {:?}", ws_message.type_);
            }
        }

        Ok(())
    }

    /// Broadcast message to all connected clients
    pub fn broadcast_message(&self, message: NetworkMessage) -> Result<()> {
        debug!("Broadcasting message: {}", message.message_id);

        self.message_broadcaster.send(message)
            .map_err(|e| anyhow!("Failed to broadcast message: {}", e))?;

        // Update stats
        self.connection_stats.write().unwrap().messages_sent += 1;

        Ok(())
    }

    /// Send message to specific connection
    pub fn send_message_to_connection(&self, connection_id: &str, message: NetworkMessage) -> Result<()> {
        debug!("Sending message to connection {}: {}", connection_id, message.message_id);

        let mut targeted_message = message;
        targeted_message.target = Some(connection_id.to_string());

        self.message_broadcaster.send(targeted_message)
            .map_err(|e| anyhow!("Failed to send message: {}", e))?;

        // Update stats
        self.connection_stats.write().unwrap().messages_sent += 1;

        Ok(())
    }

    /// HTTP API Handlers
    async fn auth_challenge_handler(
        State(_network_manager): State<Arc<NetworkManager>>,
        Json(_request): Json<ApiRequest>,
    ) -> Result<Json<ApiResponse>, StatusCode> {
        // TODO: Implement authentication challenge
        Ok(Json(ApiResponse {
            success: true,
            message: "Challenge created".to_string(),
            data: Some(serde_json::json!({
                "challenge_id": "test-challenge",
                "nonce": "test-nonce"
            })),
            session_id: None,
        }))
    }

    async fn auth_verify_handler(
        State(_network_manager): State<Arc<NetworkManager>>,
        Json(_request): Json<ApiRequest>,
    ) -> Result<Json<ApiResponse>, StatusCode> {
        // TODO: Implement authentication verification
        Ok(Json(ApiResponse {
            success: true,
            message: "Authentication successful".to_string(),
            data: Some(serde_json::json!({
                "session_id": "test-session",
                "token": "test-token"
            })),
            session_id: Some("test-session".to_string()),
        }))
    }

    async fn wasm_execute_handler(
        State(_network_manager): State<Arc<NetworkManager>>,
        Json(_request): Json<ApiRequest>,
    ) -> Result<Json<ApiResponse>, StatusCode> {
        // TODO: Implement WebAssembly execution
        Ok(Json(ApiResponse {
            success: true,
            message: "WASM executed successfully".to_string(),
            data: Some(serde_json::json!({
                "result": "Hello from WebAssembly!"
            })),
            session_id: None,
        }))
    }

    async fn editor_command_handler(
        State(_network_manager): State<Arc<NetworkManager>>,
        Json(_request): Json<ApiRequest>,
    ) -> Result<Json<ApiResponse>, StatusCode> {
        // TODO: Implement editor command handling
        Ok(Json(ApiResponse {
            success: true,
            message: "Editor command executed".to_string(),
            data: None,
            session_id: None,
        }))
    }

    async fn system_status_handler(
        State(network_manager): State<Arc<NetworkManager>>,
    ) -> Json<ApiResponse> {
        let stats = network_manager.get_network_stats();
        Json(ApiResponse {
            success: true,
            message: "System status".to_string(),
            data: Some(serde_json::to_value(stats).unwrap()),
            session_id: None,
        })
    }

    async fn system_metrics_handler(
        State(network_manager): State<Arc<NetworkManager>>,
    ) -> Json<ApiResponse> {
        let stats = network_manager.get_network_stats();
        Json(ApiResponse {
            success: true,
            message: "System metrics".to_string(),
            data: Some(serde_json::to_value(stats).unwrap()),
            session_id: None,
        })
    }

    async fn health_check_handler() -> Json<ApiResponse> {
        Json(ApiResponse {
            success: true,
            message: "Service is healthy".to_string(),
            data: Some(serde_json::json!({
                "status": "ok",
                "timestamp": chrono::Utc::now().timestamp()
            })),
            session_id: None,
        })
    }

    /// Message handlers
    async fn handle_auth_request(&self, _connection_id: &str, _message: &WebSocketMessage) -> Result<()> {
        // TODO: Implement WebSocket authentication
        debug!("Handling auth request");
        Ok(())
    }

    async fn handle_wasm_execute_request(&self, _connection_id: &str, _message: &WebSocketMessage) -> Result<()> {
        // TODO: Implement WebSocket WASM execution
        debug!("Handling WASM execute request");
        Ok(())
    }

    async fn handle_editor_command(&self, _connection_id: &str, _message: &WebSocketMessage) -> Result<()> {
        // TODO: Implement WebSocket editor command
        debug!("Handling editor command");
        Ok(())
    }

    async fn handle_client_heartbeat(&self, connection_id: &str) -> Result<()> {
        debug!("Handling client heartbeat from {}", connection_id);

        // Update connection last activity
        if let Some(conn) = self.websocket_connections.write().unwrap().get_mut(connection_id) {
            conn.last_activity = chrono::Utc::now();
        }

        Ok(())
    }

    /// Get network statistics
    pub fn get_network_stats(&self) -> NetworkStats {
        self.connection_stats.read().unwrap().clone()
    }

    /// Get active WebSocket connections
    pub fn get_active_connections(&self) -> Vec<WebSocketConnection> {
        self.websocket_connections.read().unwrap().values().cloned().collect()
    }

    /// Graceful shutdown signal
    async fn shutdown_signal(&self) {
        tokio::signal::ctrl_c()
            .await
            .expect("Failed to install CTRL+C signal handler");

        info!("Received shutdown signal, stopping network servers");
    }

    /// Cleanup and shutdown
    pub async fn shutdown(&self) -> Result<()> {
        info!("Shutting down network manager");

        // Close all WebSocket connections
        let connection_ids: Vec<String> = self.websocket_connections.read().unwrap()
            .keys().cloned().collect();

        for connection_id in connection_ids {
            // Send close message to each connection
            let close_message = NetworkMessage {
                message_id: uuid::Uuid::new_v4().to_string(),
                message_type: MessageType::SystemAlert,
                sender: Some("system".to_string()),
                target: Some(connection_id),
                payload: serde_json::json!({
                    "type": "shutdown",
                    "message": "Server is shutting down"
                }),
                timestamp: chrono::Utc::now(),
            };

            let _ = self.message_broadcaster.send(close_message);
        }

        // Give connections time to close gracefully
        tokio::time::sleep(tokio::time::Duration::from_secs(2)).await;

        // Clear connection tracking
        self.websocket_connections.write().unwrap().clear();
        self.http_sessions.write().unwrap().clear();

        info!("Network manager shutdown complete");
        Ok(())
    }
}

impl Clone for NetworkManager {
    fn clone(&self) -> Self {
        Self {
            config: Arc::clone(&self.config),
            auth_manager: Arc::clone(&self.auth_manager),
            websocket_connections: Arc::clone(&self.websocket_connections),
            http_sessions: Arc::clone(&self.http_sessions),
            message_broadcaster: self.message_broadcaster.clone(),
            connection_stats: Arc::clone(&self.connection_stats),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::config::RuntimeConfig;
    use std::sync::Arc;

    fn create_test_config() -> Arc<RuntimeConfig> {
        let mut config = RuntimeConfig::default();
        config.network.bind_address = "127.0.0.1".to_string();
        config.network.http_port = 8080;
        config.network.websocket_port = 8081;
        Arc::new(config)
    }

    #[test]
    fn test_network_manager_creation() {
        let config = create_test_config();
        let auth_manager = Arc::new(RwLock::new(
            crate::auth::AuthenticationManager::new(config.clone()).unwrap()
        ));

        let network_manager = NetworkManager::new(config, auth_manager);
        let stats = network_manager.get_network_stats();

        assert_eq!(stats.total_connections, 0);
        assert_eq!(stats.active_websocket_connections, 0);
    }

    #[test]
    fn test_network_message_creation() {
        let message = NetworkMessage {
            message_id: "test-msg".to_string(),
            message_type: MessageType::ClientHeartbeat,
            sender: Some("test-client".to_string()),
            target: None,
            payload: serde_json::json!({"ping": "pong"}),
            timestamp: chrono::Utc::now(),
        };

        assert_eq!(message.message_id, "test-msg");
        assert!(matches!(message.message_type, MessageType::ClientHeartbeat));
    }
}

