//! Arch Linux integration for WebAssembly environment

use anyhow::{anyhow, Result};
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use std::path::{Path, PathBuf};
use std::process::{Command, Stdio};
use std::sync::{Arc, RwLock};
use subprocess::{Exec, Redirection};
use tracing::{debug, error, info, warn};
use walkdir::WalkDir;

use crate::config::{ArchLinuxConfig, RuntimeConfig};

/// Arch Linux integration manager
#[derive(Debug)]
pub struct ArchLinuxIntegration {
    config: Arc<RuntimeConfig>,
    packages: Arc<RwLock<HashMap<String, PackageInfo>>>,
    services: Arc<RwLock<HashMap<String, SystemdService>>>,
    filesystem_mounts: Arc<RwLock<HashMap<String, MountInfo>>>,
    environment_vars: Arc<RwLock<HashMap<String, String>>>,
    command_history: Arc<RwLock<Vec<CommandExecution>>>,
}

/// Package information from pacman
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PackageInfo {
    pub name: String,
    pub version: String,
    pub description: String,
    pub architecture: String,
    pub repository: Option<String>,
    pub installed_size: u64,
    pub install_date: Option<chrono::DateTime<chrono::Utc>>,
    pub dependencies: Vec<String>,
    pub provides: Vec<String>,
    pub conflicts: Vec<String>,
    pub status: PackageStatus,
}

/// Package installation status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum PackageStatus {
    Installed,
    Available,
    Outdated,
    Missing,
}

/// Systemd service information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SystemdService {
    pub name: String,
    pub status: ServiceStatus,
    pub enabled: bool,
    pub description: String,
    pub main_pid: Option<u32>,
    pub memory_usage: Option<u64>,
    pub cpu_usage: Option<f64>,
    pub restart_count: u32,
    pub last_restart: Option<chrono::DateTime<chrono::Utc>>,
}

/// Systemd service status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum ServiceStatus {
    Active,
    Inactive,
    Failed,
    Unknown,
}

/// Filesystem mount information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MountInfo {
    pub source: String,
    pub target: PathBuf,
    pub filesystem_type: String,
    pub options: Vec<String>,
    pub mounted: bool,
    pub mount_time: Option<chrono::DateTime<chrono::Utc>>,
    pub total_size: Option<u64>,
    pub used_size: Option<u64>,
    pub available_size: Option<u64>,
}

/// Command execution record
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CommandExecution {
    pub command: String,
    pub args: Vec<String>,
    pub working_dir: PathBuf,
    pub exit_code: i32,
    pub stdout: String,
    pub stderr: String,
    pub execution_time_ms: u64,
    pub timestamp: chrono::DateTime<chrono::Utc>,
    pub user: String,
}

/// System resource information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SystemResources {
    pub cpu_usage_percent: f64,
    pub memory_total_mb: u64,
    pub memory_used_mb: u64,
    pub memory_available_mb: u64,
    pub disk_usage: Vec<DiskUsage>,
    pub load_average: [f64; 3], // 1m, 5m, 15m
    pub uptime_seconds: u64,
    pub processes_count: u32,
}

/// Disk usage information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DiskUsage {
    pub filesystem: String,
    pub mount_point: String,
    pub total_gb: f64,
    pub used_gb: f64,
    pub available_gb: f64,
    pub usage_percent: f64,
}

impl ArchLinuxIntegration {
    /// Create a new Arch Linux integration manager
    pub fn new(config: Arc<RuntimeConfig>) -> Result<Self> {
        info!("Initializing Arch Linux integration");

        // Verify we're running on Arch Linux
        Self::verify_arch_linux()?;

        Ok(Self {
            config,
            packages: Arc::new(RwLock::new(HashMap::new())),
            services: Arc::new(RwLock::new(HashMap::new())),
            filesystem_mounts: Arc::new(RwLock::new(HashMap::new())),
            environment_vars: Arc::new(RwLock::new(HashMap::new())),
            command_history: Arc::new(RwLock::new(Vec::new())),
        })
    }

    /// Verify we're running on Arch Linux
    fn verify_arch_linux() -> Result<()> {
        let os_release = std::fs::read_to_string("/etc/os-release")
            .map_err(|e| anyhow!("Failed to read /etc/os-release: {}", e))?;

        if !os_release.contains("Arch Linux") {
            warn!("Not running on Arch Linux, some features may not work correctly");
        } else {
            debug!("Verified running on Arch Linux");
        }

        Ok(())
    }

    /// Initialize Arch Linux environment
    pub async fn initialize(&self) -> Result<()> {
        info!("Initializing Arch Linux environment");

        // Update package database
        self.update_package_database().await?;

        // Load installed packages
        self.refresh_package_list().await?;

        // Load systemd services
        self.refresh_services_list().await?;

        // Setup filesystem mounts
        self.setup_filesystem_mounts().await?;

        // Initialize environment variables
        self.setup_environment().await?;

        // Install required packages
        self.install_required_packages().await?;

        info!("Arch Linux environment initialized successfully");
        Ok(())
    }

    /// Update pacman package database
    pub async fn update_package_database(&self) -> Result<()> {
        info!("Updating package database");

        let result = self.execute_command(
            "pacman",
            vec!["-Sy", "--noconfirm"],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to update package database: {}", result.stderr));
        }

        info!("Package database updated successfully");
        Ok(())
    }

    /// Install a package using pacman
    pub async fn install_package(&self, package_name: &str) -> Result<()> {
        info!("Installing package: {}", package_name);

        // Check if package is in allowed list
        if !self.config.arch.allowed_packages.contains(&package_name.to_string()) {
            return Err(anyhow!("Package '{}' is not in allowed packages list", package_name));
        }

        let result = self.execute_command(
            "pacman",
            vec!["-S", "--noconfirm", package_name],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to install package '{}': {}", package_name, result.stderr));
        }

        // Update package info
        self.refresh_package_info(package_name).await?;

        info!("Successfully installed package: {}", package_name);
        Ok(())
    }

    /// Remove a package using pacman
    pub async fn remove_package(&self, package_name: &str) -> Result<()> {
        info!("Removing package: {}", package_name);

        let result = self.execute_command(
            "pacman",
            vec!["-R", "--noconfirm", package_name],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to remove package '{}': {}", package_name, result.stderr));
        }

        // Remove from our tracking
        self.packages.write().unwrap().remove(package_name);

        info!("Successfully removed package: {}", package_name);
        Ok(())
    }

    /// Refresh package list from pacman
    pub async fn refresh_package_list(&self) -> Result<()> {
        debug!("Refreshing package list");

        // Get installed packages
        let result = self.execute_command(
            "pacman",
            vec!["-Q"],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to get package list: {}", result.stderr));
        }

        let mut packages = self.packages.write().unwrap();
        packages.clear();

        for line in result.stdout.lines() {
            if let Some((name, version)) = line.split_once(' ') {
                // Get detailed package info
                if let Ok(package_info) = self.get_package_info(name).await {
                    packages.insert(name.to_string(), package_info);
                } else {
                    // Basic package info
                    let package_info = PackageInfo {
                        name: name.to_string(),
                        version: version.to_string(),
                        description: String::new(),
                        architecture: "x86_64".to_string(),
                        repository: None,
                        installed_size: 0,
                        install_date: None,
                        dependencies: Vec::new(),
                        provides: Vec::new(),
                        conflicts: Vec::new(),
                        status: PackageStatus::Installed,
                    };
                    packages.insert(name.to_string(), package_info);
                }
            }
        }

        debug!("Loaded {} packages", packages.len());
        Ok(())
    }

    /// Get detailed package information
    pub async fn get_package_info(&self, package_name: &str) -> Result<PackageInfo> {
        debug!("Getting package info for: {}", package_name);

        let result = self.execute_command(
            "pacman",
            vec!["-Qi", package_name],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Package '{}' not found", package_name));
        }

        // Parse pacman output
        let mut info = HashMap::new();
        for line in result.stdout.lines() {
            if let Some((key, value)) = line.split_once(':') {
                info.insert(key.trim().to_string(), value.trim().to_string());
            }
        }

        let package_info = PackageInfo {
            name: package_name.to_string(),
            version: info.get("Version").unwrap_or(&String::new()).clone(),
            description: info.get("Description").unwrap_or(&String::new()).clone(),
            architecture: info.get("Architecture").unwrap_or(&"x86_64".to_string()).clone(),
            repository: info.get("Repository").cloned(),
            installed_size: info.get("Installed Size")
                .and_then(|s| s.split_whitespace().next())
                .and_then(|s| s.parse().ok())
                .unwrap_or(0),
            install_date: info.get("Install Date")
                .and_then(|s| chrono::DateTime::parse_from_str(s, "%Y-%m-%d %H:%M:%S").ok())
                .map(|dt| dt.with_timezone(&chrono::Utc)),
            dependencies: info.get("Depends On")
                .map(|s| s.split_whitespace().map(|s| s.to_string()).collect())
                .unwrap_or_default(),
            provides: info.get("Provides")
                .map(|s| s.split_whitespace().map(|s| s.to_string()).collect())
                .unwrap_or_default(),
            conflicts: info.get("Conflicts With")
                .map(|s| s.split_whitespace().map(|s| s.to_string()).collect())
                .unwrap_or_default(),
            status: PackageStatus::Installed,
        };

        Ok(package_info)
    }

    /// Refresh systemd services list
    pub async fn refresh_services_list(&self) -> Result<()> {
        debug!("Refreshing systemd services list");

        let result = self.execute_command(
            "systemctl",
            vec!["list-units", "--type=service", "--all", "--no-pager"],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to get services list: {}", result.stderr));
        }

        let mut services = self.services.write().unwrap();
        services.clear();

        for line in result.stdout.lines().skip(1) { // Skip header
            let parts: Vec<&str> = line.split_whitespace().collect();
            if parts.len() >= 4 {
                let service_name = parts[0].to_string();
                let status = match parts[2] {
                    "active" => ServiceStatus::Active,
                    "inactive" => ServiceStatus::Inactive,
                    "failed" => ServiceStatus::Failed,
                    _ => ServiceStatus::Unknown,
                };

                let service = SystemdService {
                    name: service_name.clone(),
                    status,
                    enabled: false, // Will be updated separately
                    description: parts[4..].join(" "),
                    main_pid: None,
                    memory_usage: None,
                    cpu_usage: None,
                    restart_count: 0,
                    last_restart: None,
                };

                services.insert(service_name, service);
            }
        }

        debug!("Loaded {} services", services.len());
        Ok(())
    }

    /// Start a systemd service
    pub async fn start_service(&self, service_name: &str) -> Result<()> {
        info!("Starting service: {}", service_name);

        let result = self.execute_command(
            "systemctl",
            vec!["start", service_name],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to start service '{}': {}", service_name, result.stderr));
        }

        // Update service status
        self.refresh_service_status(service_name).await?;

        info!("Successfully started service: {}", service_name);
        Ok(())
    }

    /// Stop a systemd service
    pub async fn stop_service(&self, service_name: &str) -> Result<()> {
        info!("Stopping service: {}", service_name);

        let result = self.execute_command(
            "systemctl",
            vec!["stop", service_name],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to stop service '{}': {}", service_name, result.stderr));
        }

        // Update service status
        self.refresh_service_status(service_name).await?;

        info!("Successfully stopped service: {}", service_name);
        Ok(())
    }

    /// Refresh specific service status
    pub async fn refresh_service_status(&self, service_name: &str) -> Result<()> {
        debug!("Refreshing status for service: {}", service_name);

        let result = self.execute_command(
            "systemctl",
            vec!["status", service_name, "--no-pager"],
            None,
        ).await?;

        // Parse systemctl status output
        let mut services = self.services.write().unwrap();
        if let Some(service) = services.get_mut(service_name) {
            // Update status based on output
            if result.stdout.contains("Active: active") {
                service.status = ServiceStatus::Active;
            } else if result.stdout.contains("Active: inactive") {
                service.status = ServiceStatus::Inactive;
            } else if result.stdout.contains("Active: failed") {
                service.status = ServiceStatus::Failed;
            }
        }

        Ok(())
    }

    /// Setup filesystem mounts
    pub async fn setup_filesystem_mounts(&self) -> Result<()> {
        debug!("Setting up filesystem mounts");

        // Create workspace directory
        let workspace_dir = PathBuf::from(&self.config.arch.workspace_path);
        if !workspace_dir.exists() {
            std::fs::create_dir_all(&workspace_dir)
                .map_err(|e| anyhow!("Failed to create workspace directory: {}", e))?;
            info!("Created workspace directory: {:?}", workspace_dir);
        }

        // Create temp directory
        let temp_dir = PathBuf::from(&self.config.arch.temp_path);
        if !temp_dir.exists() {
            std::fs::create_dir_all(&temp_dir)
                .map_err(|e| anyhow!("Failed to create temp directory: {}", e))?;
            info!("Created temp directory: {:?}", temp_dir);
        }

        // Setup additional mounts if configured
        for mount_config in &self.config.arch.additional_mounts {
            self.setup_mount(mount_config).await?;
        }

        Ok(())
    }

    /// Setup a specific mount
    async fn setup_mount(&self, mount_config: &str) -> Result<()> {
        debug!("Setting up mount: {}", mount_config);

        // Parse mount configuration (source:target:type:options)
        let parts: Vec<&str> = mount_config.split(':').collect();
        if parts.len() < 3 {
            return Err(anyhow!("Invalid mount configuration: {}", mount_config));
        }

        let source = parts[0];
        let target = parts[1];
        let fs_type = parts[2];
        let options = if parts.len() > 3 { parts[3] } else { "defaults" };

        // Create target directory if it doesn't exist
        let target_path = PathBuf::from(target);
        if !target_path.exists() {
            std::fs::create_dir_all(&target_path)
                .map_err(|e| anyhow!("Failed to create mount target '{}': {}", target, e))?;
        }

        // Mount the filesystem
        let result = self.execute_command(
            "mount",
            vec!["-t", fs_type, "-o", options, source, target],
            None,
        ).await?;

        if result.exit_code != 0 {
            return Err(anyhow!("Failed to mount '{}' to '{}': {}", source, target, result.stderr));
        }

        // Record mount info
        let mount_info = MountInfo {
            source: source.to_string(),
            target: target_path,
            filesystem_type: fs_type.to_string(),
            options: options.split(',').map(|s| s.to_string()).collect(),
            mounted: true,
            mount_time: Some(chrono::Utc::now()),
            total_size: None,
            used_size: None,
            available_size: None,
        };

        self.filesystem_mounts.write().unwrap()
            .insert(target.to_string(), mount_info);

        info!("Successfully mounted '{}' to '{}'", source, target);
        Ok(())
    }

    /// Setup environment variables
    pub async fn setup_environment(&self) -> Result<()> {
        debug!("Setting up environment variables");

        let mut env_vars = self.environment_vars.write().unwrap();

        // Set default environment variables
        env_vars.insert("VIMMACS_ARCH_WORKSPACE".to_string(), self.config.arch.workspace_path.clone());
        env_vars.insert("VIMMACS_ARCH_TEMP".to_string(), self.config.arch.temp_path.clone());
        env_vars.insert("PATH".to_string(), format!("{}:/usr/local/bin:/usr/bin:/bin", 
            std::env::var("PATH").unwrap_or_default()));

        // Set locale
        env_vars.insert("LANG".to_string(), self.config.arch.locale.clone());
        env_vars.insert("LC_ALL".to_string(), self.config.arch.locale.clone());

        // Set timezone
        env_vars.insert("TZ".to_string(), self.config.arch.timezone.clone());

        // Add custom environment variables
        for (key, value) in &self.config.arch.environment_vars {
            env_vars.insert(key.clone(), value.clone());
        }

        debug!("Set {} environment variables", env_vars.len());
        Ok(())
    }

    /// Install required packages
    pub async fn install_required_packages(&self) -> Result<()> {
        info!("Installing required packages");

        for package in &self.config.arch.required_packages {
            match self.install_package(package).await {
                Ok(_) => info!("Installed required package: {}", package),
                Err(e) => warn!("Failed to install required package '{}': {}", package, e),
            }
        }

        Ok(())
    }

    /// Execute a system command
    pub async fn execute_command(
        &self,
        command: &str,
        args: Vec<&str>,
        working_dir: Option<&Path>,
    ) -> Result<CommandExecution> {
        debug!("Executing command: {} {:?}", command, args);

        let start_time = std::time::Instant::now();
        let timestamp = chrono::Utc::now();

        let mut exec = Exec::cmd(command);
        for arg in &args {
            exec = exec.arg(arg);
        }

        if let Some(dir) = working_dir {
            exec = exec.cwd(dir);
        }

        // Set environment variables
        let env_vars = self.environment_vars.read().unwrap();
        for (key, value) in env_vars.iter() {
            exec = exec.env(key, value);
        }

        let result = exec
            .stdout(Redirection::Pipe)
            .stderr(Redirection::Pipe)
            .capture();

        let execution_time = start_time.elapsed();

        let execution = match result {
            Ok(capture_data) => {
                CommandExecution {
                    command: command.to_string(),
                    args: args.iter().map(|s| s.to_string()).collect(),
                    working_dir: working_dir.unwrap_or(Path::new(".")).to_path_buf(),
                    exit_code: capture_data.exit_status.code().unwrap_or(-1),
                    stdout: String::from_utf8_lossy(&capture_data.stdout).to_string(),
                    stderr: String::from_utf8_lossy(&capture_data.stderr).to_string(),
                    execution_time_ms: execution_time.as_millis() as u64,
                    timestamp,
                    user: whoami::username(),
                }
            }
            Err(e) => {
                CommandExecution {
                    command: command.to_string(),
                    args: args.iter().map(|s| s.to_string()).collect(),
                    working_dir: working_dir.unwrap_or(Path::new(".")).to_path_buf(),
                    exit_code: -1,
                    stdout: String::new(),
                    stderr: format!("Command execution failed: {}", e),
                    execution_time_ms: execution_time.as_millis() as u64,
                    timestamp,
                    user: whoami::username(),
                }
            }
        };

        // Record command execution
        self.command_history.write().unwrap().push(execution.clone());

        // Limit command history size
        let mut history = self.command_history.write().unwrap();
        if history.len() > 1000 {
            history.drain(0..100); // Remove oldest 100 entries
        }

        debug!("Command completed with exit code: {}", execution.exit_code);
        Ok(execution)
    }

    /// Get system resource information
    pub async fn get_system_resources(&self) -> Result<SystemResources> {
        debug!("Getting system resource information");

        // Get memory info
        let meminfo = std::fs::read_to_string("/proc/meminfo")
            .map_err(|e| anyhow!("Failed to read /proc/meminfo: {}", e))?;

        let mut memory_total = 0;
        let mut memory_available = 0;

        for line in meminfo.lines() {
            if let Some(value) = line.strip_prefix("MemTotal:") {
                memory_total = value.trim().split_whitespace().next()
                    .and_then(|s| s.parse::<u64>().ok())
                    .unwrap_or(0) / 1024; // Convert to MB
            } else if let Some(value) = line.strip_prefix("MemAvailable:") {
                memory_available = value.trim().split_whitespace().next()
                    .and_then(|s| s.parse::<u64>().ok())
                    .unwrap_or(0) / 1024; // Convert to MB
            }
        }

        let memory_used = memory_total - memory_available;

        // Get load average
        let loadavg = std::fs::read_to_string("/proc/loadavg")
            .map_err(|e| anyhow!("Failed to read /proc/loadavg: {}", e))?;

        let load_parts: Vec<&str> = loadavg.split_whitespace().collect();
        let load_average = [
            load_parts.get(0).and_then(|s| s.parse().ok()).unwrap_or(0.0),
            load_parts.get(1).and_then(|s| s.parse().ok()).unwrap_or(0.0),
            load_parts.get(2).and_then(|s| s.parse().ok()).unwrap_or(0.0),
        ];

        // Get uptime
        let uptime = std::fs::read_to_string("/proc/uptime")
            .map_err(|e| anyhow!("Failed to read /proc/uptime: {}", e))?;

        let uptime_seconds = uptime.split_whitespace().next()
            .and_then(|s| s.parse::<f64>().ok())
            .unwrap_or(0.0) as u64;

        // Get disk usage
        let df_result = self.execute_command("df", vec!["-h"], None).await?;
        let mut disk_usage = Vec::new();

        if df_result.exit_code == 0 {
            for line in df_result.stdout.lines().skip(1) { // Skip header
                let parts: Vec<&str> = line.split_whitespace().collect();
                if parts.len() >= 6 {
                    if let (Ok(total), Ok(used), Ok(available), Ok(usage)) = (
                        Self::parse_disk_size(parts[1]),
                        Self::parse_disk_size(parts[2]),
                        Self::parse_disk_size(parts[3]),
                        parts[4].trim_end_matches('%').parse::<f64>(),
                    ) {
                        disk_usage.push(DiskUsage {
                            filesystem: parts[0].to_string(),
                            mount_point: parts[5].to_string(),
                            total_gb: total,
                            used_gb: used,
                            available_gb: available,
                            usage_percent: usage,
                        });
                    }
                }
            }
        }

        Ok(SystemResources {
            cpu_usage_percent: 0.0, // Would need more complex calculation
            memory_total_mb: memory_total,
            memory_used_mb: memory_used,
            memory_available_mb: memory_available,
            disk_usage,
            load_average,
            uptime_seconds,
            processes_count: 0, // Would need process counting
        })
    }

    /// Parse disk size from df output
    fn parse_disk_size(size_str: &str) -> Result<f64, std::num::ParseFloatError> {
        let size_str = size_str.trim();
        if size_str.ends_with('G') {
            size_str.trim_end_matches('G').parse::<f64>()
        } else if size_str.ends_with('M') {
            size_str.trim_end_matches('M').parse::<f64>().map(|s| s / 1024.0)
        } else if size_str.ends_with('K') {
            size_str.trim_end_matches('K').parse::<f64>().map(|s| s / 1024.0 / 1024.0)
        } else {
            size_str.parse::<f64>().map(|s| s / 1024.0 / 1024.0 / 1024.0)
        }
    }

    /// Get installed packages list
    pub fn get_installed_packages(&self) -> Vec<PackageInfo> {
        self.packages.read().unwrap().values().cloned().collect()
    }

    /// Get services list
    pub fn get_services(&self) -> Vec<SystemdService> {
        self.services.read().unwrap().values().cloned().collect()
    }

    /// Get command history
    pub fn get_command_history(&self, limit: Option<usize>) -> Vec<CommandExecution> {
        let history = self.command_history.read().unwrap();
        let start = if let Some(limit) = limit {
            if history.len() > limit {
                history.len() - limit
            } else {
                0
            }
        } else {
            0
        };
        history[start..].to_vec()
    }

    /// Cleanup and shutdown
    pub async fn shutdown(&self) -> Result<()> {
        info!("Shutting down Arch Linux integration");

        // Unmount any mounted filesystems
        let mounts: Vec<String> = self.filesystem_mounts.read().unwrap()
            .keys().cloned().collect();

        for mount_point in mounts {
            if let Err(e) = self.execute_command("umount", vec![&mount_point], None).await {
                warn!("Failed to unmount '{}': {}", mount_point, e);
            }
        }

        self.filesystem_mounts.write().unwrap().clear();

        info!("Arch Linux integration shutdown complete");
        Ok(())
    }

    /// Refresh package info for specific package
    async fn refresh_package_info(&self, package_name: &str) -> Result<()> {
        if let Ok(package_info) = self.get_package_info(package_name).await {
            self.packages.write().unwrap()
                .insert(package_name.to_string(), package_info);
        }
        Ok(())
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use crate::config::RuntimeConfig;
    use std::sync::Arc;
    use tempfile::tempdir;

    fn create_test_config() -> Arc<RuntimeConfig> {
        let temp_dir = tempdir().unwrap();
        let mut config = RuntimeConfig::default();
        config.arch.workspace_path = temp_dir.path().join("workspace").to_string_lossy().to_string();
        config.arch.temp_path = temp_dir.path().join("temp").to_string_lossy().to_string();
        Arc::new(config)
    }

    #[test]
    fn test_arch_integration_creation() {
        let config = create_test_config();
        let arch_integration = ArchLinuxIntegration::new(config);

        // May fail if not running on Arch Linux, but should not panic
        match arch_integration {
            Ok(_) => println!("Arch Linux integration created successfully"),
            Err(e) => println!("Expected error on non-Arch system: {}", e),
        }
    }

    #[test]
    fn test_disk_size_parsing() {
        assert!((ArchLinuxIntegration::parse_disk_size("10G").unwrap() - 10.0).abs() < 0.001);
        assert!((ArchLinuxIntegration::parse_disk_size("1024M").unwrap() - 1.0).abs() < 0.001);
        assert!((ArchLinuxIntegration::parse_disk_size("1048576K").unwrap() - 1.0).abs() < 0.001);
    }

    #[tokio::test]
    async fn test_command_execution() {
        let config = create_test_config();
        if let Ok(arch_integration) = ArchLinuxIntegration::new(config) {
            let result = arch_integration.execute_command("echo", vec!["hello", "world"], None).await;

            if let Ok(execution) = result {
                assert_eq!(execution.exit_code, 0);
                assert!(execution.stdout.contains("hello world"));
            }
        }
    }
}

