//! Vimmacs WebAssembly Runtime Server
//! 
//! Main entry point for the WebAssembly runtime with Wasmer integration,
//! Kata Container security, and Arch Linux environment support.

use anyhow::{anyhow, Result};
use clap::{Arg, Command};
use std::path::PathBuf;
use std::sync::Arc;
use tokio::signal;
use tracing::{error, info, warn};
use tracing_subscriber::{layer::SubscriberExt, util::SubscriberInitExt};

use vimmacs_wasm_runtime::{
    config::RuntimeConfig,
    VimmacsWasmRuntime,
};

/// Main function - entry point for the runtime server
#[tokio::main]
async fn main() -> Result<()> {
    // Initialize tracing
    init_logging()?;

    info!("Starting Vimmacs WebAssembly Runtime Server");

    // Parse command line arguments
    let matches = create_cli().get_matches();

    // Load configuration
    let config_path = matches.get_one::<String>("config")
        .map(PathBuf::from)
        .unwrap_or_else(|| PathBuf::from("config/runtime.toml"));

    let config = load_configuration(&config_path).await?;

    // Validate configuration
    config.validate()?;

    info!("Configuration loaded from: {:?}", config_path);

    // Create runtime instance
    let runtime = VimmacsWasmRuntime::new(Arc::new(config)).await?;

    // Initialize runtime components
    runtime.initialize().await?;

    info!("WebAssembly runtime initialized successfully");

    // Start runtime servers
    let runtime_clone = runtime.clone();
    let server_handle = tokio::spawn(async move {
        if let Err(e) = runtime_clone.start().await {
            error!("Runtime server error: {}", e);
        }
    });

    info!("Runtime servers started");

    // Wait for shutdown signal
    wait_for_shutdown().await;

    info!("Shutdown signal received, stopping runtime...");

    // Graceful shutdown
    server_handle.abort();

    if let Err(e) = runtime.shutdown().await {
        error!("Error during shutdown: {}", e);
    }

    info!("Vimmacs WebAssembly Runtime shutdown complete");
    Ok(())
}

/// Initialize logging with tracing
